import React, { Component } from "react";
import { connect } from "react-redux";
import "./FeaturedDoctors.scss";
import { FormattedMessage } from "react-intl";
import Slider from "react-slick";
import * as actions from "../../../../src/store/actions";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import medicalImg from "../../../assets/FeaturedDoctors/1.jpg";
import Carousel from "react-bootstrap/Carousel";
import ExampleCarouselImage from "../../../components/ExampleCarouselImage";
import medicalImg from "../../../assets/medical/1.jpg";
import CardDoctor from "../../../components/CardDoctor";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
class FeaturedDoctors extends Component {

    constructor(props) {
        super(props);
        this.state = {
            arrDoctors: []
        };
    }
    componentDidMount() {
        this.props.fetchTopDoctors(10);
    }
    handleViewDetail = (doctorId) => {
        this.props.history.push(`/doctor/${doctorId}`);
        // console.log("check props", this.props);
// 
    }

    render() {
        const { language, topDoctors, isLoading } = this.props;
        let settings = {
            dots: false,
            infinite: true,
            speed: 500,
            slidesToShow: 4,
            slidesToScroll: 2,
            nextArrow: <div className="slick-next"></div>,
            prevArrow: <div className="slick-prev"></div>,
        };
        return (
            <React.Fragment>
                <div className="section-doctors">
                    <div className="doctors-content">
                        <div className="doctors-header">
                            <div className="doctors-title mb-3 ml-1">
                                Bác sĩ nổi bật tuần qua
                            </div>
                            <button
                                type="button"
                                class="btn btn-outline-primary"
                            >
                                Tìm kiếm
                            </button>
                        </div>

                        <Slider {...settings}>
                        {topDoctors && topDoctors.map((doctor, index) => {
                                const imageUrl = doctor.image ? 
                                    `data:image/jpeg;base64,${doctor.image}` : 
                                    '/default-doctor.png';
                                    
                                const name = language === LANGUAGES.VI ?
                                    `${doctor.positionData.valueVi}, ${doctor.lastName} ${doctor.firstName}` :
                                    `${doctor.positionData.valueEn}, ${doctor.firstName} ${doctor.lastName}`;

                                return (
                                    <div className="doctors-item" key={doctor.id}>
                                        <CardDoctor 
                                            imageUrl={imageUrl}
                                            name={name}
                                            position={language === language.vi ? 
                                                doctor.positionData.valueVi : 
                                                doctor.positionData.valueEn
                                            }
                                            onClick={() => this.handleViewDetail(doctor.id)}
                                        />
                                    </div>
                                );
                            })}
                        </Slider>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        isLoggedIn: state.user.isLoggedIn,
        topDoctors: state.admin.topDoctors,
        isLoading: state.admin.isLoading,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        fetchTopDoctors: (limit) => dispatch(actions.fetchTopDoctors(limit)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(FeaturedDoctors);
