import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../../store/actions";

import MenuItems from "./MenuItems";
import { languages } from "../../utils/constant";
import normalLogo from "../../assets/images/logos/logo.png";
import stickyLogo from "../../assets/images/logos/logo.png";
import "./index.scss";

const Header = (props) => {
    const {
        topbarEnable,
        menuCategoryEnable,
        headerClass,
        parentMenu,
        headerNormalLogo,
        headerStickyLogo,
        isLoggedIn,
        userInforr,
        processLogout,
        language,
        changeLanguageAppRedux,
    } = props;

    const history = useHistory();
    const [menuOpen, setMenuOpen] = useState(false);
    const [isVisible, setIsVisible] = useState(false);
    const [userMenuOpen, setUserMenuOpen] = useState(false);

    useEffect(() => {
        // Sticky is displayed after scrolling for 100 pixels
        const toggleVisibility = () => {
            if (window.pageYOffset > 100) {
                setIsVisible(true);
            } else {
                setIsVisible(false);
            }
        };

        window.addEventListener("scroll", toggleVisibility);
        return () => window.removeEventListener("scroll", toggleVisibility);
    }, []);

    // Xử lý đăng xuất
    const handleLogout = () => {
        processLogout();
        history.push("/home");
        setUserMenuOpen(false);
    };

    // Xử lý chuyển đến trang hồ sơ
    const handleGoToProfile = () => {
        history.push("/user-profile");
        setUserMenuOpen(false);
    };

    // Xử lý chuyển đến trang admin
    const handleGoToAdmin = () => {
        history.push("/system/user-manage");
        setUserMenuOpen(false);
    };

    // Xử lý thay đổi ngôn ngữ
    const handleChangeLanguage = (language) => {
        changeLanguageAppRedux(language);
    };

    return (
        <>
            <header
                id="react-header"
                className={
                    headerClass
                        ? headerClass
                        : "react-header react-header-three"
                }
            >
                <div
                    className={
                        isVisible ? "header-area react-sticky" : "header-area"
                    }
                >
                    {topbarEnable ? (
                        <div className="topbar-area style1">
                            <div className="container">
                                <div className="row">
                                    <div className="col-lg-7">
                                        <div className="topbar-contact">
                                            <ul>
                                                <li>
                                                    <svg
                                                        xmlns="http://www.w3.org/2000/svg"
                                                        width="24"
                                                        height="24"
                                                        viewBox="0 0 24 24"
                                                        fill="none"
                                                        stroke="currentColor"
                                                        strokeWidth="2"
                                                        strokeLinecap="round"
                                                        strokeLinejoin="round"
                                                        className="feather feather-phone"
                                                    >
                                                        <path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path>
                                                    </svg>
                                                    <a href="tel:(+84)0123456789">
                                                        {" "}
                                                        (+84) 0123 456 789
                                                    </a>
                                                </li>
                                                <li>
                                                    <svg
                                                        xmlns="http://www.w3.org/2000/svg"
                                                        width="24"
                                                        height="24"
                                                        viewBox="0 0 24 24"
                                                        fill="none"
                                                        stroke="currentColor"
                                                        strokeWidth="2"
                                                        strokeLinecap="round"
                                                        strokeLinejoin="round"
                                                        className="feather feather-mail"
                                                    >
                                                        <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
                                                        <polyline points="22,6 12,13 2,6"></polyline>
                                                    </svg>
                                                    <a href="mailto:info@ctes.org">
                                                        info@ctes.org
                                                    </a>
                                                </li>
                                            </ul>
                                        </div>
                                    </div>
                                    <div className="col-lg-5 text-right">
                                        <div className="toolbar-sl-share">
                                            <ul className="social-links">
                                                <li>
                                                    <a href="#">
                                                        <span
                                                            aria-hidden="true"
                                                            className="social_facebook"
                                                        ></span>
                                                    </a>
                                                </li>
                                                <li>
                                                    <a href="#">
                                                        <span
                                                            aria-hidden="true"
                                                            className="social_twitter"
                                                        ></span>
                                                    </a>
                                                </li>
                                                <li>
                                                    <a href="#">
                                                        <span
                                                            aria-hidden="true"
                                                            className="social_linkedin"
                                                        ></span>
                                                    </a>
                                                </li>
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ) : (
                        ""
                    )}
                    <div className="menu-part">
                        <div className="container">
                            <div className="react-main-menu">
                                <nav>
                                    <div className="menu-toggle">
                                        <div className="logo">
                                            {isVisible ? (
                                                <Link
                                                    to="/"
                                                    className="logo-text"
                                                >
                                                    <img
                                                        src={
                                                            headerStickyLogo
                                                                ? headerStickyLogo
                                                                : stickyLogo
                                                        }
                                                        alt=""
                                                    />
                                                </Link>
                                            ) : (
                                                <Link
                                                    to="/"
                                                    className="logo-text"
                                                >
                                                    <img
                                                        src={
                                                            headerNormalLogo
                                                                ? headerNormalLogo
                                                                : normalLogo
                                                        }
                                                        alt=""
                                                    />
                                                </Link>
                                            )}
                                        </div>
                                        <button
                                            type="button"
                                            id="menu-btn"
                                            className={
                                                menuOpen
                                                    ? "mobile-menu-btn open"
                                                    : "mobile-menu-btn"
                                            }
                                            onClick={() => {
                                                setMenuOpen(!menuOpen);
                                            }}
                                        >
                                            <span className="icon-bar"></span>
                                            <span className="icon-bar"></span>
                                            <span className="icon-bar"></span>
                                        </button>
                                    </div>
                                    <div
                                        className={
                                            menuOpen
                                                ? "react-inner-menus menu-open"
                                                : "react-inner-menus"
                                        }
                                    >
                                        <ul
                                            id="backmenu"
                                            className="react-menus react-sub-shadow"
                                        >
                                            <MenuItems
                                                parentMenu={parentMenu}
                                            />
                                        </ul>

                                        <div className="header-actions">
                                            {/* Hiển thị nút đăng nhập nếu chưa đăng nhập */}
                                            {!isLoggedIn ? (
                                                <div className="login-button">
                                                    <Link to="/login">
                                                        <i className="fas fa-sign-in-alt"></i>{" "}
                                                        Đăng nhập
                                                    </Link>
                                                </div>
                                            ) : (
                                                /* Hiển thị avatar và menu dropdown nếu đã đăng nhập */
                                                <div className="user-dropdown">
                                                    <div
                                                        className="dropdown-toggle"
                                                        onClick={() =>
                                                            setUserMenuOpen(
                                                                !userMenuOpen
                                                            )
                                                        }
                                                    >
                                                        <div className="welcome-text">
                                                            <span>
                                                                Xin chào,
                                                            </span>{" "}
                                                            {userInforr?.lastName ||
                                                                "User"}
                                                        </div>
                                                        <div className="user-avatar">
                                                            {userInforr?.image ? (
                                                                <img
                                                                    src={
                                                                        userInforr.image
                                                                    }
                                                                    alt="User Avatar"
                                                                />
                                                            ) : (
                                                                <div className="avatar-placeholder">
                                                                    {userInforr?.firstName?.charAt(
                                                                        0
                                                                    ) ||
                                                                        userInforr?.lastName?.charAt(
                                                                            0
                                                                        ) ||
                                                                        "U"}
                                                                </div>
                                                            )}
                                                        </div>
                                                    </div>

                                                    {userMenuOpen && (
                                                        <ul className="dropdown-menu">
                                                            <li
                                                                onClick={
                                                                    handleGoToProfile
                                                                }
                                                            >
                                                                <i className="fas fa-user"></i>{" "}
                                                                Hồ sơ cá nhân
                                                            </li>

                                                            {/* Hiển thị menu Admin chỉ cho người dùng có vai trò admin */}
                                                            {userInforr?.roleId ===
                                                                "R1" && (
                                                                <li
                                                                    onClick={
                                                                        handleGoToAdmin
                                                                    }
                                                                >
                                                                    <i className="fas fa-cogs"></i>{" "}
                                                                    Quản trị hệ
                                                                    thống
                                                                </li>
                                                            )}

                                                            <li className="divider"></li>

                                                            <li
                                                                onClick={
                                                                    handleLogout
                                                                }
                                                                className="logout-item"
                                                            >
                                                                <i className="fas fa-sign-out-alt"></i>{" "}
                                                                Đăng xuất
                                                            </li>
                                                        </ul>
                                                    )}
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </nav>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
        </>
    );
};

const mapStateToProps = (state) => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        userInforr: state.user.userInforr,
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        processLogout: () => dispatch(actions.processLogout()),
        changeLanguageAppRedux: (language) =>
            dispatch(actions.changeLanguageApp(language)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
