import React, { Component } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { connect } from "react-redux";
import { DatePicker } from "../../components/Input";
import moment from "moment";
import "./ModalEditEvent.scss";
import { getEventById } from "../../services/eventServices";
import { toast } from "react-toastify";

class ModalEditEvent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            name: "",
            typeEventCode: "",
            date: "",
            address: "",
            quantityMember: 0,
            cost: 0,
            statusCode: "",
            isLoading: false,
            description: "",
            contentMarkdown: "",
            contentHTML: "",
            image: "",
            
            // Load dữ liệu tĩnh cho các options
            eventTypeOptions: [
                { keyMap: 'E1', valueVi: 'Sự kiện nhỏ', valueEn: 'Small Event' },
                { keyMap: 'E2', valueVi: 'Sự kiện trung bình', valueEn: 'Medium Event' },
                { keyMap: 'E3', valueVi: 'Sự kiện lớn', valueEn: 'Large Event' }
            ],
            statusOptions: [
                { keyMap: 'S1', valueVi: 'Đang diễn ra', valueEn: 'Ongoing' },
                { keyMap: 'S2', valueVi: 'Hoàn thành', valueEn: 'Completed' },
                { keyMap: 'S3', valueVi: 'Hủy bỏ', valueEn: 'Canceled' }
            ]
        };
    }

    componentDidMount() {
        // Không cần gọi API để lấy dữ liệu nữa vì đã load tĩnh
    }

    componentDidUpdate(prevProps) {
        // Khi modal mở và có dữ liệu sự kiện, fetch thông tin chi tiết từ API
        if (this.props.isOpen && prevProps.isOpen !== this.props.isOpen) {
            if (this.props.currentEvent && this.props.currentEvent.id) {
                this.fetchEventDetail(this.props.currentEvent.id);
            } else {
                // Reset form nếu là tạo mới
                this.resetForm();
            }
        }
    }

    resetForm = () => {
        this.setState({
            id: "",
            name: "",
            typeEventCode: "",
            date: "",
            address: "",
            quantityMember: 0,
            cost: 0,
            statusCode: "",
            description: "",
            contentMarkdown: "",
            contentHTML: "",
            image: "",
        });
    }

    fetchEventDetail = async (eventId) => {
        try {
            this.setState({ isLoading: true });
            
            const response = await getEventById(eventId);
            
            if (response && response.data.errCode === 0 && response.data) {
                const eventData = response.data.data;
                const hasMarkdown = eventData.eventMarkdown || {};
                
                this.setState({
                    id: eventData.id,
                    name: eventData.name,
                    typeEventCode: eventData.typeEventCode,
                    date: eventData.date ? moment(eventData.date).toDate() : "",
                    address: eventData.address || "",
                    quantityMember: eventData.quantityMember || 0,
                    cost: eventData.cost || 0,
                    statusCode: eventData.statusCode,
                    description: hasMarkdown.description || "",
                    contentMarkdown: hasMarkdown.contentMarkdown || "",
                    contentHTML: hasMarkdown.contentHTML || "",
                    image: hasMarkdown.image || "",
                });
            } else {
                toast.error(response.errMessage || "Không thể tải thông tin sự kiện");
            }
        } catch (error) {
            console.error("Error fetching event details:", error);
            toast.error("Đã xảy ra lỗi khi tải thông tin sự kiện");
        } finally {
            this.setState({ isLoading: false });
        }
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        });
    };

    handleDateChange = (date) => {
        this.setState({
            date,
        });
    };

    handleNumberInputChange = (event) => {
        const { name, value } = event.target;
        // Đảm bảo chỉ nhập số
        if (!isNaN(value) || value === "") {
            this.setState({
                [name]: value === "" ? 0 : parseFloat(value),
            });
        }
    };

    handleSaveEvent = () => {
        // Validate form
        const { name, typeEventCode, date, address, statusCode } = this.state;
        
        if (!name || !typeEventCode || !date || !address || !statusCode) {
            toast.warning("Vui lòng điền đầy đủ thông tin bắt buộc");
            return;
        }
        
        // Chuẩn bị dữ liệu để lưu
        const eventData = {
            id: this.state.id, // Nếu id là empty string, server sẽ hiểu là tạo mới
            name: this.state.name,
            typeEventCode: this.state.typeEventCode,
            date: moment(this.state.date).format("YYYY-MM-DD"),
            address: this.state.address,
            quantityMember: parseInt(this.state.quantityMember),
            cost: parseFloat(this.state.cost),
            statusCode: this.state.statusCode,
            description: this.state.description,
            contentMarkdown: this.state.contentMarkdown,
            contentHTML: this.state.contentHTML,
            image: this.state.image
        };
        
        this.props.updateEvent(eventData);
    };

    getEventTypeName = (code) => {
        const type = this.state.eventTypeOptions.find(item => item.keyMap === code);
        return type ? type.valueVi : '';
    }

    getStatusName = (code) => {
        const status = this.state.statusOptions.find(item => item.keyMap === code);
        return status ? status.valueVi : '';
    }

    render() {
        const { isOpen, toggleModal } = this.props;
        const { 
            name, typeEventCode, date, address, quantityMember,
            cost, statusCode, eventTypeOptions, statusOptions, isLoading,
            description, contentMarkdown, image
        } = this.state;
        cons
        const modalTitle = this.state.id ? "Cập nhật sự kiện" : "Thêm sự kiện mới";
        
        return (
            <Modal 
                isOpen={isOpen} 
                toggle={toggleModal}
                className="event-edit-modal"
                size="lg"
            >
                <ModalHeader toggle={toggleModal}>
                    {modalTitle}
                    {this.state.id && (
                        <div className="event-info-header">
                            <span className="event-type-badge">
                                {this.getEventTypeName(typeEventCode)}
                            </span>
                            <span className={`event-status-badge status-${statusCode}`}>
                                {this.getStatusName(statusCode)}
                            </span>
                        </div>
                    )}
                </ModalHeader>
                <ModalBody>
                    {isLoading ? (
                        <div className="loading-container">
                            <div className="spinner"></div>
                            <p>Đang tải thông tin sự kiện...</p>
                        </div>
                    ) : (
                        <div className="container">
                            <div className="row">
                                <div className="col-12 mb-3">
                                    <label>Tên sự kiện <span className="required">*</span></label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="name"
                                        value={name}
                                        onChange={this.handleInputChange}
                                        placeholder="Nhập tên sự kiện"
                                    />
                                </div>
                                
                                <div className="col-md-6 mb-3">
                                    <label>Loại sự kiện <span className="required">*</span></label>
                                    <select
                                        className="form-control"
                                        name="typeEventCode"
                                        value={typeEventCode}
                                        onChange={this.handleInputChange}
                                    >
                                        <option value="">-- Chọn loại sự kiện --</option>
                                        {eventTypeOptions.map(option => (
                                            <option key={option.keyMap} value={option.keyMap}>
                                                {option.valueVi}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                
                                <div className="col-md-6 mb-3">
                                    <label>Ngày diễn ra <span className="required">*</span></label>
                                    <DatePicker
                                        className="form-control"
                                        onChange={this.handleDateChange}
                                        value={date}
                                        minDate={new Date()}
                                    />
                                </div>
                                
                                <div className="col-12 mb-3">
                                    <label>Địa điểm <span className="required">*</span></label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="address"
                                        value={address}
                                        onChange={this.handleInputChange}
                                        placeholder="Nhập địa điểm tổ chức"
                                    />
                                </div>
                                
                                <div className="col-md-4 mb-3">
                                    <label>Số lượng thành viên</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        name="quantityMember"
                                        value={quantityMember}
                                        onChange={this.handleNumberInputChange}
                                        min="0"
                                    />
                                </div>
                                
                                <div className="col-md-4 mb-3">
                                    <label>Chi phí (VNĐ)</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        name="cost"
                                        value={cost}
                                        onChange={this.handleNumberInputChange}
                                        min="0"
                                    />
                                </div>
                                
                                <div className="col-md-4 mb-3">
                                    <label>Trạng thái <span className="required">*</span></label>
                                    <select
                                        className="form-control"
                                        name="statusCode"
                                        value={statusCode}
                                        onChange={this.handleInputChange}
                                    >
                                        <option value="">-- Chọn trạng thái --</option>
                                        {statusOptions.map(option => (
                                            <option key={option.keyMap} value={option.keyMap}>
                                                {option.valueVi}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="col-12 mb-3">
                                    <label>Mô tả ngắn</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        value={description}
                                        onChange={this.handleInputChange}
                                        placeholder="Nhập mô tả ngắn về sự kiện"
                                    />
                                </div>

                                <div className="col-12 mb-3">
                                    <label>URL Hình ảnh</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="image"
                                        value={image}
                                        onChange={this.handleInputChange}
                                        placeholder="Nhập URL hình ảnh sự kiện"
                                    />
                                    {image && (
                                        <div className="image-preview mt-2">
                                            <img src={image} alt="Event preview" />
                                        </div>
                                    )}
                                </div>

                                <div className="col-12 mb-3">
                                    <label>Nội dung chi tiết (Markdown)</label>
                                    <textarea
                                        className="form-control"
                                        name="contentMarkdown"
                                        value={contentMarkdown}
                                        onChange={this.handleInputChange}
                                        rows="6"
                                        placeholder="Nhập nội dung chi tiết (hỗ trợ cú pháp Markdown)"
                                    ></textarea>
                                </div>
                            </div>
                            
                            <div className="form-notice">
                                <small>Các trường đánh dấu <span className="required">*</span> là bắt buộc</small>
                            </div>
                        </div>
                    )}
                </ModalBody>
                <ModalFooter>
                    <Button 
                        color="primary" 
                        onClick={this.handleSaveEvent}
                        disabled={isLoading}
                    >
                        {isLoading ? 'Đang xử lý...' : 'Lưu'}
                    </Button>{" "}
                    <Button 
                        color="secondary" 
                        onClick={toggleModal}
                    >
                        Hủy
                    </Button>
                </ModalFooter>
            </Modal>
        );
    }
}

export default connect()(ModalEditEvent);
