import React, { Component } from "react";
import { connect } from "react-redux";
import "./FeaturedDoctors.scss";
import { FormattedMessage } from "react-intl";
import Slider from "react-slick";
import * as actions from "../../../../src/store/actions";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import medicalImg from "../../../assets/FeaturedDoctors/1.jpg";
import Carousel from "react-bootstrap/Carousel";
import ExampleCarouselImage from "../../../components/ExampleCarouselImage";
import medicalImg from "../../../assets/medical/1.jpg";
import CardDoctor from "../../../components/CardDoctor";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
class FeaturedDoctors extends Component {

    constructor(props) {
        super(props);
        this.state = {
            arrDoctors: []
        };
    }
    componentDidMount() {
        this.props.loadTopDoctors();
        console.log("check props", this.props);
    }


    render() {
        let settings = {
            dots: false,
            infinite: true,
            speed: 500,
            slidesToShow: 4,
            slidesToScroll: 2,
            nextArrow: <div className="slick-next"></div>,
            prevArrow: <div className="slick-prev"></div>,
        };
        return (
            <React.Fragment>
                <div className="section-doctors">
                    <div className="doctors-content">
                        <div className="doctors-header">
                            <div className="doctors-title mb-3 ml-1">
                                Bác sĩ nổi bật tuần qua
                            </div>
                            <button
                                type="button"
                                class="btn btn-outline-primary"
                            >
                                Tìm kiếm
                            </button>
                        </div>

                        <Slider {...settings}>
                            <div className="doctors-item">
                                <CardDoctor />
                            </div>
                            <div className="doctors-item">
                                <CardDoctor />
                            </div>
                            <div className="doctors-item">
                                <CardDoctor />
                            </div>
                            <div className="doctors-item">
                                <CardDoctor />
                            </div>
                            <div className="doctors-item">
                                <CardDoctor />

                                {/* <img
                                    className="doctors-img"
                                    src={medicalImg}
                                    alt="doctors"
                                /> */}
                                {/* <span className="doctors-title">
                                    Bệnh viện Bạch Mai
                                </span> */}
                            </div>
                        </Slider>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        isLoggedIn: state.user.isLoggedIn,
        topDoctors: state.doctor.topDoctors,
        isLoading: state.doctor.isLoading,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        fetchTopDoctors: (limit) => dispatch(actions.fetchTopDoctors(limit)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(FeaturedDoctors);
