import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { Modal, Button, Form } from "react-bootstrap";
import { FaRoad } from "react-icons/fa";
class ModalUser extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    componentDidMount() {}

    render() {
        // Nhận cáo thuộc tính (props) từ component cha
        const {
            show,
            handleClose,
            userEditData,
            handleChangeUserEditData,
            handleSaveUserEdit,
        } = this.props;
        // Trả về giao diện của Modal
        return (
            <Modal show={show} onHide={handleClose}>
                {/* tiêu đề */}
                <Modal.Header closeButton>
                    <Modal.Title>Edit User Information</Modal.Title>
                </Modal.Header>
                {/* nội dung */}
                <Modal.Body>
                    <form>
                        <div className="form-group">
                            type="text" className="form-control"
                            name="firstName" value={userEditData.firstName}
                            onChange={handleChangeUserEditData}
                        </div>
                        <div className="form-group">
                            type="text" className="form-control"
                            name="LastName" value={userEditData.lastName}
                            onChange={handleChangeUserEditData}
                        </div>
                        <div className="form-group">
                            type="text" className="form-control"
                            name="Email" value={userEditData.email}
                            onChange={handleChangeUserEditData}
                        </div>
                        <div className="form-group">
                            type="text" className="form-control"
                            name="Address" value={userEditData.address}
                            onChange={handleChangeUserEditData}
                        </div>
                    </form>
                </Modal.Body>
                {/* Phần footer của modal */}
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSaveUserEdit}>
                        Save changes
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(ModalUser);
