import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { getAllCodeService } from "../../services/userServices";
import { languages } from "../../utils/constant";

import * as actions from "../../../src/store/"
class UserRedux extends Component {
    constructor(props) {
        super(props);
        this.state = {
            genderArr: [],
        };
    }

    async componentDidMount() {
        // try {
        //     let res = await getAllCodeService("GENDER");
        //     console.log("check res", res);
        //     if (res && res.data.errCode === 0) {
        //         this.setState({ genderArr: res.data.data});
        //     }
        // } catch (error) {
        //     console.log(error);
        // }
    }

    render() {
        console.log("check state", this.state);
        let genders = this.state.genderArr;
        let language = this.props.language;
        return (
            <div className="manage-user">
                <div className="title">
                    <FormattedMessage id="manage-user.title" />
                </div>
                <div className="user-redux-body">
                    <div className="container">
                        <div className="row">
                            <div className="mb-3">
                                <FormattedMessage id="manage-user.add-new-user" />
                            </div>
                            <form>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label htmlFor="email" className="form-label">
                                            <FormattedMessage id="manage-user.email" />
                                        </label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="email"
                                            placeholder="Email"
                                        />
                                    </div>

                                    <div className="col-md-3">
                                        <label htmlFor="password" className="form-label">
                                            <FormattedMessage id="manage-user.password" />
                                        </label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="password"
                                            placeholder="Password"
                                        />
                                    </div>

                                    <div className="col-md-3">
                                        <label htmlFor="firstName" className="form-label">
                                            <FormattedMessage id="manage-user.firstname" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="firstName"
                                            placeholder="Firstname"
                                        />
                                    </div>
                                    <div className="col-md-3">
                                        <label htmlFor="lastName" className="form-label">
                                            <FormattedMessage id="manage-user.lastname" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="lastName"
                                            placeholder="Lastname"
                                        />
                                    </div>
                                </div>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label htmlFor="phonenumber" className="form-label">
                                            <FormattedMessage id="manage-user.phonenumber" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="phonenumber"
                                            placeholder="Phonenumber"
                                        />
                                    </div>
                                    <div className="col-md-9">
                                        <label htmlFor="address" className="form-label">
                                            <FormattedMessage id="manage-user.address" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="address"
                                            placeholder="1234 Main St"
                                        />
                                    </div>
                                </div>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label htmlFor="gender" className="form-label">
                                            <FormattedMessage id="manage-user.gender" />
                                        </label>
                                        <select id="inputState" className="form-control">
                                            {genders && genders.length>0 && genders.map((item, index) => {
                                                console.log("Gender item:", item);
                                                return (
                                                    <option key={index} value={item.keyMap}>
                                                        {language === languages.VI ? item.valueVi : item.valueEn}
                                                    </option>
                                                );
                                            })}
                                        </select>
                                        
                                    </div>

                                    <div className="col-md-3">
                                        <label htmlFor="position" className="form-label">
                                            <FormattedMessage id="manage-user.position" />
                                        </label>
                                        <select id="inputState" className="form-control">
                                            <option selected>Choose...</option>
                                        </select>
                                    </div>

                                    <div className="col-md-3">
                                        <label htmlFor="role" className="form-label">
                                            <FormattedMessage id="manage-user.role" />
                                        </label>
                                        <select id="inputState" className="form-control">
                                            <option selected>Choose...</option>
                                            <option>...</option>
                                        </select>
                                    </div>
                                    <div className="col-md-3">
                                        <label htmlFor="avatar" className="form-label">
                                            <FormattedMessage id="manage-user.avatar" />
                                        </label>
                                        <label htmlFor="exampleFormControlFile1">
                                            <FormattedMessage id="manage-user.example-file-input" />
                                        </label>
                                        <input
                                            type="file"
                                            className="form-control-file"
                                            id="exampleFormControlFile1"
                                        />
                                    </div>
                                </div>

                                <button type="submit" className="btn btn-primary">
                                    <FormattedMessage id="manage-user.submit" />
                                </button>
                            </form>
                            <select class="form-control form-control-lg">
  <option>Large select</option>
</select>
<select class="form-control">
  <option>Default select</option>
</select>
<select class="form-control form-control-sm">
  <option>Small select</option>
</select>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
