import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import Breadcrumb from '../../components/Breadcrumb/EventBreadcrumbs';
import EventDetailsMain from './EventDetailsMain';
import ScrollToTop from '../../components/ScrollTop';
import { getRecentEvents } from '../../services/eventServices';
import { toast } from 'react-toastify';
import Logo from '../../assets/images/logos/logo2.png';
import Slider from "react-slick";

const EventDetails = () => {
    const [event, setEvent] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        fetchEventDetail();
    }, [id]);

    const fetchEventDetail = async () => {
        try {
            const response = await getRecentEvents();
            if (response?.data?.errCode === 0) {
                const eventData = response.data.data.find(e => e.id === Number(id));
                if (eventData) {
                    setEvent(eventData);
                } else {
                    toast.error('Không tìm thấy sự kiện');
                    history.push('/event');
                }
            }
        } catch (error) {
            console.error("Error:", error);
            toast.error("Không thể tải thông tin sự kiện");
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (!event) return null;

    return (
        <div className="course-single">
            <Header
                parentMenu='event'
                headerNormalLogo={Logo}
                headerStickyLogo={Logo}
            />
            <div className="react-wrapper">
                <div className="react-wrapper-inner">
                    <Breadcrumb
                        eventID={event.id}
                        eventImg={event.eventMarkdown?.image}
                        eventBannerImg={event.eventMarkdown?.image}
                        eventDate={event.date}
                        eventStartTime={event.startTime || '08:00'} // Default time
                        eventEndTime={event.endTime || '17:00'} // Default time
                        eventCategory={event.eventType?.valueVi || 'Sự kiện'}
                        eventTitle={event.name}
                        eventLocation={event.address}
                            eventEndTime= {event.endTime}
                            eventCategory= {event.category}
                            eventTitle={data.name}
                            eventBtnText= "Find Out More"
                            eventLocation={data.address}
                        />
                        )
                    }).slice(0, 12)}
                
                
     

                    <EventDetailsMain
                        eventID= {event.id}
                        eventImg= {event.image}
                        eventBannerImg= {event.bannerImg}
                        eventDayCount= {event.dayCount}
                        eventDate= {event.date}
                        eventStartTime= {event.startTime}
                        eventEndTime= {event.endTime}
                        eventCategory= {event.category}
                        eventTitle= {event.title}
                        eventBtnText= "Find Out More"
                        eventLocation= {event.location}
                        eventCost= {event.cost}
                        eventHost= {event.host}
                        eventTotalSlot= {event.totalSlot}
                        eventBookedSlot= {event.bookedSlot}
                        eventContactNo= {event.phone}
                    />

                    {/* scrolltop-start */}
                    <ScrollToTop />
                    {/* scrolltop-end */}
                </div>
            </div>

            <Footer />

        </div>
    );
}

export default EventDetails;
