import React, { Component } from "react";
import MarkdownIt from "markdown-it";
import MdEditor from "react-markdown-editor-lite";
import "react-markdown-editor-lite/lib/index.css";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import { createEventDescription } from "../../services/Service";
import Select from 'react-select';

const mdParser = new MarkdownIt();

class EventDescription extends Component {
    constructor(props) {
        super(props);
        this.state = {
            contentMarkdown: "",
            contentHTML: "",
            description: "",
            selectedPosition: null,
            selectedEvent: null,
            image: "",
            positions: [],
            events: []
        };
    }

    async componentDidMount() {
        try {
            // Fetch positions and events from Allcodes
            const positions = await getAllCodeService("POSITION");
            const events = await getAllCodeService("EVENT");
            
            this.setState({
                positions: this.buildSelectOptions(positions.data),
                events: this.buildSelectOptions(events.data)
            });
        } catch (error) {
            console.error("Error fetching data:", error);
            toast.error("Error loading data");
        }
    }

    buildSelectOptions = (items) => {
        return items.map(item => ({
            value: item.keyMap,
            label: item.valueVi
        }));
    };

    handleEditorChange = ({ html, text }) => {
        this.setState({
            contentMarkdown: text,
            contentHTML: html
        });
    };

    handleImageChange = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = () => {
            this.setState({ image: reader.result });
        };
    };

    handleSaveContent = async () => {
        const { contentHTML, contentMarkdown, description, 
                selectedPosition, selectedEvent, image } = this.state;

        if (!selectedEvent) {
            toast.error("Please select an event");
            return;
        }

        try {
            const response = await createEventDescription({
                contentHTML,
                contentMarkdown,
                description,
                positionId: selectedPosition?.value || null,
                eventId: selectedEvent.value,
                image
            });

            if (response && response.data.errCode === 0) {
                toast.success("Save description successfully!");
                this.setState({
                    contentMarkdown: "",
                    contentHTML: "",
                    description: "",
                    selectedPosition: null,
                    selectedEvent: null,
                    image: ""
                });
            } else {
                toast.error(response.data.errMessage);
            }
        } catch (error) {
            console.error("Error saving content:", error);
            toast.error("Error saving content");
        }
    };

    render() {
        const { selectedPosition, selectedEvent, positions, events } = this.state;

        return (
            <div className="event-description-container p-4">
                <h2 className="text-center mb-4">Event Description Editor</h2>
                
                <div className="row mb-4">
                    <div className="col-md-6">
                        <label className="form-label">Event Type:</label>
                        <Select
                            value={selectedEvent}
                            onChange={(option) => this.setState({ selectedEvent: option })}
                            options={events}
                            className="basic-select"
                            placeholder="Select event type"
                            isSearchable={true}
                        />
                    </div>
                    <div className="col-md-6">
                        <label className="form-label">Position (Optional):</label>
                        <Select
                            value={selectedPosition}
                            onChange={(option) => this.setState({ selectedPosition: option })}
                            options={positions}
                            className="basic-select"
                            placeholder="Select position"
                            isClearable={true}
                            isSearchable={true}
                        />
                    </div>
                </div>

                <div className="mb-4">
                    <label className="form-label">Short Description:</label>
                    <textarea
                        className="form-control"
                        rows="3"
                        value={this.state.description}
                        onChange={(e) => this.setState({ description: e.target.value })}
                    ></textarea>
                </div>

                <div className="mb-4">
                    <label className="form-label">Featured Image:</label>
                    <input
                        type="file"
                        className="form-control"
                        accept="image/*"
                        onChange={this.handleImageChange}
                    />
                </div>

                <div className="markdown-editor mb-4">
                    <label className="form-label">Detailed Description:</label>
                    <MdEditor
                        style={{ height: "500px" }}
                        renderHTML={(text) => mdParser.render(text)}
                        onChange={this.handleEditorChange}
                        value={this.state.contentMarkdown}
                    />
                </div>

                <button
                    className="btn btn-primary"
                    onClick={this.handleSaveContent}
                >
                    Save Description
                </button>
            </div>
        );
    }
}

const mapStateToProps = (state) => ({
    language: state.app.language
});

export default connect(mapStateToProps)(EventDescription);
