import React, { Component } from "react";
import { connect } from "react-redux";
import "./EventDisplay.scss";
import { getAllEvents, deleteEventById } from "../../services/eventServices";
import { toast } from "react-toastify";
import ModalEditEvent from "./ModalEditEvent";
import moment from "moment";

class EventDisplay extends Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [],
            isOpenModalEdit: false,
            currentEvent: null,
        };
    }

    async componentDidMount() {
        await this.fetchEvents();
    }

    fetchEvents = async () => {
        try {
            const response = await getAllEvents();
            
            if (response && response.errCode === 0) {
                this.setState({
                    events: response.data || [],
                });
            } else {
                toast.error(response.errMessage || "Có lỗi khi lấy danh sách sự kiện");
            }
        } catch (error) {
            console.error("Lỗi khi lấy danh sách sự kiện:", error);
            toast.error("Không thể kết nối đến server");
        }
    };

    handleEditEvent = (event) => {
        this.setState({
            isOpenModalEdit: true,
            currentEvent: event,
        });
    };

    handleCreateNewEvent = () => {
        this.setState({
            isOpenModalEdit: true,
            currentEvent: null, // null nghĩa là tạo mới
        });
    };

    toggleModalEdit = () => {
        this.setState({
            isOpenModalEdit: false,
            currentEvent: null,
        });
    };

    handleDeleteEvent = async (eventId) => {
        try {
            // Tìm event muốn xóa trong state để hiển thị tên
            const eventToDelete = this.state.events.find(e => e.id === eventId);
            
            if (window.confirm(`Bạn có chắc chắn muốn xóa sự kiện "${eventToDelete.name}"?`)) {
                // Cần thêm API deleteEventById vào eventServices.js
                const response = await deleteEventById(eventId);
                
                if (response && response.errCode === 0) {
                    toast.success("Xóa sự kiện thành công");
                    await this.fetchEvents(); // Cập nhật lại danh sách
                } else {
                    toast.error(response.errMessage || "Có lỗi khi xóa sự kiện");
                }
            }
        } catch (error) {
            console.error("Lỗi khi xóa sự kiện:", error);
            toast.error("Không thể kết nối đến server");
        }
    };

    handleUpdateEvent = async (eventData) => {
        try {
            // Cập nhật sự kiện
            // Cần thêm API updateEvent vào eventServices.js
            const response = await updateEvent(eventData);
            
            if (response && response.errCode === 0) {
                toast.success("Cập nhật sự kiện thành công");
                await this.fetchEvents(); // Cập nhật lại danh sách
                this.toggleModalEdit();
                
                // Hỏi admin xem có muốn gửi thông báo về việc cập nhật sự kiện không
                if (window.confirm("Bạn có muốn gửi thông báo về việc cập nhật sự kiện này không?")) {
                    // TODO: Mở modal gửi thông báo
                    this.openSendNotificationModal(eventData);
                }
            } else {
                toast.error(response.errMessage || "Có lỗi khi cập nhật sự kiện");
            }
        } catch (error) {
            console.error("Lỗi khi cập nhật sự kiện:", error);
            toast.error("Không thể kết nối đến server");
        }
    };

    openSendNotificationModal = (event) => {
        // TODO: Implement notification modal
        console.log("Mở modal gửi thông báo cho sự kiện:", event);
    };

    // Hàm định dạng ngày tháng
    formatDate = (dateString) => {
        return moment(dateString).format("DD/MM/YYYY");
    };

    // Hàm định dạng tiền tệ
    formatCurrency = (value) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(value);
    };

    // Hàm render badge trạng thái
    renderStatusBadge = (status, statusCode) => {
        let className = "";
        
        switch (statusCode) {
            case "S1":
                className = "badge-info";
                break;
            case "S2":
                className = "badge-success";
                break;
            case "S3":
                className = "badge-danger";
                break;
            default:
                className = "badge-secondary";
        }
        
        return <span className={`status-badge ${className}`}>{status}</span>;
    };

    render() {
        const { events, isOpenModalEdit, currentEvent } = this.state;
        
        return (
            <div className="event-display-container">
                <div className="title text-center">
                    <h2>Quản lý sự kiện</h2>
                </div>

                <div className="action-buttons mb-3">
                    <button 
                        className="btn btn-primary" 
                        onClick={this.handleCreateNewEvent}
                    >
                        <i className="fas fa-plus"></i> Thêm sự kiện mới
                    </button>
                </div>

                <div className="table-container">
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Tên sự kiện</th>
                                <th scope="col">Loại sự kiện</th>
                                <th scope="col">Ngày diễn ra</th>
                                <th scope="col">Địa điểm</th>
                                <th scope="col">Số thành viên</th>
                                <th scope="col">Chi phí</th>
                                <th scope="col">Trạng thái</th>
                                <th scope="col">Thao tác</th>
                            </tr>
                        </thead>
                        <tbody>
                            {events && events.length > 0 ? 
                                events.map((event) => (
                                    <tr key={event.id}>
                                        <td>{event.id}</td>
                                        <td>{event.name}</td>
                                        <td>{event.eventType?.valueVi}</td>
                                        <td>{this.formatDate(event.date)}</td>
                                        <td>{event.address}</td>
                                        <td>{event.quantityMember}</td>
                                        <td>{this.formatCurrency(event.cost)}</td>
                                        <td>
                                            {this.renderStatusBadge(
                                                event.status?.valueVi,
                                                event.statusCode
                                            )}
                                        </td>
                                        <td>
                                            <div className="action-buttons">
                                                <button
                                                    className="btn btn-warning btn-sm mx-1"
                                                    onClick={() => this.handleEditEvent(event)}
                                                    title="Chỉnh sửa"
                                                >
                                                    <i className="fas fa-edit"></i>
                                                </button>
                                                <button
                                                    className="btn btn-danger btn-sm mx-1"
                                                    onClick={() => this.handleDeleteEvent(event.id)}
                                                    title="Xóa"
                                                >
                                                    <i className="fas fa-trash"></i>
                                                </button>
                                                <button
                                                    className="btn btn-info btn-sm mx-1"
                                                    onClick={() => this.openSendNotificationModal(event)}
                                                    title="Gửi thông báo"
                                                >
                                                    <i className="fas fa-bell"></i>
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))
                            : (
                                <tr>
                                    <td colSpan="9" className="text-center">
                                        Không có sự kiện nào
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>

                <ModalEditEvent
                    isOpen={isOpenModalEdit}
                    toggleModal={this.toggleModalEdit}
                    currentEvent={currentEvent}
                    updateEvent={this.handleUpdateEvent}
                />
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventDisplay);
