const GITHUB_TOKEN = 'ghp_dFE0zqP6mj2f0O9hxVELWCmXUbPFqW1kPlVx'; // Token của bạn
const REPO_OWNER = 'Trantoan12022004';
const REPO_NAME = 'host_image_page_anhtonton';

export const uploadImageToGithub = async (imageFile) => {
    try {
        const imageData = new FormData();
        imageData.append('image', imageFile);
        
        // Upload to host service (like Imgur) first since GitHub needs a URL
        const uploadResponse = await fetch('https://api.imgur.com/3/image', {
            method: 'POST',
            headers: {
                'Authorization': 'Client-ID YOUR_IMGUR_CLIENT_ID'
            },
            body: imageData
        });

        const uploadData = await uploadResponse.json();
        const imageUrl = uploadData.data.link;

        return imageUrl;
    } catch (error) {
        console.error('Error uploading image:', error);
        throw error;
    }
};
