import React, { Component, useState, useEffect } from 'react';
import Slider from "react-slick";

// import SectionTitle from '../../components/SectionTitle'
import SectionTitle from '../../../components/SectionTitle/index.js'
import SingleEvent from '../../../components/Event/SingleEvent';
import { toast } from 'react-toastify';
import events from '../../../data/Events.json';

const Event = () => {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await getRecentEvents();
            if (response && response.data && response.data.errCode === 0) {
                setEvents(response.data.data);
            }
        } catch (error) {
            console.error("Error fetching events:", error);
            toast.error("Không thể tải danh sách sự kiện");
        } finally {
            setLoading(false);
        }
    };
    const eventSettings = {
        dots: true,
        arrows: false, 
        infinite: true,
        centerMode: false,
        slidesToShow: 4,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1199,
                settings: {
                    slidesToShow: 2,
                }
            },
            {

                breakpoint: 767,
                settings: {
                    slidesToShow: 1,
                    arrows: false,
                }
            }
        ]
    };

    return (
        <div className="react-upcoming__event blog__area">
            <div className="container">
                <SectionTitle Title="Upcoming Events" />
                <div className="event-slider owl-carousel wow animate__fadeInUp" data-wow-duration="0.3s">
                    <Slider {...eventSettings}> 
                        {events.map((data, index) => {
                            return (
                                <SingleEvent
                                    eventID= {data.id}
                                    eventImg= {data.image}
                                    eventBannerImg= {data.bannerImg}
                                    eventDayCount= {data.dayCount}
                                    eventDate= {data.date}
                                    eventStartTime= {data.startTime}
                                    eventEndTime= {data.endTime}
                                    eventCategory= {data.category}
                                    eventTitle= {data.title}
                                    eventBtnText= "Xem chi tiết"
                                    eventLocation= {data.location}
                                />
                            )
                        }).slice(0, 6)}
                    </Slider>
                </div>                        
            </div>
        </div>
    );
}

export default Event;
