import React, { Component } from "react";
import { connect } from "react-redux";
import "./EventDisplay.scss";
import { getAllEvents, deleteEvent, updateEvent } from "../../services/eventServices";
import { toast } from "react-toastify";
import ModalEditEvent from "./ModalEditEvent";
import moment from "moment";
import { FormattedMessage } from "react-intl";
class EventDisplay extends Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [],
            isOpenModalEdit: false,
            eventId: null,
        };
    }

    async componentDidMount() {
        await this.fetchEvents();
    }
    // Thêm các hàm xử lý modal
    handleEditEvent = (eventId) => {
        this.setState({
            isOpenModalEdit: true,
            eventId: eventId,
        });
    };

    handleCloseModalEdit = () => {
        this.setState({
            isOpenModalEdit: false,
            eventId: null,
        });
    };

    // Hàm xử lý sau khi cập nhật thành công
    handleEditEventSuccess = () => {
        this.fetchEvents(); // Lấy lại danh sách sự kiện sau khi cập nhật
        this.handleCloseModalEdit(); // Đóng modal
    };
    handleDeleteEvent = async (eventId) => {
        try {
            const eventToDelete = this.state.events.find((event) => event.id === eventId);
            const fullName = `${eventToDelete.name}`;
            if (window.confirm(`Bạn có chắc chắn muốn xóa sự kiện: ${fullName}?`)) {
                let response = await deleteEvent(eventId);
                if (response && response.data.errCode === 0) {
                    toast.success(`Xóa sự kiện ${fullName} thành công!`);
                    await this.fetchEvents();
                } else {
                    toast.error(`Xóa sự kiện ${fullName} thất bại: ${response.data.errMessage}`);
                }
            }
        } catch (error) {
            toast.error("Có lỗi xảy ra khi xóa sự kiện");
            console.error("Lỗi khi xóa sự kiện:", error);
        }
    };
    
    fetchEvents = async () => {
        try {
            const response = await getAllEvents();
            console.log("response", response);
            if (response && response.data.errCode === 0) {
                this.setState({
                    events: response.data.data || [],
                });
            } else {
                toast.error(response.data.errMessage || "Có lỗi khi lấy danh sách sự kiện");
            }
        } catch (error) {
            console.error("Lỗi khi lấy danh sách sự kiện:", error);
            toast.error("Không thể kết nối đến server");
        }
    };

    render() {
        const { events, isOpenModalEdit, eventId } = this.state;
        console.log("events", events);

        return (
            <div className="event-display-container">
                <div className="title text-center">
                    <h2>Quản lý sự kiện</h2>
                </div>
                <div className="table-container">
                    <table className="table table-hover">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Tên sự kiện</th>
                                <th scope="col">Loại sự kiện</th>
                                <th scope="col">Ngày diễn ra</th>
                                <th scope="col">Địa điểm</th>
                                <th scope="col">Số thành viên</th>
                                <th scope="col">Chi phí</th>
                                <th scope="col">Trạng thái</th>
                                <th scope="col">Thao tác</th>
                            </tr>
                        </thead>
                        <tbody>
                            {events && events.length > 0 ? (
                                events.map((event, index) => {
                                    return (
                                        <tr key={index}>
                                            <td>{event.id}</td>
                                            <td>{event.name}</td>
                                            <td>{event.eventType.valueVi}</td>
                                            <td>{event.date}</td>
                                            <td>{event.address}</td>
                                            <td>{event.quantityMember}</td>
                                            <td>{event.cost}</td>
                                            <td>{event.status.valueVi}</td>
                                            <td>
                                                <button className="btn btn-primary" onClick={() => this.handleEditEvent((event.id))} title="Chỉnh sửa">
                                                    <i className="fas fa-edit"></i>
                                                </button>
                                                <button className="btn btn-info" onClick={() => this.setState({ isOpenModalEdit: true, currentEvent: event })} title="Thông báo">
                                                    <i className="fas fa-bell"></i>
                                                </button>
                                                <button className="btn btn-danger" onClick={() => this.handleDeleteEvent(event.id)} title="Xóa">
                                                    <i className="fas fa-trash"></i>
                                                </button>
                                                <button className="btn btn-primary" onClick={() => this.handleDeleteEvent(event.id)} title="Xóa">
                                                    <i className="fas fa-chart-bar"></i>
                                                </button>
                                            </td>
                                        </tr>
                                    );
                                })
                            ) : (
                                <tr>
                                    <td colSpan="9" className="text-center">
                                        Không có sự kiện nào
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>

                    {/* Thêm Modal */}
                    {isOpenModalEdit && (
                        <ModalEditEvent isOpen={isOpenModalEdit} eventId={eventId} closeModal={this.handleCloseModalEdit} onSuccess={this.handleEditEventSuccess} />
                    )}
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventDisplay);
