import actionTypes from "../actions/actionTypes";

const initialState = {
    isLoading: false,
    genders: [],
    roles: [],
    positions: [],
    error: null,
    successMessage: "",
    users: [],
    isLoadingUsers: false

};

const adminReducer = (state = initialState, action) => {
    // Ghi log một lần bên ngoài switch

    switch (action.type) {
        // loadGender
        case actionTypes.FETCH_GENDER_START:
            return {
                ...state,
                isLoading: true,
            };

        case actionTypes.FETCH_GENDER_SUCCESS:
            return {
                ...state,
                genders: action.data,
                isLoading: false,
            };

        case actionTypes.FETCH_GENDER_FAILED:
            return {
                ...state,
                isLoading: false,
                genders: [],
            };

        // loadposition
        case actionTypes.FETCH_POSITION_START:
            return {
                ...state,
                isLoading: true,
            };
        case actionTypes.FETCH_POSITION_SUCCESS:
            return {
                ...state,
                positions: action.data,
                isLoading: false,
            };
        case actionTypes.FETCH_POSITION_FAILED:
            return {
                ...state,
                isLoading: false,
            };

        case actionTypes.FETCH_ROLE_START: // Thêm trường hợp FETCH_ROLE_START
            return {
                ...state,
                isLoading: true,
            };
        case actionTypes.FETCH_ROLE_SUCCESS: // Thêm trường hợp FETCH_ROLE_SUCCESS
            return {
                ...state,
                roles: action.data,
                isLoading: false,
            };
        case actionTypes.FETCH_ROLE_FAILED: // Thêm trường hợp FETCH_ROLE_FAILED
            return {
                ...state,
                isLoading: false,
            };

        case actionTypes.CREATE_USER_SUCCESS:
            console.log("check acction", action.message)
            return {
                ...state,
                isLoading: false,
                successMessage: action.message,
                error: null,
            };
        case actionTypes.CREATE_USER_FAILED:
            console.log("check acction", action.error)
            return {
                ...state,
                isLoading: false,
                error: action.error,
                successMessage: "",
            };
        default:
            return state;
    }
};

export default adminReducer;
