import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import * as actions from "../../store/actions";
import "./Login.scss";
import { FormattedMessage } from "react-intl";
import { languages } from "../../utils";
import { Form, Button, Container, Row, Col, Card, InputGroup } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faFacebookF,
    faTwitter,
    faGoogle,
    faGithub,
} from "@fortawesome/free-brands-svg-icons";
import {
    faEnvelope,
    faLock,
    faEye,
    faEyeSlash,
} from "@fortawesome/free-solid-svg-icons";

import { handleLoginApi } from "../../services/userServices";
import logoImg from "../../assets/images/logos/logo.png"; // Điều chỉnh đường dẫn đến logo của bạn

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: "",
            password: "",
            errMessage: "",
            showPassword: false,
            isLoading: false
        };
    }

    handleOnChangeUsername = (event) => {
        this.setState({
            userName: event.target.value,
            errMessage: ""
        });
    };

    handleOnChangePassword = (event) => {
        this.setState({
            password: event.target.value,
            errMessage: ""
        });
    };

    toggleShowPassword = () => {
        this.setState({
            showPassword: !this.state.showPassword
        });
    };

    handleLogin = async () => {
        this.setState({
            errMessage: "",
            isLoading: true
        });

        // Kiểm tra form trước khi gửi
        if (!this.state.userName || !this.state.password) {
            this.setState({
                errMessage: "Vui lòng nhập email và mật khẩu",
                isLoading: false
            });
            return;
        }
        
        try {
            let response = await handleLoginApi(
                this.state.userName,
                this.state.password
            );
            
            this.setState({ isLoading: false });
            
            if (response.data.errCode !== 0) {
                this.setState({
                    errMessage: response.data.message || "Đăng nhập không thành công"
                });
            } else {
                this.props.userLoginSuccess(response.data.user);
            }
        } catch (error) {
            this.setState({ isLoading: false });
            
            if (error.response && error.response.data) {
                this.setState({
                    errMessage: error.response.data.message
                });
            } else {
                this.setState({
                    errMessage: "Không thể kết nối đến máy chủ"
                });
            }
        }
    };

    handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            this.handleLogin();
        }
    };

    render() {
        const { userName, password, errMessage, showPassword, isLoading } = this.state;
        
        return (
            <div className="login-page">
                <Container className="py-5">
                    <Row className="justify-content-center align-items-center">
                        <Col md={8} lg={7} xl={6} className="mb-4 mb-md-0">
                            <div className="login-welcome text-center text-md-start">
                                <div className="logo-container mb-4">
                                    <img src={logoImg} alt="CTES Logo" className="login-logo" />
                                </div>
                                <h2 className="welcome-heading">Chào mừng trở lại!</h2>
                                <p className="welcome-text">
                                    Đăng nhập để tham gia các sự kiện môi trường cùng CTES và theo dõi các
                                    hoạt động bảo vệ môi trường mới nhất
                                </p>
                                <div className="welcome-image-container">
                                    <img 
                                        src="https://img.freepik.com/free-vector/mobile-login-concept-illustration_114360-83.jpg" 
                                        alt="Login Illustration" 
                                        className="welcome-image" 
                                    />
                                </div>
                            </div>
                        </Col>
                        
                        <Col md={8} lg={5} xl={5}>
                            <Card className="login-card border-0 shadow">
                                <Card.Body className="p-4 p-md-">
                                    <h3 className="card-title text-center mb-4">Đăng nhập</h3>
                                    
                                    {errMessage && (
                                        <div className="alert alert-danger" role="alert">
                                            <FontAwesomeIcon icon="exclamation-triangle" className="me-2" />
                                            {errMessage}
                                        </div>
                                    )}
                                    
                                    <Form>
                                        <Form.Group className="mb-4" controlId="formEmail">
                                            <Form.Label>Email đăng nhập</Form.Label>
                                            <InputGroup>
                                                <InputGroup.Text className="bg-light">
                                                    <FontAwesomeIcon icon={faEnvelope} />
                                                </InputGroup.Text>
                                                <Form.Control
                                                    type="email"
                                                    placeholder="Nhập email của bạn"
                                                    value={userName}
                                                    onChange={this.handleOnChangeUsername}
                                                    onKeyDown={this.handleKeyDown}
                                                    className="py-2"
                                                />
                                            </InputGroup>
                                        </Form.Group>

                                        <Form.Group className="mb-4" controlId="formPassword">
                                            <Form.Label>Mật khẩu</Form.Label>
                                            <InputGroup>
                                                <InputGroup.Text className="bg-light">
                                                    <FontAwesomeIcon icon={faLock} />
                                                </InputGroup.Text>
                                                <Form.Control
                                                    type={showPassword ? "text" : "password"}
                                                    placeholder="Nhập mật khẩu của bạn"
                                                    value={password}
                                                    onChange={this.handleOnChangePassword}
                                                    onKeyDown={this.handleKeyDown}
                                                    className="py-2"
                                                />
                                                <Button 
                                                    variant="outline-secondary"
                                                    onClick={this.toggleShowPassword}
                                                    className="show-password-btn"
                                                >
                                                    <FontAwesomeIcon icon={showPassword ? faEyeSlash : faEye} />
                                                </Button>
                                            </InputGroup>
                                        </Form.Group>

                                        <div className="d-flex justify-content-between align-items-center mb-4">
                                            <Form.Check
                                                type="checkbox"
                                                id="rememberMe"
                                                label="Ghi nhớ đăng nhập"
                                                className="remember-check"
                                            />
                                            <a href="#forgot-password" className="forgot-link">Quên mật khẩu?</a>
                                        </div>

                                        <Button
                                            variant="primary"
                                            className="login-btn w-100 py-2 mb-4"
                                            onClick={this.handleLogin}
                                            disabled={isLoading}
                                        >
                                            {isLoading ? 'Đang xử lý...' : 'Đăng nhập'}
                                        </Button>

                                        <div className="text-center mb-4">
                                            <p className="mb-0">
                                                Chưa có tài khoản? <a href="/register" className="register-link">Đăng ký ngay</a>
                                            </p>
                                        </div>
                                        
                                        <div className="separator">
                                            <span>Hoặc đăng nhập với</span>
                                        </div>

                                        <div className="social-buttons">
                                            <Button variant="outline-primary" className="social-btn facebook">
                                                <FontAwesomeIcon icon={faFacebookF} />
                                            </Button>
                                            <Button variant="outline-info" className="social-btn twitter">
                                                <FontAwesomeIcon icon={faTwitter} />
                                            </Button>
                                            <Button variant="outline-danger" className="social-btn google">
                                                <FontAwesomeIcon icon={faGoogle} />
                                            </Button>
                                            <Button variant="outline-dark" className="social-btn github">
                                                <FontAwesomeIcon icon={faGithub} />
                                            </Button>
                                        </div>
                                    </Form>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        navigate: (path) => dispatch(push(path)),
        userLoginSuccess: (userInfor) => dispatch(actions.userLoginSuccess(userInfor)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
