import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Breadcrumb from "../../components/Breadcrumb/EventBreadcrumbs";
import EventDetailsMain from "./EventDetailsMain";
import ScrollToTop from "../../components/ScrollTop";
import { getRecentEvents } from "../../services/eventServices";
import { toast } from "react-toastify";
import Logo from "../../assets/images/logos/logo2.png";
import Slider from "react-slick";

const EventDetails = () => {
    const [event, setEvent] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        fetchEventDetail();
    }, [id]);

    const fetchEventDetail = async () => {
        try {
            const response = await getRecentEvents();
            if (response?.data?.errCode === 0) {
                const eventData = response.data.data.find(
                    (e) => e.id === Number(id)
                );
                if (eventData) {
                    setEvent(eventData);
                } else {
                    toast.error("Không tìm thấy sự kiện");
                    setTimeout(() => {
                        history.push("/event");
                    }, 2000); // Đợi 2 giây để toast hiển thị
                }
            }
        } catch (error) {
            console.error("Error:", error);
            toast.error("Không thể tải thông tin sự kiện");
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (!event) return null;

    return (
        <div className="course-single">
            <Header
                parentMenu="event"
                headerNormalLogo={Logo}
                headerStickyLogo={Logo}
            />
            <div className="react-wrapper">
                <div className="react-wrapper-inner">
                    <Breadcrumb
                        eventID={event.id}
                        eventImg={event.eventMarkdown?.image}
                        eventBannerImg={event.eventMarkdown?.image}
                        eventDate={event.date}
                        eventStartTime={event.startTime || "08:00"} // Default time
                        eventEndTime={event.endTime || "17:00"} // Default time
                        eventCategory={event.eventType?.valueVi || "Sự kiện"}
                        eventTitle={event.name}
                        eventLocation={event.address}
                    />

                    <EventDetailsMain
                        eventID={event.id}
                        eventImg={event.eventMarkdown?.image}
                        eventBannerImg={event.eventMarkdown?.image}
                        eventDate={event.date}
                        eventStartTime={event.startTime || "08:00"}
                        eventEndTime={event.endTime || "17:00"}
                        eventCategory={event.eventType?.valueVi}
                        eventTitle={event.name}
                        eventLocation={event.address}
                        eventCost={event.cost || "0"}
                        eventHost={"CTES"} // Default host
                        eventTotalSlot={event.quantityMember || 0}
                        eventBookedSlot={0} // Default booked slots
                        eventContactNo={"0123456789"} // Default contact
                        eventDescription={event.eventMarkdown?.description}
                        eventContent={event.eventMarkdown?.contentHTML}
                    />

                    <ScrollToTop />
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default EventDetails;
