import React, { Component } from "react";
import { connect } from "react-redux";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Breadcrumb from "../../components/Breadcrumb";
import ScrollToTop from "../../components/ScrollTop";
import Logo from "../../assets/images/logos/logo2.png";
import ContactForm from "./ContactForm";
import { getEventById } from "../../services/eventServices"; // Tạo service này
import { toast } from "react-toastify";
import "./EventRegistration.scss";

class EventRegistration extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: null,
            eventData: null,
            isLoading: true,
        }
    }

    
    async componentDidMount() {
        // Lấy eventId từ URL params
        const { match } = this.props;
        const eventId = match.params.id;
        
        if (eventId) {
            this.setState({ eventId });
            await this.fetchEventData(eventId);
        }
    }


    fetchEventData = async (eventId) => {
        try {
            // Gọi API lấy thông tin sự kiện theo ID
            const response = await getEventById(eventId);
            
            if (response && response.data.errCode === 0) {
                this.setState({
                    eventData: response.data.data,
                    isLoading: false
                });
            } else {
                toast.error("Không thể tải thông tin sự kiện");
                this.setState({ isLoading: false });
            }
        } catch (error) {
            console.error("Lỗi khi lấy thông tin sự kiện:", error);
            toast.error("Đã xảy ra lỗi khi tải thông tin sự kiện");
            this.setState({ isLoading: false });
        }
        
    }
    render(){
        const { eventId, eventData, isLoading } = this.state;
        return (
            <>
            <Header
                parentMenu="event"
                headerNormalLogo={Logo}
                headerStickyLogo={Logo}
            />
            
            <div className="react-wrapper">
                <div className="react-wrapper-inner">
                    <Breadcrumb
                        pageTitle="Đăng Ký Tham Gia Sự Kiện"
                    />

                    <div className="container">
                        {isLoading ? (
                            <div className="text-center py-5">
                                <div className="spinner-border text-primary" role="status">
                                    <span className="visually-hidden">Đang tải...</span>
                                </div>
                                <p className="mt-3">Đang tải thông tin sự kiện...</p>
                            </div>
                        ) : eventData ? (
                            <>
                                <div className="event-summary mb-5">
                                    <h3>Đăng ký tham gia: {eventData.name}</h3>
                                    <div className="event-info">
                                        <div className="row">
                                            <div className="col-md-6">
                                                <p><strong>Thời gian:</strong> {new Date(eventData.date).toLocaleDateString('vi-VN')} ({eventData.startTime || '08:00'} - {eventData.endTime || '17:00'})</p>
                                                <p><strong>Địa điểm:</strong> {eventData.address}</p>
                                            </div>
                                            <div className="col-md-6">
                                                <p><strong>Đơn vị tổ chức:</strong> {eventData.host || 'CTES'}</p>
                                                <p><strong>Chi phí:</strong> {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(eventData.cost || 0)}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                
                                {/* Truyền eventId và eventData cho form đăng ký */}
                                <ContactForm 
                                    eventId={eventId} 
                                    eventData={eventData}
                                />
                            </>
                        ) : (
                            <div className="alert alert-warning text-center">
                                <p>Không tìm thấy thông tin sự kiện. Vui lòng thử lại sau hoặc liên hệ quản trị viên.</p>
                                <button 
                                    className="btn btn-primary mt-3"
                                    onClick={() => this.props.history.push('/event')}
                                >
                                    Quay lại trang sự kiện
                                </button>
                            </div>
                        )}
                    </div>

                    <ScrollToTop />
                </div>
            </div>
            
            <Footer />
        </>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};
export default connect(mapStateToProps, mapDispatchToProps)(EventRegistration);
