import React, { Component } from "react";
import { Link } from "react-router-dom";
import { getUserById } from "../../services/userServices";
import { toast } from "react-toastify";
import CountUp from "react-countup";
import VisibilitySensor from "react-visibility-sensor";

class UserProfileMain extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userInfo: null,
            userId: null,
            loading: true,
            error: null,
            viewedCounter: false,
        };
    }

    componentDidMount() {
        // Lấy userId từ URL params
        const { match } = this.props;
        console.log("Match object:", match); // Debug để xem cấu trúc của match
        
        if (match && match.params && match.params.id) {
            const userId = match.params.id;
            console.log("User ID from params:", userId);
            
            // Lưu userId vào state và sau đó gọi fetchUserData
            this.setState({ userId }, () => {
                this.fetchUserData();
            });
        } else {
            console.error("Không tìm thấy ID trong URL params");
            this.setState({
                error: "ID người dùng không hợp lệ",
                loading: false,
            });
        }
    }

    // Lấy thông tin user từ API
    fetchUserData = async () => {
        try {
            const { userId } = this.state;
            console.log("Fetching data for user ID:", userId);

            if (!userId) {
                this.setState({
                    error: "ID người dùng không hợp lệ",
                    loading: false,
                });
                return;
            }

            // Gọi API lấy thông tin user
            const response = await getUserById(userId);
            console.log("API response:", response);

            if (response && response.data && response.data.errCode === 0) {
                this.setState({
                    userInfo: response.data.data,
                    loading: false,
                });
            } else {
                this.setState({
                    error: "Không thể tải thông tin người dùng",
                    loading: false,
                });
                toast.error("Không tìm thấy thông tin người dùng");
            }
        } catch (error) {
            console.error("Lỗi khi lấy thông tin người dùng:", error);
            this.setState({
                error: "Lỗi kết nối đến server",
                loading: false,
            });
            toast.error("Có lỗi xảy ra khi tải thông tin");
        }
    };

    // Phần còn lại của component giữ nguyên
    renderLoading = () => {
        return (
            <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Đang tải...</span>
                </div>
                <p className="mt-2">Đang tải thông tin chi tiết...</p>
            </div>
        );
    };

    renderError = () => {
        return (
            <div className="alert alert-danger" role="alert">
                <i className="fas fa-exclamation-triangle me-2"></i>
                {this.state.error || "Không tìm thấy thông tin người dùng"}
            </div>
        );
    };

    render() {
        const { loading, error, userInfo, viewedCounter, userId } = this.state;
        
        // Debug
        console.log("Current state:", {
            loading,
            error,
            userId,
            hasUserInfo: !!userInfo
        });

        if (loading) {
            return (
                <div className="profile-top back__course__area pt---120 pb---90">
                    <div className="container">{this.renderLoading()}</div>
                </div>
            );
        }

        if (error || !userInfo) {
            return (
                <div className="profile-top back__course__area pt---120 pb---90">
                    <div className="container">{this.renderError()}</div>
                </div>
            );
        }

        // Thống kê người dùng (có thể thay thế bằng dữ liệu thực từ API)
        const userStats = [
            {
                countNum: 85,
                countTitle: "Điểm tích lũy",
                countSubtext: "%",
            },
            {
                countNum: 24,
                countTitle: "Hoạt động đã tham gia",
                countSubtext: "",
            },
            {
                countNum: 3,
                countTitle: "Năm kinh nghiệm",
                countSubtext: "+",
            },
        ];

        // Lấy thông tin từ userInfo
        const { firstName, lastName, image, positionData, roleData, email, phoneNumber, userMarkdown, genderData } = userInfo;

        // Tạo full name
        const fullName = `${firstName || ""} ${lastName || ""}`.trim();

        // Lấy thông tin chi tiết và mô tả
        const description = userMarkdown?.description || "Chưa có thông tin mô tả";
        const userDetails = userMarkdown?.contentHTML || "<p>Chưa có thông tin chi tiết</p>";

        // Chức vụ hiển thị
        const position = positionData?.valueVi || roleData?.valueVi || "Thành viên";

        return (
            <>
                <div className="profile-top back__course__area pt---120 pb---90">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-4">
                                <div className="user-image-container text-center mb-4">
                                    {image ? (
                                        <img
                                            src={image}
                                            alt={fullName}
                                            className="img-fluid rounded"
                                            style={{ maxWidth: "100%", borderRadius: "8px" }}
                                            onError={(e) => {
                                                e.target.onerror = null;
                                                e.target.src = require("../../assets/images/instructors/1.jpg");
                                            }}
                                        />
                                    ) : (
                                        <img
                                            src={require("../../assets/images/instructors/1.jpg")}
                                            alt="Default avatar"
                                            className="img-fluid rounded"
                                            style={{ maxWidth: "100%", borderRadius: "8px" }}
                                        />
                                    )}
                                </div>
                                <div className="text-center mb-4">
                                    <Link to="#" className="follows">
                                        Theo dõi{" "}
                                        <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            width="24"
                                            height="24"
                                            viewBox="0 0 24 24"
                                            fill="none"
                                            stroke="currentColor"
                                            strokeWidth="2"
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            className="feather feather-plus"
                                        >
                                            <line x1="12" y1="5" x2="12" y2="19"></line>
                                            <line x1="5" y1="12" x2="19" y2="12"></line>
                                        </svg>
                                    </Link>
                                </div>

                                {/* Thống kê người dùng với CountUp */}
                                <div className="count__area2 mt-4">
                                    <ul className="row">
                                        {userStats.map((stat, index) => (
                                            <li key={index} className="col-12 mb-3">
                                                <div className="count__content">
                                                    <div className="text">
                                                        <CountUp start={0} end={stat.countNum} duration={2.5} suffix={stat.countSubtext} redraw={false}>
                                                            {({ countUpRef }) => (
                                                                <VisibilitySensor
                                                                    onChange={(isVisible) => {
                                                                        if (isVisible && !viewedCounter) {
                                                                            this.setState({ viewedCounter: true });
                                                                            return true;
                                                                        }
                                                                        return false;
                                                                    }}
                                                                    active={!viewedCounter}
                                                                    delayedCall
                                                                >
                                                                    <span className="count__content-title counter" ref={countUpRef}></span>
                                                                </VisibilitySensor>
                                                            )}
                                                        </CountUp>
                                                        <p>{stat.countTitle}</p>
                                                    </div>
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                            <div className="col-lg-8">
                                <ul className="user-section">
                                    <li className="user">
                                        <span className="name">Họ Và Tên:</span>
                                        <em>{fullName}</em>
                                    </li>
                                    <li>
                                        <span className="name">Chức Vụ:</span>
                                        <em>{position}</em>
                                    </li>
                                    {genderData && (
                                        <li>
                                            <span className="name">Giới tính:</span>
                                            <em>{genderData.valueVi || 'Chưa cập nhật'}</em>
                                        </li>
                                    )}
                                    <li>
                                        <span className="name">Điện thoại:</span>
                                        <em>{phoneNumber || "Chưa cập nhật"}</em>
                                    </li>
                                    <li>
                                        <span className="name">Email:</span>
                                        <em>{email || "Chưa cập nhật"}</em>
                                    </li>
                                    <li className="social">
                                        <span className="name">Liên hệ:</span>
                                        <em>
                                            <Link to="#">
                                                <span aria-hidden="true" className="social_facebook"></span>
                                            </Link>
                                            <Link to="#">
                                                <span aria-hidden="true" className="social_twitter"></span>
                                            </Link>
                                            <Link to="#">
                                                <span aria-hidden="true" className="social_linkedin"></span>
                                            </Link>
                                        </em>
                                    </li>
                                </ul>
                                <h3 className="mt-4">Giới thiệu</h3>
                                <p>{description}</p>
                                
                                <h3 className="mt-4">Thông tin chi tiết</h3>
                                <div dangerouslySetInnerHTML={{ __html: userDetails }}></div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default UserProfileMain;
