import React, { Component } from "react";
import { connect } from "react-redux";
import { languages } from "../../../utils/constant";
import './DetailDoctor.scss';
import { getDoctorDetailService } from "../../../services/userServices";
class DetailDoctor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            detailDoctor: {},
            loading: true
        };
    }

    async componentDidMount() {
        console.log()
        if (this.props.match && this.props.match.params && this.props.match.params.id) {
            try {
                const res = await getDoctorDetailService(this.props.match.params.id);
                if (res && res.data.errCode === 0) {
                    this.setState({
                        detailDoctor: res.data,
                        loading: false
                    });
                }
            } catch (e) {
                console.error('Error fetching doctor details:', e);
            }
        }
    }
    render() {

        const { detailDoctor, loading } = this.state;
        const { language } = this.props;
        console.log("check loading", loading)
        if (loading) {
            return <div className="loading">Loading...</div>;
        }

        return (
            <div className="doctor-detail-container">
                <div className="intro-doctor">
                    <div className="content-left">
                        <img 
                            src={detailDoctor.image ? detailDoctor.image : '/default-doctor.png'} 
                            alt="Doctor" 
                        />
                    </div>
                    <div className="content-right">
                        <div className="up">
                            {language === languages.VI ? 
                                `${detailDoctor.lastName} ${detailDoctor.firstName}` :
                                `${detailDoctor.firstName} ${detailDoctor.lastName}`
                            }
                        </div>
                        <div className="down">
                            {detailDoctor.doctorMarkdown && 
                                <span>{detailDoctor.doctorMarkdown.description}</span>
                            }
                        </div>
                    </div>
                </div>

                <div className="schedule-doctor">
                    {/* Add schedule component here later */}
                </div>

                <div className="detail-info-doctor">
                    {detailDoctor.doctorMarkdown && 
                        <div dangerouslySetInnerHTML={{ 
                            __html: detailDoctor.doctorMarkdown.contentHTML 
                        }}>
                        </div>
                    }
                </div>
            </div>
        );
    }
    
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(DetailDoctor);
