import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { Form, Button, Row, Col, Card } from 'react-bootstrap';

const EventRegistrationForm = (props) => {
    const { eventId } = props;
    const [validated, setValidated] = useState(false);
    
    const payMethods = [
        { code: 'BANK', name: 'Chuyển khoản ngân hàng' },
        { code: 'CASH', name: 'Tiền mặt' },
        { code: 'MOMO', name: 'Ví Momo' }
    ];

    const statusCosts = [
        { code: 'PAID', name: 'Đã thanh toán' },
        { code: 'UNPAID', name: 'Chưa thanh toán' }
    ];

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        event.preventDefault();
        
        if (form.checkValidity() === false) {
            event.stopPropagation();
            setValidated(true);
            return;
        }
        
        setValidated(true);
        
        const formData = new FormData(form);
        
        // Gửi API request
        fetch('/api/event-registrations', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                eventId: eventId,
                name: formData.get('name'),
                email: formData.get('email'),
                phone: formData.get('phone'),
                userId: formData.get('userId') || getCurrentUserId(),
                statusCostCode: formData.get('statusCostCode'),
                payMethodCode: formData.get('payMethodCode'),
                notes: formData.get('notes')
            }),
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                toast.success('Đăng ký sự kiện thành công!');
                form.reset();
                setValidated(false);
            } else {
                toast.error(data.message || 'Có lỗi xảy ra khi đăng ký');
            }
        })
        .catch((error) => {
            console.error('Error:', error);
            toast.error('Có lỗi xảy ra khi đăng ký');
        });
    };
    
    function getCurrentUserId() {
        const userInfo = JSON.parse(localStorage.getItem('userInfo'));
        return userInfo?.id || 0;
    }

    return (
        <Card className="shadow-sm border-0 mb-4">
            <Card.Body className="p-4 p-lg-5">
                <h2 className="text-center mb-4">Đăng ký tham gia sự kiện</h2>
                
                <Form noValidate validated={validated} onSubmit={handleSubmit} id="registration-form">
                    <Row className="mb-3">
                        <Col lg={12}>
                            <Form.Group controlId="name">
                                <Form.Label>Họ và tên</Form.Label>
                                <Form.Control
                                    required
                                    type="text"
                                    name="name"
                                    placeholder="Nhập họ tên của bạn"
                                />
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng nhập họ tên.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>
                    </Row>
                    
                    <Row className="mb-3">
                        <Col md={6}>
                            <Form.Group controlId="email" className="mb-3 mb-md-0">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    required
                                    type="email"
                                    name="email"
                                    placeholder="Email liên hệ"
                                />
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng nhập email hợp lệ.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>
                        
                        <Col md={6}>
                            <Form.Group controlId="phone">
                                <Form.Label>Số điện thoại</Form.Label>
                                <Form.Control
                                    required
                                    type="tel"
                                    name="phone"
                                    placeholder="Số điện thoại"
                                />
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng nhập số điện thoại.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>
                    </Row>
                    
                    <Row className="mb-3">
                        <Col lg={12}>
                            <Form.Group controlId="payMethodCode">
                                <Form.Label>Phương thức thanh toán</Form.Label>
                                <Form.Select 
                                    name="payMethodCode"
                                    required
                                >
                                    <option value="">Chọn phương thức thanh toán</option>
                                    {payMethods.map(method => (
                                        <option key={method.code} value={method.code}>
                                            {method.name}
                                        </option>
                                    ))}
                                </Form.Select>
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng chọn phương thức thanh toán.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>
                    </Row>
                    
                    <Row className="mb-3">
                        <Col lg={12}>
                            <Form.Group controlId="statusCostCode">
                                <Form.Label>Trạng thái thanh toán</Form.Label>
                                <Form.Select
                                    name="statusCostCode"
                                    required
                                >
                                    <option value="">Chọn trạng thái thanh toán</option>
                                    {statusCosts.map(status => (
                                        <option key={status.code} value={status.code}>
                                            {status.name}
                                        </option>
                                    ))}
                                </Form.Select>
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng chọn trạng thái thanh toán.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>
                    </Row>
                    
                    <Row className="mb-4">
                        <Col lg={12}>
                            <Form.Group controlId="notes">
                                <Form.Label>Ghi chú</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    name="notes"
                                    rows={4}
                                    placeholder="Thông tin bổ sung (nếu có)"
                                />
                            </Form.Group>
                        </Col>
                    </Row>
                    
                    <div className="d-grid">
                        <Button variant="primary" type="submit" size="lg">
                            Đăng ký tham gia
                        </Button>
                    </div>
                </Form>
            </Card.Body>
        </Card>
    );
}

export default EventRegistrationForm;
