import React from "react";
import { Link } from "react-router-dom";

const SingleEvent = (props) => {
    const {
        eventClass,
        eventID,
        eventDayCount,
        eventDate,
        eventStartTime,
        eventEndTime,
        eventTitle,
        eventBtnText,
        eventLocation,
    } = props;
    return (
        <div className="event-item mb-30">
        <div className="event-img">
            <img src={eventImg} alt={eventTitle} />
        </div>
        <div className="event-content">
            <h3 className="title">
                <Link to={`/event-detail/${eventID}`}>
                    {eventTitle}
                </Link>
            </h3>
            <div className="event-meta">
                <span className="date">{eventDate}</span>
                <span className="location">{eventLocation}</span>
            </div>
            <Link 
                to={`/event-detail/${eventID}`}
                className="read-more"
            >
                Xem chi tiết
            </Link>
        </div>
    </div>
    );
};

export default SingleEvent;
