import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router";
import "./EventRegistrationManage.scss";
import { getEventRegistrationsById, updateEventRegistration } from "../../services/eventServices";
import { toast } from "react-toastify";
import { Table, Badge, Button, Form, InputGroup, Spinner, Card, Container, Row, Col, Modal } from "react-bootstrap";
import { FaSearch, FaFilter, FaCheck, FaTimes, FaEye, FaEdit, FaArrowLeft, FaFileExport } from "react-icons/fa";
import moment from "moment";
import {Link, wthRouter} from "react-router-dom";
class EventRegistrationManage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: "",
            eventName: "",
            registrations: [],
            filteredRegistrations: [],
            isLoading: false,
            searchTerm: "",
            statusFilter: "all",
            showDetailModal: false,
            selectedRegistration: null,
        };
    }

    async componentDidMount() {
        // Lấy eventId từ URL (React Router v5: this.props.match.params, v6: this.props.params hoặc useParams)
        const eventId = this.props.match
        console.log("Event ID from URL:", eventId);
        if (!eventId) {
            toast.error("Không tìm thấy sự kiện!");
            return;
        }

        this.setState({ eventId, isLoading: true });
        await this.fetchRegistrations(eventId);
    }

    fetchRegistrations = async (eventId) => {
        try {
            const res = await getEventRegistrationsById(eventId);
            console.log("Event Registrations Response:", res);

            if (res && res.data && res.data.errCode === 0) {
                const registrations = res.data.data || [];
                const eventName = res.data.eventName || "Sự kiện";

                this.setState({
                    registrations,
                    filteredRegistrations: registrations,
                    eventName,
                    isLoading: false,
                });
            } else {
                toast.error(res.data.errMessage || "Lỗi khi tải danh sách đăng ký");
                this.setState({ isLoading: false });
            }
        } catch (e) {
            toast.error("Không thể kết nối máy chủ");
            this.setState({ isLoading: false });
        }
    };

    // Tìm kiếm và lọc
    handleSearch = (e) => {
        const searchTerm = e.target.value.toLowerCase();
        this.setState({ searchTerm }, this.filterRegistrations);
    };

    handleStatusFilter = (e) => {
        this.setState({ statusFilter: e.target.value }, this.filterRegistrations);
    };

    filterRegistrations = () => {
        const { registrations, searchTerm, statusFilter } = this.state;

        let filtered = [...registrations];

        // Áp dụng bộ lọc tìm kiếm
        if (searchTerm) {
            filtered = filtered.filter(
                (item) =>
                    (item.user?.firstName + " " + item.user?.lastName).toLowerCase().includes(searchTerm) ||
                    item.user?.email.toLowerCase().includes(searchTerm) ||
                    (item.user?.phoneNumber && item.user?.phoneNumber.includes(searchTerm))
            );
        }

        // Áp dụng bộ lọc trạng thái
        if (statusFilter !== "all") {
            filtered = filtered.filter((item) => item.status === statusFilter);
        }

        this.setState({ filteredRegistrations: filtered });
    };

    // Xử lý trạng thái đăng ký
    handleApprove = async (id) => {
        await this.updateStatus(id, "approved");
    };

    handleReject = async (id) => {
        await this.updateStatus(id, "rejected");
    };

    updateStatus = async (id, status) => {
        try {
            this.setState({ isLoading: true });
            const res = await updateEventRegistration(id, status);

            if (res && res.data && res.data.errCode === 0) {
                // Cập nhật trạng thái trong state
                const updatedRegistrations = this.state.registrations.map((item) => (item.id === id ? { ...item, status } : item));

                this.setState(
                    {
                        registrations: updatedRegistrations,
                        isLoading: false,
                    },
                    this.filterRegistrations
                );

                toast.success(`${status === "approved" ? "Duyệt" : "Từ chối"} đăng ký thành công!`);
            } else {
                toast.error(res.data.errMessage || "Lỗi khi cập nhật trạng thái");
                this.setState({ isLoading: false });
            }
        } catch (e) {
            toast.error("Không thể kết nối máy chủ");
            this.setState({ isLoading: false });
        }
    };

    // Xem chi tiết đăng ký
    handleViewDetail = (registration) => {
        this.setState({
            showDetailModal: true,
            selectedRegistration: registration,
        });
    };

    closeDetailModal = () => {
        this.setState({
            showDetailModal: false,
            selectedRegistration: null,
        });
    };

    // Quay lại trang danh sách sự kiện
    handleBackToEvents = () => {
        this.props.history.push("/system/event-display");
    };

    // Xuất dữ liệu ra file CSV
    exportToCSV = () => {
        const { filteredRegistrations } = this.state;

        if (filteredRegistrations.length === 0) {
            toast.info("Không có dữ liệu để xuất");
            return;
        }

        // Tạo dữ liệu CSV
        const headers = ["STT", "Họ tên", "Email", "Số điện thoại", "Ngày đăng ký", "Trạng thái"];

        const csvData = [
            headers.join(","),
            ...filteredRegistrations.map((item, idx) =>
                [
                    idx + 1,
                    `${item.user?.firstName || ""} ${item.user?.lastName || ""}`,
                    item.user?.email || "",
                    item.user?.phoneNumber || "",
                    item.createdAt ? new Date(item.createdAt).toLocaleString() : "",
                    item.status === "approved" ? "Đã duyệt" : item.status === "pending" ? "Chờ duyệt" : "Từ chối",
                ].join(",")
            ),
        ].join("\n");

        // Tạo file CSV và tải xuống
        const blob = new Blob([csvData], { type: "text/csv;charset=utf-8;" });
        const link = document.createElement("a");
        const url = URL.createObjectURL(blob);
        link.setAttribute("href", url);
        link.setAttribute("download", `danh-sach-dang-ky-su-kien-${new Date().getTime()}.csv`);
        link.style.visibility = "hidden";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    render() {
        const { eventName, filteredRegistrations, isLoading, searchTerm, statusFilter, showDetailModal, selectedRegistration } = this.state;

        return (
            <Container fluid className="event-registration-manage-container p-4">
                <Button variant="outline-secondary" className="mb-3" onClick={this.handleBackToEvents}>
                    <FaArrowLeft className="me-2" /> Quay lại danh sách sự kiện
                </Button>

                <Table className="shadow-sm mb-4">
                    <h4 className="mb-3 ">Quản lý đăng ký: {eventName}</h4>

                    <Row className="mb-3">
                        <Col md={4}>
                            <InputGroup>
                                <InputGroup.Text>
                                    <FaSearch />
                                </InputGroup.Text>
                                <Form.Control placeholder="Tìm kiếm theo tên, email hoặc SĐT" value={searchTerm} onChange={this.handleSearch} />
                            </InputGroup>
                        </Col>

                        <Col md={4}>
                            <InputGroup>
                                <InputGroup.Text>
                                    <FaFilter />
                                </InputGroup.Text>
                                <Form.Select value={statusFilter} onChange={this.handleStatusFilter}>
                                    <option value="all">Tất cả trạng thái</option>
                                    <option value="approved">Đã duyệt</option>
                                    <option value="pending">Chờ duyệt</option>
                                    <option value="rejected">Từ chối</option>
                                </Form.Select>
                            </InputGroup>
                        </Col>

                        <Col md={4} className="d-flex justify-content-end">
                            <Button variant="success" onClick={this.exportToCSV}>
                                <FaFileExport className="me-2" /> Xuất file CSV
                            </Button>
                        </Col>
                    </Row>

                    {isLoading ? (
                        <div className="text-center my-5">
                            <Spinner animation="border" variant="primary" />
                            <div className="mt-2">Đang tải dữ liệu...</div>
                        </div>
                    ) : (
                        <div className="table-responsive">
                            <Table hover className="align-middle">
                                <thead className="table-light">
                                    <tr>
                                        <th>#</th>
                                        <th>Họ tên</th>
                                        <th>Email</th>
                                        <th>Số điện thoại</th>
                                        <th>Ngày đăng ký</th>
                                        <th>Trạng thái</th>
                                        <th>Thao tác</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredRegistrations && filteredRegistrations.length > 0 ? (
                                        filteredRegistrations.map((item, idx) => (
                                            <tr key={item.id}>
                                                <td>{idx + 1}</td>
                                                <td>
                                                    {item.user?.firstName} {item.user?.lastName}
                                                </td>
                                                <td>{item.user?.email}</td>
                                                <td>{item.user?.phoneNumber || "-"}</td>
                                                <td>{item.createdAt ? moment(item.createdAt).format("DD/MM/YYYY HH:mm") : "-"}</td>
                                                <td>
                                                    {item.status === "approved" ? (
                                                        <Badge bg="success">Đã duyệt</Badge>
                                                    ) : item.status === "pending" ? (
                                                        <Badge bg="warning" text="dark">
                                                            Chờ duyệt
                                                        </Badge>
                                                    ) : (
                                                        <Badge bg="danger">Từ chối</Badge>
                                                    )}
                                                </td>
                                                <td>
                                                    <Button variant="outline-info" size="sm" className="me-1" onClick={() => this.handleViewDetail(item)} title="Xem chi tiết">
                                                        <FaEye />
                                                    </Button>

                                                    {item.status === "pending" && (
                                                        <>
                                                            <Button
                                                                variant="outline-success"
                                                                size="sm"
                                                                className="me-1"
                                                                onClick={() => this.handleApprove(item.id)}
                                                                title="Duyệt đăng ký"
                                                            >
                                                                <FaCheck />
                                                            </Button>
                                                            <Button variant="outline-danger" size="sm" onClick={() => this.handleReject(item.id)} title="Từ chối đăng ký">
                                                                <FaTimes />
                                                            </Button>
                                                        </>
                                                    )}
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="7" className="text-center py-3">
                                                {statusFilter !== "all" ? "Không tìm thấy đăng ký nào phù hợp với bộ lọc" : "Chưa có ai đăng ký sự kiện này"}
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </Table>
                        </div>
                    )}
                </Table>

                {/* Modal xem chi tiết đăng ký */}
                <Modal show={showDetailModal} onHide={this.closeDetailModal} centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Chi tiết đăng ký</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {selectedRegistration && (
                            <div>
                                <h5 className="mb-3">Thông tin người đăng ký</h5>
                                <Row className="mb-3">
                                    <Col md={4} className="fw-bold">
                                        Họ và tên:
                                    </Col>
                                    <Col>
                                        {selectedRegistration.user?.firstName} {selectedRegistration.user?.lastName}
                                    </Col>
                                </Row>
                                <Row className="mb-3">
                                    <Col md={4} className="fw-bold">
                                        Email:
                                    </Col>
                                    <Col>{selectedRegistration.user?.email}</Col>
                                </Row>
                                <Row className="mb-3">
                                    <Col md={4} className="fw-bold">
                                        Số điện thoại:
                                    </Col>
                                    <Col>{selectedRegistration.user?.phoneNumber || "Không có"}</Col>
                                </Row>
                                {selectedRegistration.user?.address && (
                                    <Row className="mb-3">
                                        <Col md={4} className="fw-bold">
                                            Địa chỉ:
                                        </Col>
                                        <Col>{selectedRegistration.user?.address}</Col>
                                    </Row>
                                )}

                                <hr className="my-4" />

                                <h5 className="mb-3">Thông tin đăng ký</h5>
                                <Row className="mb-3">
                                    <Col md={4} className="fw-bold">
                                        Thời gian đăng ký:
                                    </Col>
                                    <Col>{selectedRegistration.createdAt ? moment(selectedRegistration.createdAt).format("DD/MM/YYYY HH:mm:ss") : "-"}</Col>
                                </Row>
                                <Row className="mb-3">
                                    <Col md={4} className="fw-bold">
                                        Trạng thái:
                                    </Col>
                                    <Col>
                                        {selectedRegistration.status === "approved" ? (
                                            <Badge bg="success">Đã duyệt</Badge>
                                        ) : selectedRegistration.status === "pending" ? (
                                            <Badge bg="warning" text="dark">
                                                Chờ duyệt
                                            </Badge>
                                        ) : (
                                            <Badge bg="danger">Từ chối</Badge>
                                        )}
                                    </Col>
                                </Row>
                                {selectedRegistration.comment && (
                                    <Row className="mb-3">
                                        <Col md={4} className="fw-bold">
                                            Ghi chú:
                                        </Col>
                                        <Col>{selectedRegistration.comment}</Col>
                                    </Row>
                                )}

                                {selectedRegistration.status === "pending" && (
                                    <div className="d-flex justify-content-end gap-2 mt-4">
                                        <Button
                                            variant="success"
                                            onClick={() => {
                                                this.handleApprove(selectedRegistration.id);
                                                this.closeDetailModal();
                                            }}
                                        >
                                            <FaCheck className="me-2" /> Duyệt đăng ký
                                        </Button>
                                        <Button
                                            variant="danger"
                                            onClick={() => {
                                                this.handleReject(selectedRegistration.id);
                                                this.closeDetailModal();
                                            }}
                                        >
                                            <FaTimes className="me-2" /> Từ chối đăng ký
                                        </Button>
                                    </div>
                                )}
                            </div>
                        )}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.closeDetailModal}>
                            Đóng
                        </Button>
                    </Modal.Footer>
                </Modal>
            </Container>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(EventRegistrationManage));
