import React, { Component } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";
import { connect } from "react-redux";
import "./UserManage.scss";
import { getAllUsers, deleteUser, updateUser } from "../../services/userServices";

class UserManage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrUsers: [],
            userIdToEdit: null,
            userEditData: {
                firstName: '',
                lastName: '',
                email: '',
                address: ''
            }
        };
    }

    async componentDidMount() {
        await this.fetchAllUsers();
    }

    fetchAllUsers = async () => {
        let response = await getAllUsers("ALL");
        if (response && response.data && response.data.errCode === 0) {
            this.setState({
                arrUsers: response.data.users,
            });
        }
    }

    handleDeleteUser = async (userId) => {
        let response = await deleteUser(userId);
        if (response && response.data.errCode === 0) {
            await this.fetchAllUsers();
        } else {
            alert(response.data.errMessage);
        }
    };

    handleEditUser = (user) => {
        this.setState({
            userIdToEdit: user.id,
            userEditData: {
                firstName: user.firstName,
                lastName: user.lastName,
                email: user.email,
                address: user.address
            }
        });
    };

    handleChangeUserEditData = (event) => {
        // Lấy giá trị từ trường nhập liệu
        let inputName = event.target.name;
        let inputValue = event.target.value;
        // Tạo 1 bản sao cho obj userEditData
        let updateUserEditData = { ...this.state.userEditData };
        // Cập nhập lại giá trị nhập vào so với dữ liệu ban đầu
        updateUserEditData[inputName] = inputValue;
        // Cập nhật lại state
        this.setState({
            userEditData: updateUserEditData
            });
    };

    handleSaveUserEdit = async () => {
        // lấy dữ liệu từ state
        let { userIdToEdit, userEditData } = this.state;
        // Tạo đối tượng dữ liệu ID và thông tin người dùng
        let data = {
            id: userIdToEdit,
            ...userEditData
        };
        // Gửi yêu cầu thông tin người dùng lên server
        let response = await updateUser(data);
        // Kiểm tra phản hồi từ sever
        if (response && response.data.errCode === 0) {
            await this.fetchAllUsers();
            // reset lại state
            this.setState({
                userIdToEdit: null,
                userEditData: {
                    firstName: '',
                    lastName: '',
                    email: '',
                    address: ''
                }
            });
        } else {
            alert(response.data.errMessage);
        }
    };

    render() {
        let { arrUsers, userEditData } = this.state;
        return (
            <div className="text-center">Manage users</div>
        );
    }

}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};



export default connect(mapStateToProps, mapDispatchToProps)(UserManage);
