import React, { Component } from "react";
import "./EventRegistrationDetails.scss";
import {getEventRegistrationsById} from "../../services/eventServices"
// import EventRegistrationSummary from "./components/EventRegistrationSummary";
// import EventRegistrationTable from "./components/EventRegistrationTable";
class EventRegistrationDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventData: null,
            registrations: [],
            isLoading: true,
            error: null,
            registrationCount: 0,
            eventId: null,
        };
    }
    async componentDidMount() {
                // Lấy eventId từ URL params
                const { match } = this.props;
                const eventId = match.params.id;
        
                if (eventId) {
                    this.setState({ eventId });
                    await this.fetchEventData(eventId);
                }
        this.fetchEventRegistrations();
    }


    fetchEventRegistrations = async () => {
        try {
            const response = await getEventRegistrationsById(eventId)
            console.log("check", response)
            if (response.data.errCode === 0) {
                const { event, registrations, registrationCount } = response.data.data;
                this.setState({
                    eventData: event,
                    registrations: registrations,
                    registrationCount: registrationCount,
                    isLoading: false
                });
            } else {
                this.setState({
                    error: response.data.errMessage,
                    isLoading: false
                });
            }
        } catch (error) {
            this.setState({
                error: "Có lỗi xảy ra khi tải dữ liệu",
                isLoading: false
            });
            console.error("Error fetching event registrations:", error);
        }
    };

    render() {
        const { eventData, registrations, error, registrationCount } = this.state;
        return (
            <div className="event-registrations-container container pt-5 pb-5">
                <div className="back-link mb-4">
                    <a onClick={() => this.props.history.goBack()} className="btn btn-outline-secondary">
                        <i className="fas fa-arrow-left"></i> Quay lại
                    </a>
                </div>

                <h2 className="page-title mb-4">Danh sách người đăng ký sự kiện</h2>
                
                {/* {eventData && (
                    <EventRegistrationSummary 
                        event={eventData} 
                        registrationCount={registrationCount} 
                    />
                )}
                
                {registrations && registrations.length > 0 ? (
                    <EventRegistrationTable registrations={registrations} />
                ) : (
                    <div className="alert alert-info">
                        Chưa có người đăng ký tham gia sự kiện này.
                    </div>
                )} */}
            </div>
        );
    }
}

export default EventRegistrationDetails;
