import React, { Component } from "react";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// import { registerEvent } from "../../services/eventServices"; // Giả sử bạn có hàm này trong services

class EventRegistrationForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: props.eventId || "",
            name: "",
            email: "",
            phone: "",
            payMethodCode: "",
            statusCostCode: "",
            notes: "",
            registeredAt: new Date().toISOString().split('T')[0],
        };
    }

    handleOnChangeInput = (event, id) => {
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState({
            ...copyState,
        });
    };

    checkValidateInput = () => {
        let isValid = true;
        const requiredFields = {
            name: "Full Name",
            email: "Email",
            phone: "Phone Number",
            payMethodCode: "Payment Method",
            statusCostCode: "Payment Status"
        };

        for (let field in requiredFields) {
            if (!this.state[field]) {
                isValid = false;
                toast.error(`Missing required parameter: ${requiredFields[field]}`);
                break;
            }
        }

        return isValid;
    };

    handleRegisterEvent = async () => {
        let isValid = this.checkValidateInput();
        if (!isValid) return;

        try {
            // Lấy userId từ localStorage hoặc Redux store
            const userInfo = JSON.parse(localStorage.getItem('userInfo')) || {};
            const userId = userInfo.id || 0;

            // let response = await registerEvent({
            //     eventId: this.state.eventId,
            //     name: this.state.name,
            //     userId: userId,
            //     email: this.state.email,
            //     phone: this.state.phone,
            //     registeredAt: this.state.registeredAt,
            //     statusCostCode: this.state.statusCostCode,
            //     payMethodCode: this.state.payMethodCode,
            //     notes: this.state.notes
            // });

            if (response && response.data.errCode === 0) {
                toast.success("Event registration successful!");
                this.setState({
                    name: "",
                    email: "",
                    phone: "",
                    payMethodCode: "",
                    statusCostCode: "",
                    notes: "",
                    registeredAt: new Date().toISOString().split('T')[0]
                });
            } else {
                toast.error(response.data.errMessage || "Registration failed");
            }
        } catch (error) {
            toast.error("Something went wrong...");
            console.error("Error registering for event:", error);
        }
    };

    render() {
        return (
            <div className="event-registration-container">
                <div className="container-fluid">
                    <div className="title text-center mb-4">
                        <h2>Event Registration</h2>
                    </div>
                    <form className="row g-3">
                        <div className="col-md-6">
                            <label className="form-label">Full Name</label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.name}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "name")
                                }
                                placeholder="Enter your full name"
                                required
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                value={this.state.email}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "email")
                                }
                                placeholder="Enter your email"
                                required
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Phone Number</label>
                            <input
                                type="tel"
                                className="form-control"
                                value={this.state.phone}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "phone")
                                }
                                placeholder="Enter your phone number"
                                required
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Registration Date</label>
                            <input
                                type="date"
                                className="form-control"
                                value={this.state.registeredAt}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "registeredAt")
                                }
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Payment Method</label>
                            <select
                                className="form-select"
                                value={this.state.payMethodCode}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "payMethodCode")
                                }
                                required
                            >
                                <option value="">Choose payment method...</option>
                                <option value="BANK">Bank Transfer</option>
                                <option value="CASH">Cash</option>
                                <option value="MOMO">Momo Wallet</option>
                            </select>
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Payment Status</label>
                            <select
                                className="form-select"
                                value={this.state.statusCostCode}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "statusCostCode")
                                }
                                required
                            >
                                <option value="">Choose payment status...</option>
                                <option value="PAID">Paid</option>
                                <option value="UNPAID">Unpaid</option>
                            </select>
                        </div>

                        <div className="col-12">
                            <label className="form-label">Additional Notes</label>
                            <textarea
                                className="form-control"
                                rows="4"
                                value={this.state.notes}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "notes")
                                }
                                placeholder="Any special requirements or information"
                            ></textarea>
                        </div>

                        <div className="col-12 mt-4">
                            <button
                                type="button"
                                className="btn btn-primary"
                                onClick={() => this.handleRegisterEvent()}
                            >
                                Register Now
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        userInfo: state.user.userInfo,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventRegistrationForm);
