import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import * as actions from "../../store/actions";
import "./Login.scss";
import { FormattedMessage } from "react-intl";
import { languages } from "../../utils";
import { Form, Button, Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faFacebookF,
    faTwitter,
    faGoogle,
    faGithub,
} from "@fortawesome/free-brands-svg-icons";
class Login extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <Container p-3 my-5 d-flex flex-column w-50>
                <Form>
                    <Form.Group className="mb-4" controlId="form1">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group className="mb-4" controlId="form2">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                    </Form.Group>

                    <div className="d-flex justify-content-between mx-3 mb-4">
                        <Form.Check
                            type="checkbox"
                            id="flexCheckDefault"
                            label="Remember me"
                        />
                        <a href="#!">Forgot password?</a>
                    </div>

                    <Button variant="primary" className="mb-4 w-100">
                        Sign in
                    </Button>

                    <div className="text-center">
                        <p>
                            Not a member? <a href="#!">Register</a>
                        </p>
                        <p>or sign up with:</p>

                        <div
                            className="d-flex justify-content-between mx-auto"
                            style={{ width: "40%" }}
                        >
                            <Button
                                variant="outline-primary"
                                className="m-1"
                                style={{ color: "#1266f1" }}
                            >
                                <FontAwesomeIcon icon={faFacebookF} size="sm" />
                            </Button>

                            <Button
                                variant="outline-primary"
                                className="m-1"
                                style={{ color: "#1266f1" }}
                            >
                                <FontAwesomeIcon icon={faTwitter} size="sm" />
                            </Button>

                            <Button
                                variant="outline-primary"
                                className="m-1"
                                style={{ color: "#1266f1" }}
                            >
                                <FontAwesomeIcon icon={faGoogle} size="sm" />
                            </Button>

                            <Button
                                variant="outline-primary"
                                className="m-1"
                                style={{ color: "#1266f1" }}
                            >
                                <FontAwesomeIcon icon={faGithub} size="sm" />
                            </Button>
                        </div>
                    </div>
                </Form>
            </Container>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) =>
            dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
