import React, { Component } from "react";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./EventRegistrationList.scss"; // Tạo file SCSS riêng cho component

class EventRegistrationList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            expandedRow: null,
            searchTerm: "",
        };
    }

    // Toggle mở rộng hàng
    toggleRow = (id) => {
        this.setState((prevState) => ({
            expandedRow: prevState.expandedRow === id ? null : id,
        }));
    };

    // Format date
    formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString("vi-VN");
    };

    // Format cost to VND
    formatCost = (cost) => {
        if (!cost) return "0 VND";
        return new Intl.NumberFormat("vi-VN", {
            style: "currency",
            currency: "VND",
        }).format(parseFloat(cost));
    };

    // Lọc danh sách đăng ký
    getFilteredRegistrations = () => {
        const { eventRegistrationData } = this.props;
        const { filterStatus, filterPayment, searchTerm } = this.state;

        if (
            !eventRegistrationData ||
            !Array.isArray(eventRegistrationData) ||
            eventRegistrationData.length === 0
        ) {
            return [];
        }

        return eventRegistrationData.filter((reg) => {
            // Tìm kiếm theo tên, email hoặc số điện thoại
            if (searchTerm) {
                const searchLower = searchTerm.toLowerCase();
                return (
                    reg.name.toLowerCase().includes(searchLower) ||
                    reg.email.toLowerCase().includes(searchLower) ||
                    reg.phoneNumber.includes(searchLower)
                );
            }

            return true;
        });
    };

    // Xử lý thay đổi bộ lọc
    handleFilterChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };
    render() {
        const { eventData, eventRegistrationData } = this.props;
        const { expandedRow, searchTerm } = this.state;

        if (!eventData || !eventRegistrationData) {
            return (
                <div className="loading-container">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Đang tải...</span>
                    </div>
                </div>
            );
        }

        const filteredRegistrations = this.getFilteredRegistrations();

        return (
            <div className="event-registration-container">
                <div className="container-fuild">
                    {/* Bộ lọc danh sách đăng ký */}
                    <div className="registration-filter-container ">
                        <h3>
                            Danh sách người đăng ký (
                            {filteredRegistrations.length})
                        </h3>
                    </div>

                    {/* Bảng danh sách người đăng ký */}
                    <div className="registration-table-container">
                        <div className="card-header">
                            <div className="d-flex justify-content-between align-items-center">
                                <div className="card-body">
                                    <div className="row">
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Tìm kiếm:</label>
                                                <div className="search-input">
                                                    <i className="fas fa-search"></i>
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        placeholder="Tên, email, số điện thoại..."
                                                        name="searchTerm"
                                                        value={searchTerm}
                                                        onChange={
                                                            this
                                                                .handleFilterChange
                                                        }
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <button className="btn btn-success">
                                    <i className="fas fa-file-excel"></i> Xuất
                                    Excel
                                </button>
                            </div>
                        </div>
                        <div className="card-body p-0">
                            <div className="table-responsive">
                                {filteredRegistrations.length > 0 ? (
                                    <table className="table table-hover">
                                        <thead>
                                            <tr>
                                                <th scope="col">#</th>
                                                <th scope="col">
                                                    Người đăng ký
                                                </th>
                                                <th scope="col">Email</th>
                                                <th scope="col">
                                                    Số điện thoại
                                                </th>
                                                <th scope="col">
                                                    Ngày đăng ký
                                                </th>
                                                <th scope="col">Trạng thái</th>
                                                <th scope="col">Phương thức</th>
                                                <th scope="col">Chi tiết</th>
                                                <th
                                                    scope="col"
                                                    style={{ width: "150px" }}
                                                >
                                                    Thao tác
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {filteredRegistrations.map(
                                                (registration, index) => (
                                                    <React.Fragment
                                                        key={registration.id}
                                                    >
                                                        <tr>
                                                            <td>{index + 1}</td>
                                                            <td>
                                                                {
                                                                    registration.name
                                                                }
                                                            </td>
                                                            <td>
                                                                {
                                                                    registration.email
                                                                }
                                                            </td>
                                                            <td>
                                                                {
                                                                    registration.phoneNumber
                                                                }
                                                            </td>
                                                            <td>
                                                                {this.formatDate(
                                                                    registration.registeredAt
                                                                )}
                                                            </td>
                                                            <td>
                                                                <span
                                                                    className={`status-badge ${
                                                                        registration.statusCostCode ===
                                                                        "PS1"
                                                                            ? "paid"
                                                                            : "unpaid"
                                                                    }`}
                                                                >
                                                                    {
                                                                        registration
                                                                            .statusCost
                                                                            .valueVi
                                                                    }
                                                                </span>
                                                            </td>
                                                            <td>
                                                                {
                                                                    registration
                                                                        .payMethod
                                                                        .valueVi
                                                                }
                                                            </td>
                                                            <td>
                                                                <button
                                                                    className="btn btn-sm btn-outline-primary"
                                                                    onClick={() =>
                                                                        this.toggleRow(
                                                                            registration.id
                                                                        )
                                                                    }
                                                                >
                                                                    <i
                                                                        className={`fas fa-chevron-${
                                                                            expandedRow ===
                                                                            registration.id
                                                                                ? "up"
                                                                                : "down"
                                                                        }`}
                                                                    ></i>
                                                                </button>

                                                                <button
                                                                    className="btn btn-sm btn-primary me-1"
                                                                    title="Chỉnh sửa"
                                                                    onClick={() =>
                                                                        this.handleEditRegistration(
                                                                            registration
                                                                        )
                                                                    }
                                                                >
                                                                    <i className="fas fa-edit"></i>
                                                                </button>
                                                                <button
                                                                    className="btn btn-sm btn-danger"
                                                                    title="Xóa"
                                                                    onClick={() =>
                                                                        this.handleConfirmDelete(
                                                                            registration.id
                                                                        )
                                                                    }
                                                                >
                                                                    <i className="fas fa-trash"></i>
                                                                </button>
                                                            </td>
                                                        </tr>
                                                        {expandedRow ===
                                                            registration.id && (
                                                            <tr className="expanded-row">
                                                                <td colSpan="8">
                                                                    <div className="expanded-content">
                                                                        <div className="row">
                                                                            <div className="col-md-6">
                                                                                <div className="user-info">
                                                                                    <h5>
                                                                                        Thông
                                                                                        tin
                                                                                        người
                                                                                        dùng
                                                                                    </h5>
                                                                                    <ul>
                                                                                        <li>
                                                                                            <strong>
                                                                                                ID:
                                                                                            </strong>{" "}
                                                                                            {
                                                                                                registration.userId
                                                                                            }
                                                                                        </li>
                                                                                        <li>
                                                                                            <strong>
                                                                                                Họ:
                                                                                            </strong>{" "}
                                                                                            {registration
                                                                                                .User
                                                                                                ?.firstName ||
                                                                                                "-"}
                                                                                        </li>
                                                                                        <li>
                                                                                            <strong>
                                                                                                Tên:
                                                                                            </strong>{" "}
                                                                                            {registration
                                                                                                .User
                                                                                                ?.lastName ||
                                                                                                "-"}
                                                                                        </li>
                                                                                        <li>
                                                                                            <strong>
                                                                                                Địa
                                                                                                chỉ:
                                                                                            </strong>{" "}
                                                                                            {registration
                                                                                                .User
                                                                                                ?.address ||
                                                                                                "-"}
                                                                                        </li>
                                                                                    </ul>
                                                                                </div>
                                                                            </div>
                                                                            <div className="col-md-6">
                                                                                <div className="note-info">
                                                                                    <h5>
                                                                                        Ghi
                                                                                        chú
                                                                                    </h5>
                                                                                    <div className="note-content">
                                                                                        {registration.notes ? (
                                                                                            registration.notes
                                                                                        ) : (
                                                                                            <em>
                                                                                                Không
                                                                                                có
                                                                                                ghi
                                                                                                chú
                                                                                            </em>
                                                                                        )}
                                                                                    </div>
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </td>
                                                            </tr>
                                                        )}
                                                    </React.Fragment>
                                                )
                                            )}
                                        </tbody>
                                    </table>
                                ) : (
                                    <div className="no-results">
                                        <i className="fas fa-search"></i>
                                        <p>
                                            Không tìm thấy kết quả phù hợp với
                                            bộ lọc.
                                        </p>
                                        {eventRegistrationData.length === 0 && (
                                            <p>
                                                Chưa có người đăng ký tham gia
                                                sự kiện này.
                                            </p>
                                        )}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        userInforr: state.user.userInforr,
        isLoggedIn: state.user.isLoggedIn,
    };
};

export default connect(mapStateToProps)(EventRegistrationList);
