import React, { Component } from "react";
import MarkdownIt from "markdown-it";
import MdEditor from "react-markdown-editor-lite";
import "react-markdown-editor-lite/lib/index.css";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import { createMarkdown } from "../../services/userServices";

const mdParser = new MarkdownIt();

class MarkdownEditor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            contentMarkdown: "",
            contentHTML: "",
            description: "",
            doctorId: "",
            hasData: false,
        };
    }
    handleEditorChange = ({ html, text }) => {
        this.setState({
            contentMarkdown: text,
            contentHTML: html
        });
    };
    handleSaveContent = async () => {
        try {
            let response = await createMarkdown({
                contentHTML: this.state.contentHTML,
                contentMarkdown: this.state.contentMarkdown,
                description: this.state.description,
                doctorId: this.props.doctorId
            });

            if (response && response.errCode === 0) {
                toast.success('Save content succeed!');
            } else {
                toast.error(response.errMessage);
            }
        } catch (e) {
            console.log(e);
            toast.error('Error saving content');
        }
    };
    render() {
        return (
            <div className="markdown-editor-container">
                <div className="markdown-editor">
                    <MdEditor
                        style={{ height: "500px" }}
                        renderHTML={(text) => mdParser.render(text)}
                        onChange={this.handleEditorChange}
                        value={this.state.contentMarkdown}
                    />
                </div>

                <div className="description-input mt-3"> 
                    <label>Description:</label>
                    <textarea
                    className="from-control"
                    rows="3"
                    value={this.state.description}
                    onChange={(e) => this.setState({ description: e.target.value })}
                    />
                </div>

                <button
                    className="btn btn-primary mt-3"
                    onClick={this.handleSaveContent}
                >
                    Save Content
                </button>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        language: state.app.language,
      
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(MarkdownEditor);
