import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Breadcrumb from "../../components/Breadcrumb/EventBreadcrumbs";
import ScrollToTop from "../../components/ScrollTop";
import Logo from "../../assets/images/logos/logo2.png";
import { getEventById } from "../../services/eventServices";
import { toast } from "react-toastify";

class Recruitment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: null,
            eventData: null,
            isLoading: true,
        };
    }
    render() {
        return(
            <div className="event-registration-container">
                <Header 
                    parentMenu="event"
                    headerNormalLogo={Logo}
                    headerStickyLogo={Logo}/>
                <Breadcrumb
                    eventBannerImg="https://raw.githubusercontent.com/Trantoan12022004/host_image_page_anhtonton/main/images/tuyenthanhvien.jpg"
                    eventCategory="Sự kiện"
                    eventDate="2023-10-01"
                    eventStartTime="09:00"
                    eventEndTime="17:00"
                    eventTitle="TUYỂN THÀNH VIÊN K17"
                    eventLocation="D3-HUST"
                />
                <div className="event-recruitment-content">
                    <div className="container">
                        <div className="event-recruitment-content-inner">
                            <h2 className="event-recruitment-title">TUYỂN THÀNH VIÊN K17</h2>
                            <p className="event-recruitment-description">
                                Chào mừng các bạn đến với sự kiện tuyển thành viên K17 của chúng tôi! Đây là cơ hội tuyệt vời để bạn gia nhập vào một cộng đồng năng động và sáng tạo. Hãy cùng nhau khám phá những điều thú vị mà chúng tôi đã chuẩn bị cho bạn!
                            </p>
                            <Link to="/event-registration" className="btn btn-primary">Đăng ký ngay</Link>
                        </div>
                    </div>
                </div>
                <Footer />
                <ScrollToTop />
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        isLoggedIn: state.user.isLoggedIn,
        userInforr: state.user.userInforr,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(Recruitment);
