import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Breadcrumb from "../../components/Breadcrumb";
import ScrollToTop from "../../components/ScrollTop";
import Logo from "../../assets/images/logos/logo2.png";
// import ContactForm from "./ContactForm";
import { getEventById } from "../../services/eventServices";
import { toast } from "react-toastify";
import "./EventRegistrationDetails.scss";

class EventRegistrationDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: null,
            eventData: null,
            isLoading: true,
        };
    }

    async componentDidMount() {
        // Lấy eventId từ URL params
        const { match } = this.props;
        const eventId = match.params.id;
        console.log("check", )
        if (eventId) {
            this.setState({ eventId });
            await this.fetchEventData(eventId);
        }
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        isLoggedIn: state.user.isLoggedIn,
        userInforr: state.user.userInforr,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventRegistrationDetails);
