import "./EventRegistrationManage.scss";
import {
    getAllUsers,
    deleteUser,
    updateUser,
    createUser,
} from "../../services/userServices";
import ModalEditUser from "./ModalUser";
import { toast } from "react-toastify";
class EventRegistrationManage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            isOpenModalEdit: false,
            currentUser: null,
        };
    }

    async componentDidMount() {
    }

    render() {
        return (
            <div className="event-registration-manage-container">
                <div className="title">Quản lý đăng ký sự kiện</div>
                <div className="event-registration-manage-content">
                    {/* tạo 1 hàm thêm hai số */}
                    <button
                        className="btn btn-primary"
                        onClick={() => this.setState({ isOpenModalEdit: true })}
                    >
                        <i className="bi bi-plus"></i> Thêm người dùng
                    </button>
                    
                    {/* Nội dung quản lý đăng ký sự kiện sẽ được thêm vào đây */}
                    {/* Ví dụ: Danh sách người dùng đã đăng ký, chức năng chỉnh sửa, xóa, v.v. */}
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventRegistrationManage);
