import React, { Component } from "react";
import MarkdownIt from "markdown-it";
import MdEditor from "react-markdown-editor-lite";
import "react-markdown-editor-lite/lib/index.css";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import { createEventDescription } from "../../services/eventServices";
import { getAllCodes } from "../../services/userServices";
import { languages } from "../../utils/constant";
import * as actions from "../../../src/store/actions";
import Select from "react-select";

const mdParser = new MarkdownIt();

class MarkdownEditor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            contentMarkdown: "",
            contentHTML: "",
            description: "",
            selectedPosition: null,
            selectedEvent: null,
            image: "",
            positions: [],
            events: []
        };
    }

    async componentDidMount() {
        try {
            const events = await getAllCodes("event_type")
            this.setState({
                events: this.buildSelectOptions(events.data)
            });
            // console.log("check event", this.state.events)
        } catch (error) {
            console.error("Error fetching data:", error);
            toast.error("Error loading data");
        }
    }

    buildSelectOptions = (items) => {
        return items.map(item => ({
            value: item.keyName,
            label: item.valueVi
        }));
    };

    handleEditorChange = ({ html, text }) => {
        this.setState({
            contentMarkdown: text,
            contentHTML: html
        });
    };
    handleImageChange = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = () => {
            this.setState({ image: reader.result });
        };
    };

    handleSaveContent = async () => {
        const { contentHTML, contentMarkdown, description, 
                selectedPosition, selectedEvent, image } = this.state;

        if (!selectedEvent) {
            toast.error("Please select an event");
            return;
        }

        try {
            const response = await createMarkdown({
                contentHTML,
                contentMarkdown,
                description,
                positionId: selectedPosition?.value || null,
                eventId: selectedEvent.value,
                image
            });

            if (response && response.data.errCode === 0) {
                toast.success("Save description successfully!");
                this.setState({
                    contentMarkdown: "",
                    contentHTML: "",
                    description: "",
                    selectedPosition: null,
                    selectedEvent: null,
                    image: ""
                });
            } else {
                toast.error(response.data.errMessage);
            }
        } catch (error) {
            console.error("Error saving content:", error);
            toast.error("Error saving content");
        }
    };
    render() {
        const { isLoading, allDoctors, language } = this.props;
        console.log("check state", this.state);
        const { selectedDoctor } = this.state;
        return (
            <div className="markdown-editor-container">
                <div className="select-doctor mb-3 col-6">
                    <label className="form-label">Select Doctor:</label>
                    <Select
                        value={selectedDoctor}
                        onChange={this.handleDoctorSelect}
                        options={this.buildDoctorOptions(allDoctors)}
                        className="basic-select"
                        classNamePrefix="select"
                        placeholder={
                            language === languages.VI
                                ? "Chọn bác sĩ"
                                : "Select doctor"
                        }
                        isSearchable={true}
                        isLoading={isLoading}
                    />
                </div>
                <div class="description-input mt-3 col-6">
                    <label for="exampleFormControlTextarea1" class="form-label">
                        Description:
                    </label>
                    <textarea
                        class="form-control"
                        rows="3"
                        value={this.state.description}
                        onChange={(e) =>
                            this.setState({ description: e.target.value })
                        }
                    ></textarea>
                </div>
                <div className="markdown-editor">
                    <MdEditor
                        style={{ height: "500px" }}
                        renderHTML={(text) => mdParser.render(text)}
                        onChange={this.handleEditorChange}
                        value={this.state.contentMarkdown}
                    />
                </div>

                <button
                    className="btn btn-primary mt-3"
                    onClick={this.handleSaveContent}
                >
                    Save Content
                </button>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        isLoggedIn: state.user.isLoggedIn,
        allDoctors: state.admin.allDoctors,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        fetchAllDoctors: () => dispatch(actions.fetchAllDoctors()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(MarkdownEditor);
