import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { languages } from "../../utils/constant";
import "./UserRedux.scss";
import * as actions from "../../../src/store/actions";
import Lightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";
class UserRedux extends Component {
    constructor(props) {
        super(props);
        this.state = {
            genderArr: [],
            positionArr: [],
            roleArr: [],
            previewImgURL: "",
            isOpen: false,
            email: "",
            password: "",
            firstName: "",
            lastName: "",
            address: "",
            phoneNumber: "",
            gender: "",
            role: "",
            position: "",
            avatar: "",
        };
    }

    async componentDidMount() {
        this.props.getGenderStart();
        this.props.getPositionStart();
        this.props.getRoleStart();
    }
    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.genderRedux !== this.props.genderRedux) {
            this.setState({ genderArr: this.props.genderRedux });
        }
        if (prevProps.positionRedux !== this.props.positionRedux) {
            this.setState({ positionArr: this.props.positionRedux });
        }
        if (prevProps.roleRedux !== this.props.roleRedux) {
            this.setState({ roleArr: this.props.roleRedux });
        }
    }

    handleOnchangeImage(event) {
        let data = event.target.files;
        let file = data[0];
        if (file) {
            let objectUrl = URL.createObjectURL(file);
            this.setState({
                previewImgURL: objectUrl,
                avatar: file,
            });
        }
    }

    triggerFileInput = () => {
        document.getElementById("previewImg").click();
    };

    openPreviewImage = () => {
        if (!this.state.previewImgURL) return;
        this.setState({ isOpen: true });
    };

    handleOnSubmit = () => {};

    onChangeInput = (event, id) => {
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState
        // email: '',
        //     password: '',
        //     firstName: '',
        //     lastName: '',
        //     address: '',
        //     phoneNumber: '',
        //     gender: '',
        //     role: '',
        //     position: '',
        //     avatar: '',
    };
    render() {
        let genders = this.state.genderArr;
        let language = this.props.language;
        let isLoading = this.props.isLoading;
        let positions = this.state.positionArr;
        let roles = this.state.roleArr;

        let {
            email,
            password,
            firstName,
            lastName,
            address,
            phoneNumber,
            gender,
            role,
            position,
            avatar,
        } = this.state;
        return (
            <div className="manage-user">
                <div className="title">
                    <FormattedMessage id="manage-user.title" />
                </div>
                <div className="user-redux-body">
                    <div className="container">
                        <div className="text-center">
                            {isLoading === true ? "Loading infomation" : ""}
                        </div>

                        <div className="row">
                            <div className="mb-3">
                                <FormattedMessage id="manage-user.add-new-user" />
                            </div>
                            <form>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label
                                            htmlFor="email"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.email" />
                                        </label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="email"
                                            placeholder="Email"
                                            value={email}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "email"
                                                )
                                            }
                                        />
                                    </div>

                                    <div className="col-md-3">
                                        <label
                                            htmlFor="password"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.password" />
                                        </label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "password"
                                                )
                                            }
                                        />
                                    </div>

                                    <div className="col-md-3">
                                        <label
                                            htmlFor="firstName"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.firstname" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="firstName"
                                            placeholder="Firstname"
                                            value={firstName}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "firstName"
                                                )
                                            }
                                        />
                                    </div>
                                    <div className="col-md-3">
                                        <label
                                            htmlFor="lastName"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.lastname" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="lastName"
                                            placeholder="Lastname"
                                            value={lastName}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "lastName"
                                                )
                                            }
                                        />
                                    </div>
                                </div>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label
                                            htmlFor="phonenumber"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.phonenumber" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="phonenumber"
                                            placeholder="Phonenumber"
                                            value={phoneNumber}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "phoneNumber"
                                                )
                                            }
                                        />
                                    </div>
                                    <div className="col-md-9">
                                        <label
                                            htmlFor="address"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.address" />
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="address"
                                            placeholder="1234 Main St"
                                            value={address}
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "address"
                                                )
                                            }
                                        />
                                    </div>
                                </div>
                                <div className="row mb-3">
                                    <div className="col-md-3">
                                        <label
                                            htmlFor="gender"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.gender" />
                                        </label>
                                        <select
                                            id="inputState"
                                            className="form-control"
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "gender"
                                                )
                                            }
                                        >
                                            {genders &&
                                                genders.length > 0 &&
                                                genders.map((item, index) => {
                                                    return (
                                                        <option
                                                            key={index}
                                                            value={item.keyMap}
                                                        >
                                                            {language ===
                                                            languages.VI
                                                                ? item.valueVi
                                                                : item.valueEn}
                                                        </option>
                                                    );
                                                })}
                                        </select>
                                    </div>

                                    <div className="col-md-3">
                                        <label
                                            htmlFor="position"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.position" />
                                        </label>
                                        <select
                                            id="inputState"
                                            className="form-control"
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "position"
                                                )
                                            }
                                        >
                                            {positions &&
                                                positions.length > 0 &&
                                                positions.map((item, index) => {
                                                    return (
                                                        <option
                                                            key={index}
                                                            value={item.keyMap}
                                                        >
                                                            {language ===
                                                            languages.VI
                                                                ? item.valueVi
                                                                : item.valueEn}
                                                        </option>
                                                    );
                                                })}
                                        </select>
                                    </div>

                                    <div className="col-md-3">
                                        <label
                                            htmlFor="role"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.role" />
                                        </label>
                                        <select
                                            id="inputState"
                                            className="form-control"
                                            onChange={(event) =>
                                                this.onChangeInput(
                                                    event,
                                                    "role"
                                                )
                                            }
                                        >
                                            {roles &&
                                                roles.length > 0 &&
                                                roles.map((item, index) => {
                                                    return (
                                                        <option
                                                            key={index}
                                                            value={item.keyMap}
                                                        >
                                                            {language ===
                                                            languages.VI
                                                                ? item.valueVi
                                                                : item.valueEn}
                                                        </option>
                                                    );
                                                })}
                                        </select>
                                    </div>
                                    <div className="col-md-3">
                                        <label
                                            htmlFor="avatar"
                                            className="form-label"
                                        >
                                            <FormattedMessage id="manage-user.avatar" />
                                        </label>
                                        <div>
                                            <input
                                                id="previewImg"
                                                type="file"
                                                hidden
                                                onChange={(event) =>
                                                    this.handleOnchangeImage(
                                                        event
                                                    )
                                                }
                                            />
                                            <button
                                                type="button"
                                                className="btn btn-primary"
                                                onClick={this.triggerFileInput}
                                            >
                                                Tải ảnh
                                                <i className="fas fa-upload"></i>
                                            </button>
                                            <div
                                                onClick={() =>
                                                    this.openPreviewImage()
                                                }
                                                className="preview-image"
                                                style={{
                                                    backgroundImage: `url(${this.state.previewImgURL})`,
                                                }}
                                            ></div>
                                        </div>
                                    </div>
                                </div>

                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    onClick={() => this.handleOnSubmit()}
                                >
                                    <FormattedMessage id="manage-user.submit" />
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
                {this.state.isOpen === true && (
                    <Lightbox
                        mainSrc={this.state.previewImgURL}
                        onCloseRequest={() =>
                            this.setState({
                                isOpen: false,
                            })
                        }
                    />
                )}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        genderRedux: state.admin.genders,
        isLoading: state.admin.isLoading,
        positionRedux: state.admin.positions,
        roleRedux: state.admin.roles,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        getGenderStart: () => dispatch(actions.fetchGenderStart()),
        getPositionStart: () => dispatch(actions.fetchPositionStart()),
        getRoleStart: () => dispatch(actions.fetchRoleStart()),
        // processLogout: () => dispatch(actions.processLogout()),
        // changeLanguageAppRedux: (language) =>
        //     dispatch(actions.changeLanguageApp(language)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
