import React, { useState, useEffect } from 'react';
import emailjs from 'emailjs-com';
import { toast } from 'react-toastify';

const EventRegistrationForm = (props) => {
    const { eventId } = props; // Lấy eventId từ props
    const [payMethods, setPayMethods] = useState([
        { code: 'BANK', name: 'Chuyển khoản ngân hàng' },
        { code: 'CASH', name: 'Tiền mặt' },
        { code: 'MOMO', name: 'Ví Momo' }
    ]);

    const [statusCosts, setStatusCosts] = useState([
        { code: 'PAID', name: 'Đã thanh toán' },
        { code: 'UNPAID', name: 'Chưa thanh toán' }
    ]);

    function sendEmail(e) {
        e.preventDefault();
        
        // Thêm eventId vào form data
        const formData = new FormData(e.target);
        formData.append('eventId', eventId);
        
        // Gửi API request thay vì email
        fetch('/api/event-registrations', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                eventId: eventId,
                name: formData.get('name'),
                userId: formData.get('userId') || getCurrentUserId(), // Lấy userId từ user hiện tại
                statusCostCode: formData.get('statusCostCode'),
                payMethodCode: formData.get('payMethodCode')
            }),
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                toast.success('Đăng ký sự kiện thành công!');
                e.target.reset();
            } else {
                toast.error(data.message || 'Có lỗi xảy ra khi đăng ký');
            }
        })
        .catch((error) => {
            console.error('Error:', error);
            toast.error('Có lỗi xảy ra khi đăng ký');
        });
    }
    
    // Hàm lấy userId của người dùng hiện tại
    function getCurrentUserId() {
        // Giả sử thông tin user được lưu trong localStorage
        const userInfo = JSON.parse(localStorage.getItem('userInfo'));
        return userInfo?.id || 0;
    }

    return (
        <div className="react-blog-form">
            <h2 className="contact-title">Đăng ký tham gia sự kiện</h2>
            <div id="blog-form" className="blog-form">
                <div id="form-messages"></div>
                <form id="registration-form" onSubmit={sendEmail}>                                                    
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="back-input">
                                <label htmlFor="name">Họ và tên</label>
                                <input id="name" type="text" name="name" required placeholder="Nhập họ tên của bạn" />
                            </div>
                        </div>

                        <div className="col-lg-6">
                            <div className="back-input">
                                <label htmlFor="email">Email</label>
                                <input id="email" type="email" name="email" required placeholder="Email liên hệ" />                          
                            </div>
                        </div>
                        
                        <div className="col-lg-6">
                            <div className="back-input">
                                <label htmlFor="phone">Số điện thoại</label>
                                <input id="phone" type="text" name="phone" required placeholder="Số điện thoại" />                   
                            </div>
                        </div>

                        <div className="col-lg-12">
                            <div className="back-input">
                                <label htmlFor="payMethodCode">Phương thức thanh toán</label>
                                <select id="payMethodCode" name="payMethodCode" required className="form-select">
                                    <option value="">Chọn phương thức thanh toán</option>
                                    {payMethods.map(method => (
                                        <option key={method.code} value={method.code}>{method.name}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="col-lg-12">
                            <div className="back-input">
                                <label htmlFor="statusCostCode">Trạng thái thanh toán</label>
                                <select id="statusCostCode" name="statusCostCode" required className="form-select">
                                    <option value="">Chọn trạng thái thanh toán</option>
                                    {statusCosts.map(status => (
                                        <option key={status.code} value={status.code}>{status.name}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="col-lg-12">
                            <div className="back-textarea">
                                <label htmlFor="notes">Ghi chú</label>
                                <textarea id="notes" name="notes" placeholder="Thông tin bổ sung (nếu có)"></textarea>
                            </div>
                        </div>

                        <div className="col-lg-12">                                                
                            <button type="submit" className="back-btn">
                                Đăng ký 
                                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-arrow-right">
                                    <line x1="5" y1="12" x2="19" y2="12"></line>
                                    <polyline points="12 5 19 12 12 19"></polyline>
                                </svg>
                            </button>
                        </div>
                    </div>                                                    
                </form>
            </div>
        </div>
    );
}

export default EventRegistrationForm;
