import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { toast } from 'react-toastify';
import _ from 'lodash';
import { getEventById, updateEvent } from '../../services/eventServices';
import './ModalEditEvent.scss';

class ModalEditEvent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            name: '',
            eventTypeId: '',
            date: '',
            address: '',
            cost: '',
            quantityMember: '',
            statusId: '',
            description: '',
            eventTypes: [],
            statuses: [],
            isLoading: true
        };
    }

    async componentDidMount() {
        await this.fetchEventData();
        await this.fetchSelectOptions();
    }

    fetchEventData = async () => {
        const { eventId } = this.props;
        if (eventId) {
            try {
                const response = await getEventById(eventId);
                if (response && response.data && response.data.errCode === 0) {
                    const eventData = response.data.data;
                    // Cập nhật state với dữ liệu từ API
                    this.setState({
                        id: eventData.id,
                        name: eventData.name,
                        eventType: eventData.eventType.valueVi,
                        date: eventData.date ? this.formatDateForInput(eventData.date) : '',
                        address: eventData.address || '',
                        cost: eventData.cost || '',
                        quantityMember: eventData.quantityMember || '',
                        statusId: eventData.statusId || '',
                        description: eventData.description || '',
                        isLoading: false
                    });
                } else {
                    toast.error("Không thể lấy thông tin sự kiện!");
                }
            } catch (error) {
                console.error("Lỗi khi lấy thông tin sự kiện:", error);
                toast.error("Đã xảy ra lỗi khi kết nối với server!");
            }
        }
    };



    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    validateInput = () => {
        const requiredFields = ['name', 'eventTypeId', 'date', 'address', 'statusId'];
        for (let i = 0; i < requiredFields.length; i++) {
            const field = requiredFields[i];
            if (!this.state[field]) {
                toast.error(`Vui lòng điền ${field === 'name' ? 'tên sự kiện' : 
                                          field === 'eventTypeId' ? 'loại sự kiện' :
                                          field === 'date' ? 'ngày diễn ra' :
                                          field === 'address' ? 'địa điểm' :
                                          'trạng thái'}`);
                return false;
            }
        }
        return true;
    };

    handleSaveEvent = async () => {
        if (!this.validateInput()) return;
        
        try {
            const eventData = {
                id: this.state.id,
                name: this.state.name,
                eventTypeId: this.state.eventTypeId,
                date: this.state.date,
                address: this.state.address,
                cost: this.state.cost,
                quantityMember: this.state.quantityMember,
                statusId: this.state.statusId,
                description: this.state.description
            };

            const response = await updateEvent(eventData);
            if (response && response.data && response.data.errCode === 0) {
                toast.success("Cập nhật sự kiện thành công!");
                this.props.onSuccess();
            } else {
                toast.error(response.data.errMessage || "Có lỗi khi cập nhật sự kiện!");
            }
        } catch (error) {
            console.error("Lỗi khi cập nhật sự kiện:", error);
            toast.error("Đã xảy ra lỗi khi kết nối với server!");
        }
    };

    render() {
        const { isOpen, closeModal } = this.props;
        const { name, eventTypeId, date, address, cost, quantityMember, statusId, description, eventTypes, statuses, isLoading } = this.state;

        return (
            <Modal isOpen={isOpen} toggle={closeModal} className="modal-edit-event" size="lg">
                <ModalHeader toggle={closeModal}>Chỉnh sửa thông tin sự kiện</ModalHeader>
                <ModalBody>
                    {isLoading ? (
                        <div className="loading-spinner">Đang tải dữ liệu...</div>
                    ) : (
                        <div className="form-container">
                            <div className="row">
                                <div className="col-6 form-group mb-3">
                                    <label>Tên sự kiện</label>
                                    <input 
                                        type="text" 
                                        className="form-control" 
                                        name="name" 
                                        value={name} 
                                        onChange={this.handleInputChange}
                                    />
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Loại sự kiện</label>
                                    <select 
                                        className="form-control" 
                                        name="eventTypeId" 
                                        value={eventTypeId} 
                                        onChange={this.handleInputChange}
                                    >
                                        <option value="">Chọn loại sự kiện</option>
                                        {eventTypes && eventTypes.length > 0 && 
                                            eventTypes.map((item) => (
                                                <option key={item.id} value={item.keyMap}>{item.valueVi}</option>
                                            ))
                                        }
                                    </select>
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Ngày diễn ra</label>
                                    <input 
                                        type="date" 
                                        className="form-control" 
                                        name="date" 
                                        value={date} 
                                        onChange={this.handleInputChange}
                                    />
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Địa điểm</label>
                                    <input 
                                        type="text" 
                                        className="form-control" 
                                        name="address" 
                                        value={address} 
                                        onChange={this.handleInputChange}
                                    />
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Chi phí</label>
                                    <input 
                                        type="number" 
                                        className="form-control" 
                                        name="cost" 
                                        value={cost} 
                                        onChange={this.handleInputChange}
                                    />
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Số thành viên</label>
                                    <input 
                                        type="number" 
                                        className="form-control" 
                                        name="quantityMember" 
                                        value={quantityMember} 
                                        onChange={this.handleInputChange}
                                    />
                                </div>
                                <div className="col-6 form-group mb-3">
                                    <label>Trạng thái</label>
                                    <select 
                                        className="form-control" 
                                        name="statusId" 
                                        value={statusId} 
                                        onChange={this.handleInputChange}
                                    >
                                        <option value="">Chọn trạng thái</option>
                                        {statuses && statuses.length > 0 && 
                                            statuses.map((item) => (
                                                <option key={item.id} value={item.keyMap}>{item.valueVi}</option>
                                            ))
                                        }
                                    </select>
                                </div>
                                <div className="col-12 form-group mb-3">
                                    <label>Mô tả</label>
                                    <textarea 
                                        className="form-control" 
                                        name="description" 
                                        value={description} 
                                        onChange={this.handleInputChange}
                                        rows="4"
                                    />
                                </div>
                            </div>
                        </div>
                    )}
                </ModalBody>
                <ModalFooter>
                    <Button color="secondary" onClick={closeModal}>Hủy</Button>
                    <Button 
                        color="primary" 
                        onClick={this.handleSaveEvent} 
                        disabled={isLoading}
                    >
                        Lưu thay đổi
                    </Button>
                </ModalFooter>
            </Modal>
        );
    }
}

export default ModalEditEvent;
