import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import SingleCourse from '../../components/Course/SingleCourse';
import { getUsersByPosition, getAllUsers } from '../../services/userServices';
import { toast } from 'react-toastify';
im

class ManageListMain extends Component {
    constructor(props) {
        super(props);
        this.state = {
            allUsers: [],         // Lưu trữ tất cả người dùng để lọc
            managers: [],         // Người dùng hiển thị sau khi lọc
            loading: true,
            error: null,
            searchTerm: '',       // Từ khóa tìm kiếm
            filters: {           // Các bộ lọc
                position: 'ALL',
                role: 'ALL',
                gender: 'ALL'
            },
            sortBy: 'default',    // Sắp xếp
            currentPage: 1,       // Phân trang
            itemsPerPage: 9,      // Số item mỗi trang
        };
    }

    componentDidMount() {
        this.fetchAllUsers();
    }

    // Hàm fetch tất cả người dùng
    fetchAllUsers = async () => {
        try {
            this.setState({ loading: true });
            
            // Gọi API thông qua service
            const response = await getAllUsers();
            
            if (response && response.data && response.data.errCode === 0) {
                const users = response.data.data || [];
                this.setState({ 
                    allUsers: users,
                    managers: users,
                    loading: false
                });
            } else {
                this.setState({ 
                    error: response.data?.errMessage || 'Đã xảy ra lỗi khi tải dữ liệu',
                    loading: false 
                });
                toast.error("Không thể tải danh sách người dùng");
            }
        } catch (error) {
            console.error('Error fetching users:', error);
            this.setState({ 
                error: 'Không thể kết nối đến server',
                loading: false 
            });
            toast.error("Lỗi kết nối đến server");
        }
    };

    // Hàm fetch người dùng theo vị trí
    fetchUsersByPosition = async (positionCode) => {
        try {
            this.setState({ loading: true });
            
            const response = await getUsersByPosition(positionCode);
            
            if (response && response.data && response.data.errCode === 0) {
                this.setState({ 
                    managers: response.data.data || [],
                    loading: false
                });
            } else {
                this.setState({ 
                    error: response.data?.errMessage || 'Đã xảy ra lỗi khi tải dữ liệu',
                    loading: false 
                });
                toast.error("Không thể tải danh sách người dùng theo vị trí");
            }
        } catch (error) {
            console.error('Error fetching users by position:', error);
            this.setState({ 
                error: 'Không thể kết nối đến server',
                loading: false 
            });
            toast.error("Lỗi kết nối đến server");
        }
    };

    // Hàm áp dụng tất cả bộ lọc
    applyFilters = () => {
        const { allUsers, searchTerm, filters, sortBy } = this.state;
        
        // Bắt đầu với tất cả người dùng
        let filteredUsers = [...allUsers];
        
        // Áp dụng lọc theo vị trí
        if (filters.position !== 'ALL') {
            filteredUsers = filteredUsers.filter(user => user.positionCode === filters.position);
        }
        
        // Áp dụng lọc theo vai trò
        if (filters.role !== 'ALL') {
            filteredUsers = filteredUsers.filter(user => user.roleCode === filters.role);
        }
        
        // Áp dụng lọc theo giới tính
        if (filters.gender !== 'ALL') {
            filteredUsers = filteredUsers.filter(user => user.genderCode === filters.gender);
        }
        
        // Áp dụng tìm kiếm
        if (searchTerm.trim()) {
            const searchLower = searchTerm.toLowerCase().trim();
            filteredUsers = filteredUsers.filter(user => 
                (user.firstName && user.firstName.toLowerCase().includes(searchLower)) ||
                (user.lastName && user.lastName.toLowerCase().includes(searchLower)) ||
                (user.email && user.email.toLowerCase().includes(searchLower)) ||
                (user.phoneNumber && user.phoneNumber.includes(searchTerm))
            );
        }
        
        // Áp dụng sắp xếp
        switch(sortBy) {
            case 'name-asc':
                filteredUsers.sort((a, b) => {
                    const nameA = `${a.firstName || ''} ${a.lastName || ''}`.trim().toLowerCase();
                    const nameB = `${b.firstName || ''} ${b.lastName || ''}`.trim().toLowerCase();
                    return nameA.localeCompare(nameB);
                });
                break;
            case 'name-desc':
                filteredUsers.sort((a, b) => {
                    const nameA = `${a.firstName || ''} ${a.lastName || ''}`.trim().toLowerCase();
                    const nameB = `${b.firstName || ''} ${b.lastName || ''}`.trim().toLowerCase();
                    return nameB.localeCompare(nameA);
                });
                break;
            case 'date-asc':
                filteredUsers.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
                break;
            case 'date-desc':
                filteredUsers.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
                break;
            default:
                // Mặc định không sắp xếp, giữ nguyên thứ tự
                break;
        }
        
        this.setState({ managers: filteredUsers });
    };
    
    // Xử lý thay đổi tìm kiếm
    handleSearchChange = (e) => {
        this.setState({ searchTerm: e.target.value }, this.applyFilters);
    };

    // Xử lý thay đổi bộ lọc
    handleFilterChange = (e) => {
        const { name, value } = e.target;
        this.setState(prevState => ({
            filters: {
                ...prevState.filters,
                [name]: value
            }
        }), this.applyFilters);
    };

    // Xử lý thay đổi sắp xếp
    handleSortChange = (e) => {
        this.setState({ sortBy: e.target.value }, this.applyFilters);
    };

    // Chuyển đến trang khác
    handlePageChange = (page) => {
        this.setState({ currentPage: page });
    };

    // Render trạng thái loading
    renderLoading = () => {
        return (
            <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Đang tải...</span>
                </div>
                <p className="mt-2">Đang tải dữ liệu...</p>
            </div>
        );
    };

    // Render trạng thái lỗi
    renderError = () => {
        const { error } = this.state;
        
        return (
            <div className="alert alert-danger" role="alert">
                <i className="fas fa-exclamation-triangle me-2"></i>
                {error || 'Đã xảy ra lỗi khi tải dữ liệu'}
            </div>
        );
    };

    // Render danh sách người dùng
    renderManagers = () => {
        const { managers, currentPage, itemsPerPage } = this.state;
        
        if (managers.length === 0) {
            return (
                <div className="col-12 text-center py-4">
                    <p>Không tìm thấy thành viên phù hợp với tiêu chí tìm kiếm</p>
                </div>
            );
        }

        // Tính toán phân trang
        const indexOfLastItem = currentPage * itemsPerPage;
        const indexOfFirstItem = indexOfLastItem - itemsPerPage;
        const currentItems = managers.slice(indexOfFirstItem, indexOfLastItem);

        return currentItems.map((manager) => (
            <div 
                key={manager.id} 
                className="col-xxl-4 col-xl-4 col-lg-4 col-md-6 col-sm-12 col-12 wow animate__fadeInUp" 
                data-wow-duration="0.3s"
                style={{marginBottom: '30px'}}
            >
                <SingleCourse
                    personID={manager.id}
                    avatar={manager.image || manager.userMarkdown?.image}
                    firstName={manager.firstName}
                    lastName={manager.lastName}
                    role={manager.positionData?.valueVi || manager.roleData?.valueVi || 'Thành viên'}
                    description={manager.userMarkdown?.description || 'Chưa có thông tin mô tả'}
                    contactInfo={{
                        email: manager.email,
                        phone: manager.phoneNumber
                    }}
                />
            </div>
        ));
    };

    // Render phân trang
    renderPagination = () => {
        const { managers, currentPage, itemsPerPage } = this.state;
        const totalPages = Math.ceil(managers.length / itemsPerPage);
        
        if (totalPages <= 1) return null;
        
        const pageNumbers = [];
        
        // Giới hạn số trang hiển thị
        const maxPagesToShow = 5;
        let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
        let endPage = startPage + maxPagesToShow - 1;
        
        if (endPage > totalPages) {
            endPage = totalPages;
            startPage = Math.max(1, endPage - maxPagesToShow + 1);
        }
        
        for (let i = startPage; i <= endPage; i++) {
            pageNumbers.push(i);
        }
        
        return (
            <ul className="back-pagination pt---20">
                {currentPage > 1 && (
                    <li>
                        <Link to="#" onClick={() => this.handlePageChange(currentPage - 1)}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-arrow-left">
                                <line x1="19" y1="12" x2="5" y2="12"></line>
                                <polyline points="12 19 5 12 12 5"></polyline>
                            </svg>
                        </Link>
                    </li>
                )}
                
                {pageNumbers.map(number => (
                    <li key={number} className={currentPage === number ? 'active' : ''}>
                        <Link to="#" onClick={() => this.handlePageChange(number)}>
                            {number}
                        </Link>
                    </li>
                ))}
                
                {currentPage < totalPages && (
                    <li className="back-next">
                        <Link to="#" onClick={() => this.handlePageChange(currentPage + 1)}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-arrow-right">
                                <line x1="5" y1="12" x2="19" y2="12"></line>
                                <polyline points="12 5 19 12 12 19"></polyline>
                            </svg>
                        </Link>
                    </li>
                )}
            </ul>
        );
    };

    render() {
        const { loading, error, managers, searchTerm, filters, sortBy } = this.state;

        return (
            <div className="react-course-filter back__course__page_grid pb---40 pt---110">
                <div className="container pb---70">
                    {/* Tìm kiếm */}
                    <div className="row mb-4">
                        <div className="col-12">
                            <div className="search-container">
                                <div className="input-group">
                                    <span className="input-group-text">
                                        <i className="fas fa-search"></i>
                                    </span>
                                    <input 
                                        type="text" 
                                        className="form-control" 
                                        placeholder="Tìm kiếm theo tên, email, số điện thoại..." 
                                        value={searchTerm}
                                        onChange={this.handleSearchChange}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Bộ lọc */} 
                    <div className="row align-items-center back-vertical-middle shorting__course mb-50">
                        <div className="col-md-2">
                            <div className="all__icons"> 
                                <div className="list__icons">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-sliders">
                                        <line x1="4" y1="21" x2="4" y2="14"></line>
                                        <line x1="4" y1="10" x2="4" y2="3"></line>
                                        <line x1="12" y1="21" x2="12" y2="12"></line>
                                        <line x1="12" y1="8" x2="12" y2="3"></line>
                                        <line x1="20" y1="21" x2="20" y2="16"></line>
                                        <line x1="20" y1="12" x2="20" y2="3"></line>
                                        <line x1="1" y1="14" x2="7" y2="14"></line>
                                        <line x1="9" y1="8" x2="15" y2="8"></line>
                                        <line x1="17" y1="16" x2="23" y2="16"></line>
                                    </svg>
                                </div>
                                <div className="result-count">Bộ lọc</div>
                            </div>
                        </div>
                        <div className="col-md-10 text-right">
                            <select 
                                className="from-control position" 
                                name="position"
                                value={filters.position}
                                onChange={this.handleFilterChange}
                            >
                                <option value="ALL">Tất cả vị trí</option>
                                <option value="P1">Đội trưởng</option>
                                <option value="P2">Phó đội</option>
                                <option value="P3">Trưởng ban</option>
                                <option value="P4">Phó ban</option>
                                <option value="P5">Thành viên</option>
                            </select>
                            <select 
                                className="from-control role"
                                name="role"
                                value={filters.role}
                                onChange={this.handleFilterChange}
                            >
                                <option value="ALL">Tất cả vai trò</option>
                                <option value="R1">Quản trị viên</option>
                                <option value="R2">Người dùng</option>
                            </select>
                            <select 
                                className="from-control gender"
                                name="gender"
                                value={filters.gender}
                                onChange={this.handleFilterChange}
                            >
                                <option value="ALL">Tất cả giới tính</option>
                                <option value="M">Nam</option>
                                <option value="F">Nữ</option>
                                <option value="O">Khác</option>
                            </select>
                        </div>
                    </div>

                    {/* Kết quả và sắp xếp */}
                    <div className="row align-items-center back-vertical-middle shorting__course2 mb-50">
                        <div className="col-md-6">
                            <div className="all__icons"> 
                                <div className="result-count">Tìm thấy {managers.length} thành viên</div>
                            </div>
                        </div>
                        <div className="col-md-6 text-right">                                
                            <select 
                                className="from-control"
                                value={sortBy}
                                onChange={this.handleSortChange}
                            >
                                <option value="default">Sắp xếp theo: Mặc định</option>
                                <option value="name-asc">Tên A-Z</option>
                                <option value="name-desc">Tên Z-A</option>
                                <option value="date-desc">Mới nhất</option>
                                <option value="date-asc">Cũ nhất</option>
                            </select>
                        </div>
                    </div>       
                    
                    {/* Hiển thị danh sách */}
                    <div className="row"> 
                        {loading ? this.renderLoading() : 
                         error ? this.renderError() : 
                         this.renderManagers()}
                    </div>

                    {/* Phân trang */}
                    {!loading && !error && this.renderPagination()}
                </div>
            </div>
        );
    }
}

export default ManageListMain;
