import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { languages } from "../../utils/constant";
import "./UserRedux.scss";
import * as actions from "../../../src/store/actions";
import Lightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";
import { create } from "lodash";
import { createUser } from "../../../src/services/userServices";
import {
    getAllUsers,
    deleteUser,
    updateUser,
} from "../../services/userServices";

import "react-toastify/dist/ReactToastify.css";
class UserRedux extends Component {
    constructor(props) {
        super(props);
        this.editFormRef = React.createRef();
        this.state = {
            email: "",
            password: "",
            firstName: "",
            lastName: "",
            address: "",
            phoneNumber: "",
            genderCode: "",
            roleCode: "",
            positionCode: "",
            image: "",
            showPassword: false,
            previewImgURL: "", // Thêm state mới
            isOpen: false, // Để control lightbox preview
        };
    }

    // gọi hàm bên redux
    async componentDidMount() {}

    handleOnChangeInput = (event, id) => {
        // Copy state hiện tại
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState({
            ...copyState,
        });
    };

    handleOnChangeImage = async (event) => {
        let data = event.target.files;
        let file = data[0];
        if (file) {
            let base64 = await this.getBase64(file);
            let objectUrl = URL.createObjectURL(file);
            this.setState({
                previewImgURL: objectUrl,
                image: base64,
            });
        }
    };

    getBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = (error) => reject(error);
        });
    };

    openPreviewImage = () => {
        if (!this.state.previewImgURL) return;
        this.setState({
            isOpen: true,
        });
    };
    render() {
        console.log("check state", this.state);
        return (
            <div className="user-redux-container">
                <div className="container-fluid px-5">
                    <div className="title text-center">
                        <h2>User Management</h2>
                    </div>
                    <form className="row g-3">
                        <div className="col-md-6">
                            <label className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                value={this.state.email}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "email")
                                }
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Password</label>
                            <input
                                type={
                                    this.state.showPassword
                                        ? "text"
                                        : "password"
                                }
                                className="form-control"
                                value={this.state.password}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "password")
                                }
                            />
                            <button
                                
                                type="button"
                                onClick={() =>
                                    this.setState({
                                        showPassword: !this.state.showPassword,
                                    })
                                }
                            >
                                <i
                                    className={
                                        this.state.showPassword
                                            ? "fas fa-eye-slash"
                                            : "fas fa-eye"
                                    }
                                ></i>
                            </button>
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">First Name</label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.firstName}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "firstName")
                                }
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Last Name</label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.lastName}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "lastName")
                                }
                            />
                        </div>

                        <div className="col-12">
                            <label className="form-label">Address</label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.address}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "address")
                                }
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Phone Number</label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.phoneNumber}
                                onChange={(event) =>
                                    this.handleOnChangeInput(
                                        event,
                                        "phoneNumber"
                                    )
                                }
                            />
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Gender</label>
                            <select
                                className="form-select"
                                value={this.state.genderCode}
                                onChange={(event) =>
                                    this.handleOnChangeInput(
                                        event,
                                        "genderCode"
                                    )
                                }
                            >
                                <option value="">Choose gender...</option>
                                <option value="M">Male</option>
                                <option value="F">Female</option>
                                <option value="O">Other</option>
                            </select>
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Role</label>
                            <select
                                className="form-select"
                                value={this.state.roleCode}
                                onChange={(event) =>
                                    this.handleOnChangeInput(event, "roleCode")
                                }
                            >
                                <option value="">Choose role...</option>
                                <option value="R1">Admin</option>
                                <option value="R2">Doctor</option>
                                <option value="R3">Patient</option>
                            </select>
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Position</label>
                            <select
                                className="form-select"
                                value={this.state.positionCode}
                                onChange={(event) =>
                                    this.handleOnChangeInput(
                                        event,
                                        "positionCode"
                                    )
                                }
                            >
                                <option value="">Choose position...</option>
                                <option value="P1">Doctor</option>
                                <option value="P2">Master</option>
                                <option value="P3">Associate</option>
                            </select>
                        </div>

                        <div className="col-md-6">
                            <label className="form-label">Image</label>
                            <div className="preview-img-container">
                                <input
                                    type="file"
                                    className="form-control mb-3"
                                    onChange={(event) =>
                                        this.handleOnChangeImage(event)
                                    }
                                    accept="image/*"
                                />
                                {this.state.previewImgURL && (
                                    <div
                                        className="preview-image"
                                        style={{
                                            backgroundImage: `url(${this.state.previewImgURL})`,
                                        }}
                                        onClick={() => this.openPreviewImage()}
                                    ></div>
                                )}
                            </div>
                        </div>

                        <div className="col-12">
                            <button type="submit" className="btn btn-primary">
                                Save
                            </button>
                        </div>
                    </form>
                    {this.state.isOpen === true && (
                        <Lightbox
                            mainSrc={this.state.previewImgURL}
                            onCloseRequest={() =>
                                this.setState({ isOpen: false })
                            }
                        />
                    )}
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
