import React, { Component } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { connect } from "react-redux";
import { getAllCodeService } from "../../services/userServices";
import { DatePicker } from "../../components/Input";
import moment from "moment";
import "./ModalEditEvent.scss";

class ModalEditEvent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            name: "",
            typeEventCode: "",
            date: "",
            address: "",
            quantityMember: 0,
            cost: 0,
            statusCode: "",
            eventTypeOptions: [],
            statusOptions: [],
        };
    }

    async componentDidMount() {
        // Lấy danh sách loại sự kiện và trạng thái từ API
        await this.fetchOptions();
    }

    componentDidUpdate(prevProps) {
        // Khi modal mở và có dữ liệu sự kiện, điền thông tin vào form
        if (this.props.isOpen && prevProps.isOpen !== this.props.isOpen) {
            if (this.props.currentEvent) {
                const { 
                    id, name, typeEventCode, date, address, 
                    quantityMember, cost, statusCode 
                } = this.props.currentEvent;
                
                this.setState({
                    id,
                    name,
                    typeEventCode,
                    date: date ? moment(date).toDate() : "",
                    address: address || "",
                    quantityMember: quantityMember || 0,
                    cost: cost || 0,
                    statusCode,
                });
            } else {
                // Reset form nếu là tạo mới
                this.setState({
                    id: "",
                    name: "",
                    typeEventCode: "",
                    date: "",
                    address: "",
                    quantityMember: 0,
                    cost: 0,
                    statusCode: "",
                });
            }
        }
    }

    fetchOptions = async () => {
        try {
            const eventTypeResponse = await getAllCodeService("event_type");
            const statusResponse = await getAllCodeService("event_status");

            if (eventTypeResponse && eventTypeResponse.errCode === 0 &&
                statusResponse && statusResponse.errCode === 0) {
                this.setState({
                    eventTypeOptions: eventTypeResponse.data || [],
                    statusOptions: statusResponse.data || [],
                });
            }
        } catch (error) {
            console.error("Error fetching options:", error);
        }
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        });
    };

    handleDateChange = (date) => {
        this.setState({
            date,
        });
    };

    handleNumberInputChange = (event) => {
        const { name, value } = event.target;
        // Đảm bảo chỉ nhập số
        if (!isNaN(value) || value === "") {
            this.setState({
                [name]: value === "" ? 0 : parseFloat(value),
            });
        }
    };

    handleSaveEvent = () => {
        // Validate form
        const { name, typeEventCode, date, address, statusCode } = this.state;
        
        if (!name || !typeEventCode || !date || !address || !statusCode) {
            alert("Vui lòng điền đầy đủ thông tin bắt buộc");
            return;
        }
        
        // Chuẩn bị dữ liệu để lưu
        const eventData = {
            id: this.state.id, // Nếu id là empty string, server sẽ hiểu là tạo mới
            name: this.state.name,
            typeEventCode: this.state.typeEventCode,
            date: moment(this.state.date).format("YYYY-MM-DD"),
            address: this.state.address,
            quantityMember: parseInt(this.state.quantityMember),
            cost: parseFloat(this.state.cost),
            statusCode: this.state.statusCode,
        };
        
        this.props.updateEvent(eventData);
    };

    render() {
        const { isOpen, toggleModal } = this.props;
        const { 
            name, typeEventCode, date, address, quantityMember,
            cost, statusCode, eventTypeOptions, statusOptions 
        } = this.state;
        conso
        
        const modalTitle = this.state.id ? "Cập nhật sự kiện" : "Thêm sự kiện mới";
        
        return (
            <Modal 
                isOpen={isOpen} 
                toggle={toggleModal}
                className="event-edit-modal"
                size="lg"
            >
                <ModalHeader toggle={toggleModal}>
                    {modalTitle}
                </ModalHeader>
                <ModalBody>
                    <div className="container">
                        <div className="row">
                            <div className="col-12 mb-3">
                                <label>Tên sự kiện <span className="required">*</span></label>
                                <input
                                    type="text"
                                    className="form-control"
                                    name="name"
                                    value={name}
                                    onChange={this.handleInputChange}
                                    placeholder="Nhập tên sự kiện"
                                />
                            </div>
                            
                            <div className="col-md-6 mb-3">
                                <label>Loại sự kiện <span className="required">*</span></label>
                                <select
                                    className="form-control"
                                    name="typeEventCode"
                                    value={typeEventCode}
                                    onChange={this.handleInputChange}
                                >
                                    <option value="">-- Chọn loại sự kiện --</option>
                                    {eventTypeOptions.map(option => (
                                        <option key={option.keyMap} value={option.keyMap}>
                                            {option.valueVi}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            
                            <div className="col-md-6 mb-3">
                                <label>Ngày diễn ra <span className="required">*</span></label>
                                <DatePicker
                                    className="form-control"
                                    onChange={this.handleDateChange}
                                    value={date}
                                    minDate={new Date()}
                                />
                            </div>
                            
                            <div className="col-12 mb-3">
                                <label>Địa điểm <span className="required">*</span></label>
                                <input
                                    type="text"
                                    className="form-control"
                                    name="address"
                                    value={address}
                                    onChange={this.handleInputChange}
                                    placeholder="Nhập địa điểm tổ chức"
                                />
                            </div>
                            
                            <div className="col-md-4 mb-3">
                                <label>Số lượng thành viên</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    name="quantityMember"
                                    value={quantityMember}
                                    onChange={this.handleNumberInputChange}
                                    min="0"
                                />
                            </div>
                            
                            <div className="col-md-4 mb-3">
                                <label>Chi phí (VNĐ)</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    name="cost"
                                    value={cost}
                                    onChange={this.handleNumberInputChange}
                                    min="0"
                                />
                            </div>
                            
                            <div className="col-md-4 mb-3">
                                <label>Trạng thái <span className="required">*</span></label>
                                <select
                                    className="form-control"
                                    name="statusCode"
                                    value={statusCode}
                                    onChange={this.handleInputChange}
                                >
                                    <option value="">-- Chọn trạng thái --</option>
                                    {statusOptions.map(option => (
                                        <option key={option.keyMap} value={option.keyMap}>
                                            {option.valueVi}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        
                        <div className="form-notice">
                            <small>Các trường đánh dấu <span className="required">*</span> là bắt buộc</small>
                        </div>
                    </div>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={this.handleSaveEvent}>
                        Lưu
                    </Button>{" "}
                    <Button color="secondary" onClick={toggleModal}>
                        Hủy
                    </Button>
                </ModalFooter>
            </Modal>
        );
    }
}

export default connect()(ModalEditEvent);
