import React, { useState, useEffect } from 'react';
import { useLocation,  } from 'react-router-dom';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import Breadcrumb from '../../components/Breadcrumb/EventBreadcrumbs';
import EventDetailsMain from './EventDetailsMain';
import ScrollToTop from '../../components/ScrollTop';
import events from '../../data/Events.json';
import { getRecentEvents } from '../../services/eventServices';
import { toast } from 'react-toastify';
import Logo from '../../assets/images/logos/logo2.png';
import Slider from "react-slick";


const EventDetails = () => {

    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await getRecentEvents();
            if (response && response.data && response.data.errCode === 0) {
                setEvents(response.data.data);
            }
        } catch (error) {
            console.error("Error fetching events:", error);
            toast.error("Không thể tải danh sách sự kiện");
        } finally {
            setLoading(false);
        }
    };


    const location = useLocation();
    const eventURL = location.pathname.split('/'); 
    
    const event = events.find((b) => b.id === Number(eventURL[2]));

    return (
        <div className="course-single">
            <Header
                parentMenu='event'
                // menuCategoryEnable='enable'
                headerNormalLogo={Logo}
                headerStickyLogo={Logo}
            />

            <div class="react-wrapper">
                <div class="react-wrapper-inner">
                <div className="row">
                    {events.map((data, index) => {
                        return (
                            <div className="col-lg-3">
                                <SingleEvent
                                      key={data.id}
                                      eventID={data.id}
                                      eventImg={data.eventMarkdown?.image || ''}
                                      eventTitle={data.name}
                                      eventDate={data.date}
                                      eventLocation={data.address}
                                      eventType={data.eventType?.valueVi}
                                      eventStatus={data.status?.valueVi}
                                      eventDescription={data.eventMarkdown?.description}
                                      eventCost={data.cost}
                                      eventQuantityMember={data.quantityMember}
                                      eventBtnText="Xem chi tiết"
                                />
                            </div>
                        )
                    }).slice(0, 12)}
                </div>
                    <Breadcrumb
                        eventID= {event.id}
                        eventImg= {event.image}
                        eventBannerImg= {`${event.bannerImg}`}
                        eventDayCount= {event.dayCount}
                        eventDate= {event.date}
                        eventStartTime= {event.startTime}
                        eventEndTime= {event.endTime}
                        eventCategory= {event.category}
                        eventTitle= {event.title}
                        eventBtnText= "Find Out More"
                        eventLocation= {event.location}
                    />

                    <EventDetailsMain
                        eventID= {event.id}
                        eventImg= {event.image}
                        eventBannerImg= {event.bannerImg}
                        eventDayCount= {event.dayCount}
                        eventDate= {event.date}
                        eventStartTime= {event.startTime}
                        eventEndTime= {event.endTime}
                        eventCategory= {event.category}
                        eventTitle= {event.title}
                        eventBtnText= "Find Out More"
                        eventLocation= {event.location}
                        eventCost= {event.cost}
                        eventHost= {event.host}
                        eventTotalSlot= {event.totalSlot}
                        eventBookedSlot= {event.bookedSlot}
                        eventContactNo= {event.phone}
                    />

                    {/* scrolltop-start */}
                    <ScrollToTop />
                    {/* scrolltop-end */}
                </div>
            </div>

            <Footer />

        </div>
    );
}

export default EventDetails;
