import React, { Component } from "react";
import { connect } from "react-redux";
import "./EventRegistrationManage.scss";
import { FormattedMessage } from "react-intl";
import { getAllEventRegistrations, updateRegistrationStatus, markAttendance } from "../../services/eventService";
import { Container, Row, Col, Card, Table, Button, Form, InputGroup, Badge, Modal, Spinner, Alert, Dropdown, OverlayTrigger, Tooltip } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faSearch,
    faFilter,
    faUserCheck,
    faUserTimes,
    faFileExport,
    faInfoCircle,
    faSortAlphaDown,
    faSortAlphaUp,
    faCheck,
    faTimes,
    faEye,
    faEdit,
    faTrashAlt,
    faUserCog,
    faCalendarCheck,
    faSms,
    faArrowLeft,
    faUserEdit,
    faPhone,
    faEnvelope,
} from "@fortawesome/free-solid-svg-icons";
import { CSVLink } from "react-csv";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";
import ReactPaginate from "react-paginate";
import Select from "react-select";
import { toast } from "react-toastify";

class EventRegistrationManage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: this.props.match?.params?.id || "",
            eventName: "",
            registrations: [],
            filteredRegistrations: [],
            isLoading: true,
            errorMessage: "",

            // Pagination
            currentPage: 0,
            registrationsPerPage: 10,

            // Sorting
            sortField: "createdAt",
            sortDirection: "desc",

            // Filtering
            searchTerm: "",
            filterStatus: "all",
            filterAttendance: "all",

            // Modals
            showDetailModal: false,
            showAttendanceModal: false,
            showMessageModal: false,
            selectedRegistration: null,

            // Attendance form
            attendanceDate: new Date(),
            attendanceNote: "",

            // Message form
            messageText: "",
            messageType: "sms",

            // Event details
            eventStartDate: null,
            eventEndDate: null,
            eventLocation: "",
            eventStatus: "",
            totalRegistrations: 0,
            approvedRegistrations: 0,
            pendingRegistrations: 0,
            rejectedRegistrations: 0,
            attendedRegistrations: 0,
        };
    }

    async componentDidMount() {
        await this.fetchRegistrations();
    }

    fetchRegistrations = async () => {
        try {
            this.setState({ isLoading: true, errorMessage: "" });

            // Call API to get registrations for this event
            const response = await getAllEventRegistrations(this.state.eventId);

            if (response && response.data && response.data.errCode === 0) {
                const registrations = response.data.registrations || [];

                // Calculate statistics
                const totalRegistrations = registrations.length;
                const approvedRegistrations = registrations.filter((r) => r.status === "approved").length;
                const pendingRegistrations = registrations.filter((r) => r.status === "pending").length;
                const rejectedRegistrations = registrations.filter((r) => r.status === "rejected").length;
                const attendedRegistrations = registrations.filter((r) => r.hasAttended).length;

                // Get event details
                const eventDetails = response.data.eventDetails || {};

                this.setState({
                    registrations,
                    filteredRegistrations: registrations,
                    eventName: eventDetails.name || "Sự kiện",
                    eventStartDate: eventDetails.startDate ? new Date(eventDetails.startDate) : null,
                    eventEndDate: eventDetails.endDate ? new Date(eventDetails.endDate) : null,
                    eventLocation: eventDetails.location || "",
                    eventStatus: eventDetails.status || "",
                    totalRegistrations,
                    approvedRegistrations,
                    pendingRegistrations,
                    rejectedRegistrations,
                    attendedRegistrations,
                    isLoading: false,
                });
            } else {
                this.setState({
                    errorMessage: response.data.errMessage || "Đã xảy ra lỗi khi tải dữ liệu",
                    isLoading: false,
                });
            }
        } catch (error) {
            console.error("Error fetching registrations:", error);
            this.setState({
                errorMessage: "Không thể kết nối đến máy chủ",
                isLoading: false,
            });
        }
    };

    // Sorting functions
    handleSort = (field) => {
        const { sortField, sortDirection } = this.state;

        const newDirection = field === sortField && sortDirection === "asc" ? "desc" : "asc";

        this.setState(
            {
                sortField: field,
                sortDirection: newDirection,
            },
            this.applyFiltersAndSort
        );
    };

    // Filtering functions
    handleSearchChange = (e) => {
        this.setState(
            {
                searchTerm: e.target.value,
                currentPage: 0,
            },
            this.applyFiltersAndSort
        );
    };

    handleStatusFilterChange = (e) => {
        this.setState(
            {
                filterStatus: e.target.value,
                currentPage: 0,
            },
            this.applyFiltersAndSort
        );
    };

    handleAttendanceFilterChange = (e) => {
        this.setState(
            {
                filterAttendance: e.target.value,
                currentPage: 0,
            },
            this.applyFiltersAndSort
        );
    };

    applyFiltersAndSort = () => {
        const { registrations, searchTerm, filterStatus, filterAttendance, sortField, sortDirection } = this.state;

        let filtered = [...registrations];

        // Apply search term filter
        if (searchTerm) {
            const term = searchTerm.toLowerCase();
            filtered = filtered.filter(
                (reg) =>
                    (reg.user?.firstName + " " + reg.user?.lastName).toLowerCase().includes(term) ||
                    reg.user?.email.toLowerCase().includes(term) ||
                    reg.user?.phoneNumber?.includes(term)
            );
        }

        // Apply status filter
        if (filterStatus !== "all") {
            filtered = filtered.filter((reg) => reg.status === filterStatus);
        }

        // Apply attendance filter
        if (filterAttendance !== "all") {
            const hasAttended = filterAttendance === "attended";
            filtered = filtered.filter((reg) => reg.hasAttended === hasAttended);
        }

        // Apply sorting
        filtered.sort((a, b) => {
            let valueA, valueB;

            // Determine which field to sort by
            switch (sortField) {
                case "name":
                    valueA = (a.user?.firstName + " " + a.user?.lastName).toLowerCase();
                    valueB = (b.user?.firstName + " " + b.user?.lastName).toLowerCase();
                    break;
                case "email":
                    valueA = a.user?.email.toLowerCase();
                    valueB = b.user?.email.toLowerCase();
                    break;
                case "phoneNumber":
                    valueA = a.user?.phoneNumber || "";
                    valueB = b.user?.phoneNumber || "";
                    break;
                case "createdAt":
                default:
                    valueA = new Date(a.createdAt);
                    valueB = new Date(b.createdAt);
                    break;
            }

            // Perform the sort
            if (sortDirection === "asc") {
                return valueA > valueB ? 1 : -1;
            } else {
                return valueA < valueB ? 1 : -1;
            }
        });

        this.setState({
            filteredRegistrations: filtered,
        });
    };

    // Pagination handlers
    handlePageClick = (event) => {
        this.setState({
            currentPage: event.selected,
        });
    };

    // Modal functions
    openDetailModal = (registration) => {
        this.setState({
            selectedRegistration: registration,
            showDetailModal: true,
        });
    };

    openAttendanceModal = (registration) => {
        this.setState({
            selectedRegistration: registration,
            showAttendanceModal: true,
            attendanceDate: registration.attendedAt ? new Date(registration.attendedAt) : new Date(),
            attendanceNote: registration.attendanceNote || "",
        });
    };

    openMessageModal = (registration) => {
        this.setState({
            selectedRegistration: registration,
            showMessageModal: true,
            messageText: "",
            messageType: "sms",
        });
    };

    closeModals = () => {
        this.setState({
            showDetailModal: false,
            showAttendanceModal: false,
            showMessageModal: false,
            selectedRegistration: null,
        });
    };

    // Status update handlers
    handleStatusChange = async (registrationId, newStatus) => {
        try {
            const response = await updateRegistrationStatus(registrationId, newStatus);

            if (response && response.data && response.data.errCode === 0) {
                // Update the local state
                const updatedRegistrations = this.state.registrations.map((reg) => (reg.id === registrationId ? { ...reg, status: newStatus } : reg));

                let { approvedRegistrations, pendingRegistrations, rejectedRegistrations } = this.state;

                // Update statistics
                if (newStatus === "approved") {
                    approvedRegistrations++;
                    pendingRegistrations--;
                } else if (newStatus === "rejected") {
                    rejectedRegistrations++;
                    pendingRegistrations--;
                }

                this.setState(
                    {
                        registrations: updatedRegistrations,
                        approvedRegistrations,
                        pendingRegistrations,
                        rejectedRegistrations,
                    },
                    this.applyFiltersAndSort
                );

                toast.success(`Đã ${newStatus === "approved" ? "chấp nhận" : "từ chối"} đăng ký thành công`);
            } else {
                toast.error(response.data.errMessage || "Cập nhật trạng thái không thành công");
            }
        } catch (error) {
            console.error("Error updating registration status:", error);
            toast.error("Không thể kết nối đến máy chủ");
        }
    };

    // Attendance handlers
    handleAttendanceSubmit = async () => {
        const { selectedRegistration, attendanceDate, attendanceNote } = this.state;

        try {
            const response = await markAttendance(selectedRegistration.id, true, attendanceDate, attendanceNote);

            if (response && response.data && response.data.errCode === 0) {
                // Update the local state
                const updatedRegistrations = this.state.registrations.map((reg) =>
                    reg.id === selectedRegistration.id
                        ? {
                              ...reg,
                              hasAttended: true,
                              attendedAt: attendanceDate,
                              attendanceNote: attendanceNote,
                          }
                        : reg
                );

                this.setState(
                    {
                        registrations: updatedRegistrations,
                        attendedRegistrations: this.state.attendedRegistrations + 1,
                    },
                    () => {
                        this.applyFiltersAndSort();
                        this.closeModals();
                    }
                );

                toast.success("Đã điểm danh thành công");
            } else {
                toast.error(response.data.errMessage || "Điểm danh không thành công");
            }
        } catch (error) {
            console.error("Error marking attendance:", error);
            toast.error("Không thể kết nối đến máy chủ");
        }
    };

    handleRemoveAttendance = async (registrationId) => {
        try {
            const response = await markAttendance(registrationId, false);

            if (response && response.data && response.data.errCode === 0) {
                // Update the local state
                const updatedRegistrations = this.state.registrations.map((reg) =>
                    reg.id === registrationId
                        ? {
                              ...reg,
                              hasAttended: false,
                              attendedAt: null,
                              attendanceNote: "",
                          }
                        : reg
                );

                this.setState(
                    {
                        registrations: updatedRegistrations,
                        attendedRegistrations: this.state.attendedRegistrations - 1,
                    },
                    this.applyFiltersAndSort
                );

                toast.success("Đã hủy điểm danh thành công");
            } else {
                toast.error(response.data.errMessage || "Hủy điểm danh không thành công");
            }
        } catch (error) {
            console.error("Error removing attendance:", error);
            toast.error("Không thể kết nối đến máy chủ");
        }
    };

    // Message handlers
    handleSendMessage = async () => {
        const { selectedRegistration, messageText, messageType } = this.state;

        // Implement message sending functionality here
        toast.success(`Đã gửi ${messageType === "sms" ? "SMS" : "Email"} thành công`);
        this.closeModals();
    };

    // Navigation handlers
    handleBackToEvents = () => {
        this.props.history.push("/system/event-manage");
    };

    // CSV Export preparation
    prepareExportData = () => {
        const { filteredRegistrations } = this.state;

        return filteredRegistrations.map((reg) => ({
            "Họ tên": `${reg.user?.firstName || ""} ${reg.user?.lastName || ""}`,
            Email: reg.user?.email || "",
            "Số điện thoại": reg.user?.phoneNumber || "",
            "Trạng thái": this.translateStatus(reg.status),
            "Điểm danh": reg.hasAttended ? "Đã điểm danh" : "Chưa điểm danh",
            "Ngày đăng ký": moment(reg.createdAt).format("DD/MM/YYYY HH:mm"),
            "Ngày điểm danh": reg.attendedAt ? moment(reg.attendedAt).format("DD/MM/YYYY HH:mm") : "",
            "Ghi chú": reg.registrationNote || "",
        }));
    };

    // Helper functions
    translateStatus = (status) => {
        switch (status) {
            case "approved":
                return "Đã duyệt";
            case "pending":
                return "Chờ duyệt";
            case "rejected":
                return "Từ chối";
            default:
                return status;
        }
    };

    getStatusBadge = (status) => {
        switch (status) {
            case "approved":
                return <Badge bg="success">Đã duyệt</Badge>;
            case "pending":
                return (
                    <Badge bg="warning" text="dark">
                        Chờ duyệt
                    </Badge>
                );
            case "rejected":
                return <Badge bg="danger">Từ chối</Badge>;
            default:
                return <Badge bg="secondary">{status}</Badge>;
        }
    };

    render() {
        const {
            eventName,
            filteredRegistrations,
            isLoading,
            errorMessage,
            currentPage,
            registrationsPerPage,
            sortField,
            sortDirection,
            searchTerm,
            filterStatus,
            filterAttendance,
            showDetailModal,
            showAttendanceModal,
            showMessageModal,
            selectedRegistration,
            attendanceDate,
            attendanceNote,
            messageText,
            messageType,
            totalRegistrations,
            approvedRegistrations,
            pendingRegistrations,
            rejectedRegistrations,
            attendedRegistrations,
            eventStartDate,
            eventEndDate,
            eventLocation,
            eventStatus,
        } = this.state;

        // Pagination data
        const offset = currentPage * registrationsPerPage;
        const currentRegistrations = filteredRegistrations.slice(offset, offset + registrationsPerPage);
        const pageCount = Math.ceil(filteredRegistrations.length / registrationsPerPage);

        // CSV export filename
        const csvFilename = `danh-sach-dang-ky-${eventName.replace(/[^a-z0-9]/gi, "-").toLowerCase()}-${moment().format("DDMMYYYY")}.csv`;

        return (
            <Container fluid className="event-registration-manage px-4 py-4">
                <Row className="mb-4">
                    <Col>
                        <Button variant="outline-secondary" className="mb-3" onClick={this.handleBackToEvents}>
                            <FontAwesomeIcon icon={faArrowLeft} className="me-2" />
                            Quay lại danh sách sự kiện
                        </Button>

                        <h2 className="page-title">
                            <span className="text-primary">Quản lý đăng ký: </span>
                            {eventName}
                        </h2>

                        {eventStatus && (
                            <Badge bg={eventStatus === "active" ? "success" : eventStatus === "upcoming" ? "primary" : "secondary"} className="fs-6 mb-3">
                                {eventStatus === "active" ? "Đang diễn ra" : eventStatus === "upcoming" ? "Sắp diễn ra" : "Đã kết thúc"}
                            </Badge>
                        )}

                        {eventStartDate && (
                            <p className="mb-1">
                                <strong>Thời gian: </strong>
                                {moment(eventStartDate).format("DD/MM/YYYY HH:mm")}
                                {eventEndDate && ` - ${moment(eventEndDate).format("DD/MM/YYYY HH:mm")}`}
                            </p>
                        )}

                        {eventLocation && (
                            <p className="mb-3">
                                <strong>Địa điểm: </strong>
                                {eventLocation}
                            </p>
                        )}
                    </Col>
                </Row>

                {/* Statistics Cards */}
                <Row className="mb-4">
                    <Col md={4} className="mb-3 mb-md-0">
                        <Card className="h-100 shadow-sm">
                            <Card.Body className="d-flex align-items-center">
                                <div className="rounded-circle bg-light p-3 me-3">
                                    <FontAwesomeIcon icon={faUserCheck} className="text-primary fa-2x" />
                                </div>
                                <div>
                                    <h3 className="mb-0">{totalRegistrations}</h3>
                                    <p className="text-muted mb-0">Tổng số đăng ký</p>
                                </div>
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col md={8}>
                        <Card className="h-100 shadow-sm">
                            <Card.Body>
                                <Row>
                                    <Col xs={6} md={3} className="border-end border-light">
                                        <div className="text-center">
                                            <h5 className="mb-1 text-success">{approvedRegistrations}</h5>
                                            <p className="text-muted small mb-0">Đã duyệt</p>
                                        </div>
                                    </Col>
                                    <Col xs={6} md={3} className="border-end border-light">
                                        <div className="text-center">
                                            <h5 className="mb-1 text-warning">{pendingRegistrations}</h5>
                                            <p className="text-muted small mb-0">Chờ duyệt</p>
                                        </div>
                                    </Col>
                                    <Col xs={6} md={3} className="border-end border-light mt-3 mt-md-0">
                                        <div className="text-center">
                                            <h5 className="mb-1 text-danger">{rejectedRegistrations}</h5>
                                            <p className="text-muted small mb-0">Từ chối</p>
                                        </div>
                                    </Col>
                                    <Col xs={6} md={3} className="mt-3 mt-md-0">
                                        <div className="text-center">
                                            <h5 className="mb-1 text-primary">{attendedRegistrations}</h5>
                                            <p className="text-muted small mb-0">Điểm danh</p>
                                        </div>
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>

                {/* Filters and Actions */}
                <Card className="shadow-sm mb-4">
                    <Card.Body>
                        <Row className="align-items-center">
                            <Col lg={4} className="mb-3 mb-lg-0">
                                <InputGroup>
                                    <InputGroup.Text>
                                        <FontAwesomeIcon icon={faSearch} />
                                    </InputGroup.Text>
                                    <Form.Control type="text" placeholder="Tìm kiếm theo tên, email hoặc SĐT" value={searchTerm} onChange={this.handleSearchChange} />
                                </InputGroup>
                            </Col>

                            <Col lg={6} className="mb-3 mb-lg-0">
                                <Row>
                                    <Col md={6} className="mb-2 mb-md-0">
                                        <InputGroup>
                                            <InputGroup.Text>
                                                <FontAwesomeIcon icon={faFilter} />
                                            </InputGroup.Text>
                                            <Form.Select value={filterStatus} onChange={this.handleStatusFilterChange}>
                                                <option value="all">Tất cả trạng thái</option>
                                                <option value="approved">Đã duyệt</option>
                                                <option value="pending">Chờ duyệt</option>
                                                <option value="rejected">Từ chối</option>
                                            </Form.Select>
                                        </InputGroup>
                                    </Col>

                                    <Col md={6}>
                                        <InputGroup>
                                            <InputGroup.Text>
                                                <FontAwesomeIcon icon={faUserCheck} />
                                            </InputGroup.Text>
                                            <Form.Select value={filterAttendance} onChange={this.handleAttendanceFilterChange}>
                                                <option value="all">Tất cả điểm danh</option>
                                                <option value="attended">Đã điểm danh</option>
                                                <option value="not-attended">Chưa điểm danh</option>
                                            </Form.Select>
                                        </InputGroup>
                                    </Col>
                                </Row>
                            </Col>

                            <Col lg={2} className="text-end">
                                <CSVLink data={this.prepareExportData()} filename={csvFilename} className="btn btn-outline-success" target="_blank">
                                    <FontAwesomeIcon icon={faFileExport} className="me-2" />
                                    Xuất Excel
                                </CSVLink>
                            </Col>
                        </Row>
                    </Card.Body>
                </Card>

                {/* Registrations Table */}
                <Card className="shadow-sm">
                    <Card.Body>
                        {isLoading ? (
                            <div className="text-center py-5">
                                <Spinner animation="border" variant="primary" />
                                <p className="mt-3 mb-0">Đang tải dữ liệu...</p>
                            </div>
                        ) : errorMessage ? (
                            <Alert variant="danger">
                                <FontAwesomeIcon icon={faExclamationTriangle} className="me-2" />
                                {errorMessage}
                            </Alert>
                        ) : filteredRegistrations.length === 0 ? (
                            <Alert variant="info">
                                <FontAwesomeIcon icon={faInfoCircle} className="me-2" />
                                Không có dữ liệu đăng ký phù hợp.
                            </Alert>
                        ) : (
                            <>
                                <div className="table-responsive">
                                    <Table hover className="align-middle mb-0">
                                        <thead className="bg-light">
                                            <tr>
                                                <th style={{ width: "40px" }}>#</th>
                                                <th style={{ cursor: "pointer" }} onClick={() => this.handleSort("name")}>
                                                    Họ tên
                                                    {sortField === "name" && <FontAwesomeIcon icon={sortDirection === "asc" ? faSortAlphaDown : faSortAlphaUp} className="ms-2" />}
                                                </th>
                                                <th style={{ cursor: "pointer" }} onClick={() => this.handleSort("email")}>
                                                    Email
                                                    {sortField === "email" && <FontAwesomeIcon icon={sortDirection === "asc" ? faSortAlphaDown : faSortAlphaUp} className="ms-2" />}
                                                </th>
                                                <th>Số điện thoại</th>
                                                <th>Trạng thái</th>
                                                <th>Điểm danh</th>
                                                <th style={{ cursor: "pointer" }} onClick={() => this.handleSort("createdAt")}>
                                                    Ngày đăng ký
                                                    {sortField === "createdAt" && (
                                                        <FontAwesomeIcon icon={sortDirection === "asc" ? faSortAlphaDown : faSortAlphaUp} className="ms-2" />
                                                    )}
                                                </th>
                                                <th style={{ width: "160px" }}>Thao tác</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {currentRegistrations.map((registration, index) => (
                                                <tr key={registration.id}>
                                                    <td>{offset + index + 1}</td>
                                                    <td>
                                                        {registration.user?.firstName} {registration.user?.lastName}
                                                    </td>
                                                    <td>{registration.user?.email}</td>
                                                    <td>{registration.user?.phoneNumber || "-"}</td>
                                                    <td>{this.getStatusBadge(registration.status)}</td>
                                                    <td>
                                                        {registration.hasAttended ? (
                                                            <OverlayTrigger
                                                                placement="top"
                                                                overlay={
                                                                    <Tooltip>
                                                                        {registration.attendedAt ? moment(registration.attendedAt).format("DD/MM/YYYY HH:mm") : ""}
                                                                        {registration.attendanceNote ? (
                                                                            <>
                                                                                <br />
                                                                                {registration.attendanceNote}
                                                                            </>
                                                                        ) : (
                                                                            ""
                                                                        )}
                                                                    </Tooltip>
                                                                }
                                                            >
                                                                <Badge bg="success">
                                                                    <FontAwesomeIcon icon={faCheck} className="me-1" />
                                                                    Đã điểm danh
                                                                </Badge>
                                                            </OverlayTrigger>
                                                        ) : (
                                                            <Badge bg="secondary">
                                                                <FontAwesomeIcon icon={faTimes} className="me-1" />
                                                                Chưa điểm danh
                                                            </Badge>
                                                        )}
                                                    </td>
                                                    <td>
                                                        {moment(registration.createdAt).format("DD/MM/YYYY")}
                                                        <br />
                                                        <small className="text-muted">{moment(registration.createdAt).format("HH:mm")}</small>
                                                    </td>
                                                    <td>
                                                        <Dropdown>
                                                            <Dropdown.Toggle variant="light" size="sm" className="border">
                                                                <FontAwesomeIcon icon={faUserCog} className="me-1" />
                                                                Tác vụ
                                                            </Dropdown.Toggle>
                                                            <Dropdown.Menu>
                                                                <Dropdown.Item onClick={() => this.openDetailModal(registration)}>
                                                                    <FontAwesomeIcon icon={faEye} className="me-2" />
                                                                    Xem chi tiết
                                                                </Dropdown.Item>

                                                                {registration.status === "pending" && (
                                                                    <>
                                                                        <Dropdown.Item
                                                                            onClick={() => this.handleStatusChange(registration.id, "approved")}
                                                                            className="text-success"
                                                                        >
                                                                            <FontAwesomeIcon icon={faCheck} className="me-2" />
                                                                            Duyệt đăng ký
                                                                        </Dropdown.Item>
                                                                        <Dropdown.Item onClick={() => this.handleStatusChange(registration.id, "rejected")} className="text-danger">
                                                                            <FontAwesomeIcon icon={faTimes} className="me-2" />
                                                                            Từ chối
                                                                        </Dropdown.Item>
                                                                    </>
                                                                )}

                                                                {registration.status === "approved" && !registration.hasAttended && (
                                                                    <Dropdown.Item onClick={() => this.openAttendanceModal(registration)}>
                                                                        <FontAwesomeIcon icon={faCalendarCheck} className="me-2" />
                                                                        Điểm danh
                                                                    </Dropdown.Item>
                                                                )}

                                                                {registration.hasAttended && (
                                                                    <Dropdown.Item onClick={() => this.handleRemoveAttendance(registration.id)} className="text-danger">
                                                                        <FontAwesomeIcon icon={faTimes} className="me-2" />
                                                                        Hủy điểm danh
                                                                    </Dropdown.Item>
                                                                )}

                                                                <Dropdown.Divider />

                                                                <Dropdown.Item onClick={() => this.openMessageModal(registration)}>
                                                                    <FontAwesomeIcon icon={faSms} className="me-2" />
                                                                    Gửi tin nhắn
                                                                </Dropdown.Item>
                                                            </Dropdown.Menu>
                                                        </Dropdown>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </Table>
                                </div>

                                {/* Pagination */}
                                {pageCount > 1 && (
                                    <div className="d-flex justify-content-end mt-4">
                                        <ReactPaginate
                                            previousLabel={"Trước"}
                                            nextLabel={"Tiếp"}
                                            breakLabel={"..."}
                                            pageCount={pageCount}
                                            marginPagesDisplayed={2}
                                            pageRangeDisplayed={3}
                                            onPageChange={this.handlePageClick}
                                            containerClassName={"pagination"}
                                            pageClassName={"page-item"}
                                            pageLinkClassName={"page-link"}
                                            previousClassName={"page-item"}
                                            previousLinkClassName={"page-link"}
                                            nextClassName={"page-item"}
                                            nextLinkClassName={"page-link"}
                                            breakClassName={"page-item"}
                                            breakLinkClassName={"page-link"}
                                            activeClassName={"active"}
                                            forcePage={currentPage}
                                        />
                                    </div>
                                )}
                            </>
                        )}
                    </Card.Body>
                </Card>

                {/* Detail Modal */}
                <Modal show={showDetailModal} onHide={this.closeModals} size="lg" centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Thông tin chi tiết đăng ký</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {selectedRegistration && (
                            <Row>
                                <Col md={6}>
                                    <Card className="mb-3">
                                        <Card.Header className="bg-light">
                                            <FontAwesomeIcon icon={faUserEdit} className="me-2" />
                                            Thông tin người đăng ký
                                        </Card.Header>
                                        <Card.Body>
                                            <p>
                                                <strong>Họ tên:</strong> {selectedRegistration.user?.firstName} {selectedRegistration.user?.lastName}
                                            </p>
                                            <p>
                                                <strong>Email:</strong> {selectedRegistration.user?.email}
                                            </p>
                                            <p>
                                                <strong>Số điện thoại:</strong> {selectedRegistration.user?.phoneNumber || "Không có"}
                                            </p>
                                            <p>
                                                <strong>Ngày sinh:</strong>{" "}
                                                {selectedRegistration.user?.birthDate ? moment(selectedRegistration.user.birthDate).format("DD/MM/YYYY") : "Không có"}
                                            </p>
                                            <p>
                                                <strong>Giới tính:</strong>{" "}
                                                {selectedRegistration.user?.gender === "male" ? "Nam" : selectedRegistration.user?.gender === "female" ? "Nữ" : "Khác"}
                                            </p>
                                            <p>
                                                <strong>Địa chỉ:</strong> {selectedRegistration.user?.address || "Không có"}
                                            </p>
                                        </Card.Body>
                                    </Card>
                                </Col>

                                <Col md={6}>
                                    <Card className="mb-3">
                                        <Card.Header className="bg-light">
                                            <FontAwesomeIcon icon={faInfoCircle} className="me-2" />
                                            Thông tin đăng ký
                                        </Card.Header>
                                        <Card.Body>
                                            <p>
                                                <strong>Trạng thái:</strong> {this.getStatusBadge(selectedRegistration.status)}
                                            </p>
                                            <p>
                                                <strong>Điểm danh:</strong>{" "}
                                                {selectedRegistration.hasAttended ? (
                                                    <Badge bg="success">
                                                        <FontAwesomeIcon icon={faCheck} className="me-1" />
                                                        Đã điểm danh
                                                    </Badge>
                                                ) : (
                                                    <Badge bg="secondary">
                                                        <FontAwesomeIcon icon={faTimes} className="me-1" />
                                                        Chưa điểm danh
                                                    </Badge>
                                                )}
                                            </p>
                                            <p>
                                                <strong>Ngày đăng ký:</strong> {moment(selectedRegistration.createdAt).format("DD/MM/YYYY HH:mm")}
                                            </p>
                                            {selectedRegistration.hasAttended && (
                                                <p>
                                                    <strong>Ngày điểm danh:</strong> {moment(selectedRegistration.attendedAt).format("DD/MM/YYYY HH:mm")}
                                                </p>
                                            )}
                                            {selectedRegistration.registrationNote && (
                                                <p>
                                                    <strong>Ghi chú đăng ký:</strong> {selectedRegistration.registrationNote}
                                                </p>
                                            )}
                                            {selectedRegistration.hasAttended && selectedRegistration.attendanceNote && (
                                                <p>
                                                    <strong>Ghi chú điểm danh:</strong> {selectedRegistration.attendanceNote}
                                                </p>
                                            )}
                                        </Card.Body>
                                    </Card>

                                    {selectedRegistration.status === "pending" && (
                                        <div className="d-flex gap-2">
                                            <Button
                                                variant="success"
                                                className="flex-grow-1"
                                                onClick={() => {
                                                    this.handleStatusChange(selectedRegistration.id, "approved");
                                                    this.closeModals();
                                                }}
                                            >
                                                <FontAwesomeIcon icon={faCheck} className="me-2" />
                                                Duyệt đăng ký
                                            </Button>
                                            <Button
                                                variant="danger"
                                                className="flex-grow-1"
                                                onClick={() => {
                                                    this.handleStatusChange(selectedRegistration.id, "rejected");
                                                    this.closeModals();
                                                }}
                                            >
                                                <FontAwesomeIcon icon={faTimes} className="me-2" />
                                                Từ chối đăng ký
                                            </Button>
                                        </div>
                                    )}
                                </Col>
                            </Row>
                        )}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.closeModals}>
                            Đóng
                        </Button>
                    </Modal.Footer>
                </Modal>

                {/* Attendance Modal */}
                <Modal show={showAttendanceModal} onHide={this.closeModals} centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Điểm danh tham gia sự kiện</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {selectedRegistration && (
                            <Form>
                                <p>
                                    Đánh dấu điểm danh cho:
                                    <strong className="ms-1">
                                        {selectedRegistration.user?.firstName} {selectedRegistration.user?.lastName}
                                    </strong>
                                </p>

                                <Form.Group className="mb-3">
                                    <Form.Label>Ngày giờ điểm danh</Form.Label>
                                    <DatePicker
                                        selected={attendanceDate}
                                        onChange={(date) => this.setState({ attendanceDate: date })}
                                        showTimeSelect
                                        dateFormat="dd/MM/yyyy HH:mm"
                                        className="form-control"
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Label>Ghi chú (không bắt buộc)</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={3}
                                        value={attendanceNote}
                                        onChange={(e) => this.setState({ attendanceNote: e.target.value })}
                                        placeholder="Nhập ghi chú về việc điểm danh (nếu có)"
                                    />
                                </Form.Group>
                            </Form>
                        )}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.closeModals}>
                            Hủy bỏ
                        </Button>
                        <Button variant="success" onClick={this.handleAttendanceSubmit}>
                            <FontAwesomeIcon icon={faUserCheck} className="me-2" />
                            Xác nhận điểm danh
                        </Button>
                    </Modal.Footer>
                </Modal>

                {/* Message Modal */}
                <Modal show={showMessageModal} onHide={this.closeModals} centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Gửi tin nhắn</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {selectedRegistration && (
                            <Form>
                                <p>
                                    Gửi tin nhắn tới:
                                    <strong className="ms-1">
                                        {selectedRegistration.user?.firstName} {selectedRegistration.user?.lastName}
                                    </strong>
                                </p>

                                <Form.Group className="mb-3">
                                    <Form.Label>Phương thức</Form.Label>
                                    <div>
                                        <Form.Check
                                            inline
                                            type="radio"
                                            label={
                                                <>
                                                    <FontAwesomeIcon icon={faSms} className="me-1" /> SMS
                                                </>
                                            }
                                            name="messageType"
                                            id="smsRadio"
                                            value="sms"
                                            checked={messageType === "sms"}
                                            onChange={() => this.setState({ messageType: "sms" })}
                                        />
                                        <Form.Check
                                            inline
                                            type="radio"
                                            label={
                                                <>
                                                    <FontAwesomeIcon icon={faEnvelope} className="me-1" /> Email
                                                </>
                                            }
                                            name="messageType"
                                            id="emailRadio"
                                            value="email"
                                            checked={messageType === "email"}
                                            onChange={() => this.setState({ messageType: "email" })}
                                        />
                                    </div>
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Label>
                                        {messageType === "sms" ? (
                                            <>
                                                Số điện thoại <small className="text-muted">(Không thể chỉnh sửa)</small>
                                            </>
                                        ) : (
                                            <>
                                                Email <small className="text-muted">(Không thể chỉnh sửa)</small>
                                            </>
                                        )}
                                    </Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={messageType === "sms" ? selectedRegistration.user?.phoneNumber || "Không có số điện thoại" : selectedRegistration.user?.email}
                                        disabled
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Label>{messageType === "sms" ? "Nội dung tin nhắn" : "Nội dung email"}</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={5}
                                        value={messageText}
                                        onChange={(e) => this.setState({ messageText: e.target.value })}
                                        placeholder={messageType === "sms" ? "Nhập nội dung tin nhắn SMS..." : "Nhập nội dung email..."}
                                    />
                                </Form.Group>
                            </Form>
                        )}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.closeModals}>
                            Hủy bỏ
                        </Button>
                        <Button variant="primary" onClick={this.handleSendMessage} disabled={!messageText || (messageType === "sms" && !selectedRegistration?.user?.phoneNumber)}>
                            <FontAwesomeIcon icon={messageType === "sms" ? faSms : faEnvelope} className="me-2" />
                            Gửi {messageType === "sms" ? "SMS" : "Email"}
                        </Button>
                    </Modal.Footer>
                </Modal>
            </Container>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(EventRegistrationManage);
