import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import * as actions from "../../store/actions";
import "./Login.scss";
import { FormattedMessage } from "react-intl";
import { languages } from "../../utils";
import { Form, InputGroup, Button, Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faFacebookF,
    faTwitter,
    faGoogle,
    faGithub,
} from "@fortawesome/free-brands-svg-icons";
import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons"; // Import icons

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: "",
            password: "",
            showPassword: false,
        };
    }

    handleOnChangeUsername = (event) => {
        this.setState({
            userName: event.target.value,
        });
    };

    handleOnChangePassword = (event) => {
        this.setState({
            password: event.target.value,
        });
    };

    toggleShowPassword = () => {
        this.setState({ showPassword: !this.state.showPassword });
    };

    handleLogin = () => {
        alert("ban da login");
    };

    render() {
        return (
            <Container>
                <Row className="justify-content-md-center">
                    <Col md={4}>
                        <h2 className="text-center mt-5">Login</h2>
                        <Form>
                            <Form.Group className="mb-4" controlId="form1">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control
                                    type="email"
                                    placeholder="Enter email"
                                    value={this.state.userName}
                                    onChange={(event) => {
                                        this.handleOnChangeUsername(event);
                                    }}
                                />
                            </Form.Group>

                            <Form.Group className="mb-4" controlId="form2">
                                <Form.Label>Password</Form.Label>
                                <InputGroup>
                                    <Form.Control
                                        type={this.state.showPassword ? "text" : "password"}
                                        placeholder="Password"
                                        value={this.state.password}
                                        onChange={(event) => {
                                            this.handleOnChangePassword(event);
                                        }}
                                    />
                                    {/* <Button
                                        variant="outline-secondary"
                                        onClick={this.toggleShowPassword}
                                    >
                                        <FontAwesomeIcon
                                            icon={this.state.showPassword ? faEyeSlash : faEye}
                                        />
                                    </Button> */}
                                </InputGroup>
                            </Form.Group>

                            <div className="d-flex justify-content-between mx-3 mb-4">
                                <Form.Check
                                    type="checkbox"
                                    id="flexCheckDefault"
                                    label="Remember me"
                                />
                                <a href="#!">Forgot password?</a>
                            </div>

                            <Button
                                variant="primary"
                                className="mb-4 w-100"
                                onClick={() => {
                                    this.handleLogin();
                                }}
                            >
                                Sign in
                            </Button>

                            <div className="text-center">
                                <p>
                                    Not a member? <a href="#!">Register</a>
                                </p>
                                <p>or sign up with:</p>

                                <div
                                    className="d-flex justify-content-between mx-auto"
                                    style={{ width: "40%" }}
                                >
                                    <Button
                                        variant="outline-primary"
                                        className="m-1"
                                        style={{ color: "#1266f1" }}
                                    >
                                        <FontAwesomeIcon
                                            icon={faFacebookF}
                                            size="sm"
                                        />
                                    </Button>

                                    <Button
                                        variant="outline-primary"
                                        className="m-1"
                                        style={{ color: "#1266f1" }}
                                    >
                                        <FontAwesomeIcon
                                            icon={faTwitter}
                                            size="sm"
                                        />
                                    </Button>

                                    <Button
                                        variant="outline-primary"
                                        className="m-1"
                                        style={{ color: "#1266f1" }}
                                    >
                                        <FontAwesomeIcon
                                            icon={faGoogle}
                                            size="sm"
                                        />
                                    </Button>

                                    <Button
                                        variant="outline-primary"
                                        className="m-1"
                                        style={{ color: "#1266f1" }}
                                    >
                                        <FontAwesomeIcon
                                            icon={faGithub}
                                            size="sm"
                                        />
                                    </Button>
                                </div>
                            </div>
                        </Form>
                    </Col>
                </Row>
            </Container>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) =>
            dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
