import React from "react";
import { Modal, Button } from "react-bootstrap";

const ModalUser = ({
    show,
    handleClose,
    userEditData,
    handleChangeUserEditData,
    handleSaveUserEdit,
}) => {
    return (
        <Modal show={show} onHide={handleClose}>
            {/* tiêu đề */}
            <Modal.Header>
                <Modal.Title>Chỉnh sửa thông tin người dùng</Modal.Title>
            </Modal.Header>
            {/* nội dung */}
            <Modal.Body>
                <form>
                    <div className="form-group">
                        <label>Họ:</label>
                        <input
                            type="text"
                            className="form-control"
                            name="firstName"
                            value={userData.firstName}
                            onChange={handleUserChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Tên:</label>
                        <input
                            type="text"
                            className="form-control"
                            name="lastName"
                            value={userData.lastName}
                            onChange={handleUserChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            className="form-control"
                            name="email"
                            value={userData.email}
                            onChange={handleUserChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Địa chỉ:</label>
                        <input
                            type="text"
                            className="form-control"
                            name="address"
                            value={userData.address}
                            onChange={handleUserChange}
                        />
                    </div>
                </form>
            </Modal.Body>
            {/* Phần footer của modal */}
            <Modal.Footer>
                <Button variant="secondary" size="sm" onClick={handleClose}>
                    Đóng
                </Button>
                <Button variant="primary" size="sm" onClick={handleUserSave}>
                    Lưu thay đổi
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default ModalUser;
