import React, { Component } from "react";
import { connect } from "react-redux";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { registerEvent } from "../../services/eventServices";
import "./EventRegistrationList.scss"; // Tạo file SCSS riêng cho component

class EventRegistrationList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventId: props.eventId || "",
            name: "",
            email: "",
            phone: "",
            payMethodCode: "",
            statusCostCode: "",
            notes: "",
            registeredAt: new Date().toISOString().split("T")[0],
            isSubmitting: false,
        };
    }
    // Trong componentDidMount:
    componentDidMount() {
        const { userInforr, isLoggedIn } = this.props;

        // Auto-fill user information if logged in
        if (isLoggedIn && userInforr) {
            this.setState({
                name: `${userInforr.firstName} ${userInforr.lastName}`.trim(),
                email: userInforr.email || "",
                phone: userInforr.phoneNumber || "",
            });
        }
    }

    // Cập nhật eventId khi props thay đổi
    componentDidUpdate(prevProps) {
        if (this.props.eventId !== prevProps.eventId) {
            this.setState({ eventId: this.props.eventId || "" });
        }
    }

    handleOnChangeInput = (event, id) => {
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState({
            ...copyState,
        });
    };


    render() {
        return (
            <div className="event-registration-container">
                <div className="container">
                    <div className="registration-form-wrapper">
                        <div className="form-header">
                            <h2>Đăng Ký Tham Gia Sự Kiện</h2>
                            <p>
                                Vui lòng điền đầy đủ thông tin bên dưới để hoàn
                                tất đăng ký.
                            </p>
                        </div>

                        <form className="registration-form">
                            <div className="form-row">
                                <div className="form-group">
                                    <label>
                                        Họ và tên{" "}
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={this.state.name}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "name"
                                            )
                                        }
                                        placeholder="Nhập họ và tên của bạn"
                                    />
                                </div>

                                <div className="form-group">
                                    <label>
                                        Email{" "}
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="email"
                                        value={this.state.email}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "email"
                                            )
                                        }
                                        placeholder="Nhập địa chỉ email"
                                    />
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group">
                                    <label>
                                        Số điện thoại{" "}
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="tel"
                                        value={this.state.phone}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "phone"
                                            )
                                        }
                                        placeholder="Nhập số điện thoại của bạn"
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Ngày đăng ký</label>
                                    <input
                                        type="date"
                                        value={this.state.registeredAt}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "registeredAt"
                                            )
                                        }
                                    />
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group">
                                    <label>
                                        Phương thức thanh toán{" "}
                                        <span className="required">*</span>
                                    </label>
                                    <select
                                        value={this.state.payMethodCode}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "payMethodCode"
                                            )
                                        }
                                    >
                                        <option value="">
                                            Chọn phương thức thanh toán
                                        </option>
                                        <option value="PM2">
                                            Chuyển khoản ngân hàng
                                        </option>
                                        <option value="PM1">Tiền mặt</option>
                                        <option value="PM3">Ví MoMo</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>
                                        Trạng thái thanh toán{" "}
                                        <span className="required">*</span>
                                    </label>
                                    <select
                                        value={this.state.statusCostCode}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "statusCostCode"
                                            )
                                        }
                                    >
                                        <option value="">
                                            Chọn trạng thái thanh toán
                                        </option>
                                        <option value="PS1">
                                            Đã thanh toán
                                        </option>
                                        <option value="PS2">
                                            Chưa thanh toán
                                        </option>
                                    </select>
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group full-width">
                                    <label>Ghi chú bổ sung</label>
                                    <textarea
                                        rows="4"
                                        value={this.state.notes}
                                        onChange={(event) =>
                                            this.handleOnChangeInput(
                                                event,
                                                "notes"
                                            )
                                        }
                                        placeholder="Nhập thông tin bổ sung nếu cần thiết"
                                    ></textarea>
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="register-button"
                                    onClick={() => this.handleRegisterEvent()}
                                >
                                    Đăng Ký Ngay
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        language: state.app.language,
        userInforr: state.user.userInforr,
        isLoggedIn: state.user.isLoggedIn,
    };
};

export default connect(mapStateToProps)(EventRegistrationList);
