import React, { useState, useEffect } from "react";
import { Link, useLocation, useParams } from 'react-router-dom';
import { getUserById } from '../../services/userServices';
import { toast } from 'react-toastify';
import CountUp from 'react-countup';
import VisibilitySensor from 'react-visibility-sensor';

const UserProfileMain = () => {
    // Lấy id từ URL params
    const { id } = useParams();
    
    // State để lưu trữ thông tin người dùng
    const [userInfo, setUserInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [viewedCounter, setViewedCounter] = useState(false);
    
    // Lấy dữ liệu từ API khi component mount
    useEffect(() => {
        const fetchUserData = async () => {
            try {
                setLoading(true);
                
                // Gọi API để lấy thông tin chi tiết
                const response = await getUserById(id);
                
                if (response && response.data && response.data.errCode === 0) {
                    setUserInfo(response.data.data);
                } else {
                    setError('Không thể tải thông tin người dùng');
                    toast.error('Không tìm thấy thông tin người dùng');
                }
            } catch (error) {
                console.error('Lỗi khi lấy thông tin người dùng:', error);
                setError('Lỗi kết nối đến server');
                toast.error('Có lỗi xảy ra khi tải thông tin');
            } finally {
                setLoading(false);
            }
        };
        
        if (id) {
            fetchUserData();
        } else {
            setError('ID người dùng không hợp lệ');
            setLoading(false);
        }
    }, [id]);
    
    // Hiển thị trạng thái loading
    if (loading) {
        return (
            <div className="profile-top back__course__area pt---120 pb---90">
                <div className="container">
                    <div className="text-center py-5">
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Đang tải...</span>
                        </div>
                        <p className="mt-2">Đang tải thông tin chi tiết...</p>
                    </div>
                </div>
            </div>
        );
    }
    
    // Hiển thị thông báo lỗi
    if (error || !userInfo) {
        return (
            <div className="profile-top back__course__area pt---120 pb---90">
                <div className="container">
                    <div className="alert alert-danger" role="alert">
                        <i className="fas fa-exclamation-triangle me-2"></i>
                        {error || 'Không tìm thấy thông tin người dùng'}
                    </div>
                </div>
            </div>
        );
    }
    
    // Xử lý dữ liệu để hiển thị
    const {
        firstName,
        lastName,
        image,
        positionData,
        roleData,
        email,
        phoneNumber,
        userMarkdown,
        genderData
    } = userInfo;

    // Tạo full name
    const fullName = `${firstName || ''} ${lastName || ''}`.trim();
    
    // Lấy thông tin chi tiết và mô tả
    const description = userMarkdown?.description || 'Chưa có thông tin mô tả';
    const userDetails = userMarkdown?.contentHTML || 'Chưa có thông tin chi tiết';
    
    // Chức vụ hiển thị
    const position = positionData?.valueVi || roleData?.valueVi || 'Thành viên';
    
    // Thống kê người dùng
    const userStats = [
        {
            countNum: 85,
            countTitle: 'Điểm tích lũy',
            countSubtext: '%'
        },
        {
            countNum: 24,
            countTitle: 'Hoạt động đã tham gia',
            countSubtext: ''
        },
        {
            countNum: 3,
            countTitle: 'Năm kinh nghiệm',
            countSubtext: '+'
        }
    ];

    return (
        <>
            <div className="profile-top back__course__area pt---120 pb---90">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4">
                            <div className="user-image-container text-center mb-4">
                                {image ? (
                                    <img 
                                        src={image} 
                                        alt={fullName} 
                                        className="user-profile-image rounded-circle"
                                        onError={(e) => {
                                            e.target.onerror = null;
                                            e.target.src = require('../../assets/images/user/default-avatar.png');
                                        }}
                                    />
                                ) : (
                                    <img 
                                        src={require('../../assets/images/user/default-avatar.png')} 
                                        alt="Default avatar" 
                                        className="user-profile-image rounded-circle"
                                    />
                                )}
                                <div className="mt-3">
                                    <Link to="#" className="follows">
                                        Theo dõi 
                                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-plus">
                                            <line x1="12" y1="5" x2="12" y2="19"></line>
                                            <line x1="5" y1="12" x2="19" y2="12"></line>
                                        </svg>
                                    </Link>
                                </div>
                            </div>
                            
                            {/* Thống kê người dùng */}
                            <div className="user-stats mt-4">
                                <div className="count__area2"> 
                                    <ul className="row">
                                        {userStats.map((stat, index) => (
                                            <li key={index} className="col-12 mb-4">
                                                <div className="count__content">
                                                    <div className="text">
                                                        <CountUp 
                                                            start={0} 
                                                            end={stat.countNum} 
                                                            duration={2.5}
                                                            suffix={stat.countSubtext}
                                                            redraw={false}
                                                        >
                                                            {({ countUpRef }) => (
                                                                <VisibilitySensor
                                                                    onChange={(isVisible) => {
                                                                        if (isVisible && !viewedCounter) {
                                                                            setViewedCounter(true);
                                                                            return true;
                                                                        }
                                                                        return false;
                                                                    }}
                                                                    active={!viewedCounter}
                                                                    delayedCall
                                                                >
                                                                    <span className="count__content-title counter" ref={countUpRef}>
                                                                        {/* CountUp sẽ tự động điền giá trị vào đây */}
                                                                    </span>
                                                                </VisibilitySensor>
                                                            )}
                                                        </CountUp>
                                                        <p className="mt-2">{stat.countTitle}</p>
                                                    </div>                                           
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        </div>
                        
                        <div className="col-lg-8">
                            <ul className="user-section">
                                <li className="user">
                                    <span className="name">Họ và tên:</span><em>{fullName}</em>
                                </li>
                                <li>
                                    <span className="name">Chức vụ:</span><em>{position}</em>
                                </li>
                                <li>
                                    <span className="name">Giới tính:</span><em>{genderData?.valueVi || 'Chưa cập nhật'}</em>
                                </li>
                                <li>
                                    <span className="name">Điện thoại:</span><em>{phoneNumber || 'Chưa cập nhật'}</em>
                                </li>
                                <li>
                                    <span className="name">Email:</span><em>{email || 'Chưa cập nhật'}</em>
                                </li>
                                <li className="social">
                                    <span className="name">Liên hệ:</span>
                                    <em>
                                        <Link to="#"><span aria-hidden="true" className="social_facebook"></span></Link>
                                        <Link to="#"><span aria-hidden="true" className="social_twitter"></span></Link>
                                        <Link to="#"><span aria-hidden="true" className="social_linkedin"></span></Link>
                                    </em>
                                </li>
                            </ul>
                            
                            <h3 className="mt-5 mb-3">Giới thiệu</h3>
                            <p>{description}</p>
                            
                            <h3 className="mt-5 mb-3">Thông tin chi tiết</h3>
                            <div dangerouslySetInnerHTML={{ __html: userDetails }}></div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default UserProfileMain;
