import React, { Component } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { Modal, Button } from "react-bootstrap";
import "./UserManage.scss";
import { getAllUsers, deleteUser, updateUser } from "../../services/userServices";

class UserManage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrUsers: [],
            userIdToEdit: null,
            userEditData: {
                firstName: '',
                lastName: '',
                email: '',
                address: ''
            },
            showModal: false
        };
    }

    async componentDidMount() {
        await this.fetchAllUsers();
    }

    fetchAllUsers = async () => {
        let response = await getAllUsers("ALL");
        if (response && response.data && response.data.errCode === 0) {
            this.setState({
                arrUsers: response.data.users,
            });
        }
    }

    handleEditUser = (user) => {
        this.setState({
            userIdToEdit: user.id,
            userEditData: {
                firstName: user.firstName,
                lastName: user.lastName,
                email: user.email,
                address: user.address
            },
            showModal: true // Hiển thị modal
        });
    };

    handleChangeUserEditData = (event) => {
        let inputName = event.target.name;
        let inputValue = event.target.value;
        let updatedUserEditData = {};

        for (let key in this.state.userEditData) {
            if (this.state.userEditData.hasOwnProperty(key)) {
                updatedUserEditData[key] = this.state.userEditData[key];
            }
        }

        updatedUserEditData[inputName] = inputValue;

        this.setState({
            userEditData: updatedUserEditData
        });
    };

    handleSaveUserEdit = async () => {
        let { userIdToEdit, userEditData } = this.state;
        let data = {
            id: userIdToEdit,
            firstName: userEditData.firstName,
            lastName: userEditData.lastName,
            email: userEditData.email,
            address: userEditData.address
        };

        let response = await updateUser(data);
        if (response && response.data.errCode === 0) {
            await this.fetchAllUsers();
            this.setState({
                userIdToEdit: null,
                userEditData: {
                    firstName: '',
                    lastName: '',
                    email: '',
                    address: ''
                },
                showModal: false // Ẩn modal sau khi lưu
            });
        } else {
            alert(response.data.errMessage);
        }
    };

    handleCloseModal = () => {
        this.setState({
            showModal: false
        });
    };

    handleDeleteUser = async (userId) => {
        console.log(`Deleting user with ID: ${userId}`);
        let response = await deleteUser(userId);
        if (response && response.data.errCode === 0) {
            await this.fetchAllUsers();
        } else {
            console.error(response.data.errMessage);
            alert(response.data.errMessage);
        }
    };

    render() {
        console.log("check render", this.state.arrUsers);
        let { arrUsers, userEditData, showModal } = this.state;
        return (
            <div className="user-center">
                <div className="title text-center">Manage user with anhtonton</div>
                
                <div className="user-table mt-4 mx-3">
                    <table className="table table-striped table-hover table-bordered" id="customers">
                        <thead className="thead-dark custom-header">
                            <tr>
                                <th style={{ width: "25%" }}>Email</th>
                                <th style={{ width: "20%" }}>Firstname</th>
                                <th style={{ width: "20%" }}>Lastname</th>
                                <th style={{ width: "25%" }}>Address</th>
                                <th style={{ width: "10%" }}>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {arrUsers && arrUsers.length > 0 &&
                                arrUsers.map((user, index) => (
                                    <tr key={index}>
                                        <td>{user.email}</td>
                                        <td>{user.firstName}</td>
                                        <td>{user.lastName}</td>
                                        <td>{user.address}</td>
                                        <td>
                                            <button className="btn" onClick={() => this.handleEditUser(user)}>
                                                <FaEdit />
                                            </button>
                                            <button className="btn" onClick={() => this.handleDeleteUser(user.id)}>
                                                <FaTrash />
                                            </button>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>

                <Modal show={showModal} onHide={this.handleCloseModal}>
                    <Modal.Header closeButton>
                        <Modal.Title>Edit User Information</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <form>
                            <div className="form-group">
                                <label>First Name:</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    name="firstName"
                                    value={userEditData.firstName}
                                    onChange={this.handleChangeUserEditData}
                                />
                            </div>
                            <div className="form-group">
                                <label>Last Name:</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    name="lastName"
                                    value={userEditData.lastName}
                                    onChange={this.handleChangeUserEditData}
                                />
                            </div>
                            <div className="form-group">
                                <label>Email:</label>
                                <input
                                    type="email"
                                    className="form-control"
                                    name="email"
                                    value={userEditData.email}
                                    onChange={this.handleChangeUserEditData}
                                />
                            </div>
                            <div className="form-group">
                                <label>Address:</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    name="address"
                                    value={userEditData.address}
                                    onChange={this.handleChangeUserEditData}
                                />
                            </div>
                        </form>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleCloseModal}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleSaveUserEdit}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {};
};

const mapDispatchToProps = (dispatch) => {
    return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(UserManage);
